// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import {
  runGameOfLife,
  getTagColor,
  getTextColor,
  formatDate,
} from "../functionality.js"; // Import getTagColor, getTextColor, formatDate
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library
// =====================
function Library() {
  console.log("Library Component RENDER");

  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [readingListData, setReadingListData] = useState([]);
  const [activeTagFilters, setActiveTagFilters] = useState([]); // State for active tag filters
  const [readFilterState, setReadFilterState] = useState(0); // 0: All, 1: Read, 2: Unread

  console.log("Library State Initialized:", {
    theme,
    readingListData,
    activeTagFilters,
    readFilterState,
  });

  // --- Helper Functions ---
  const toggleTheme = () => {
    console.log("toggleTheme called");
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    console.log("scrollToTop called");
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // --- Effects ---
  useEffect(() => {
    console.log("Library useEffect - Theme update - theme:", theme);
    document.body.setAttribute("data-theme", theme);
    console.log("Library useEffect - Theme update COMPLETE");
  }, [theme]);

  useEffect(() => {
    console.log("Library useEffect - GameOfLife runGameOfLife('gameOfLife')");
    runGameOfLife("gameOfLife");
    console.log("Library useEffect - GameOfLife COMPLETE");
  }, []);

  useEffect(() => {
    console.log("Library useEffect - Loading reading list data...");
    try {
      const initialReadingListData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));

      setReadingListData(initialReadingListData);
      console.log(
        "Library useEffect - Reading list data loaded successfully. Length:",
        initialReadingListData.length
      );
      console.log(
        "Library useEffect - documentSimilaritiesData loaded successfully:",
        similaritiesRawData
      );
    } catch (error) {
      console.error(
        "Library useEffect - Error importing reading list data:",
        error
      );
    }
    console.log("Library useEffect - Loading reading list data - COMPLETE");
  }, []);

  useEffect(() => {
    console.log(
      "Library useEffect - initializeReadingListHTML (readingListData) - readingListData.length:",
      readingListData.length
    );
    if (readingListData.length > 0) {
      renderReadingListHTML(readingListData); // Call renderReadingListHTML here
    } else {
      console.warn(
        "Library useEffect - readingListData is empty or not yet loaded, skipping initializeReadingListHTML."
      );
    }
    console.log(
      "Library useEffect - initializeReadingListHTML (readingListData) - COMPLETE"
    );
  }, [readingListData]); // Re-render list when readingListData changes

  const renderReadingListHTML = (data) => {
    // Replaced window.initializeReadingListHTML, and generateReadingListHTML
    console.log("renderReadingListHTML is called with readingListData:", data);
    const readingList = document.getElementById("reading-list");
    if (!readingList) {
      console.error("readingList element not found!");
      return;
    }
    readingList.innerHTML = ""; // Clear existing content

    if (!data || !Array.isArray(data)) {
      console.warn(
        "readingListData is not properly initialized or is not an array."
      );
      return; // Exit if data is not valid
    }

    data.forEach((item) => {
      const li = document.createElement("li");
      li.setAttribute("data-release", item.releaseDate);
      li.setAttribute("data-time", item.readTime);
      li.setAttribute("data-read", item.isRead.toString()); // Ensure boolean is string
      li.style.display = ""; // Ensure items are initially visible, important for filters

      const html = `
      ${item.isRead ? '<span class="read-indicator">âœ“</span>' : ""}
      <div class="item-header">
        <div class="item-title">
          <strong>"${item.title}" by ${item.author}</strong>
          <span class="meta">
            ${item.isRead ? "Read: " : ""}
            ${formatDate(item.releaseDate)} |
            <span class="no-break">Est. Time ${item.readTime} hours</span>
          </span>
        </div>
        <div class="tags">
          ${item.tags
            .map(
              (tag) =>
                `<span data-type="${tag}" onClick={() => handleTagFilter(tag)} style="background-color: ${getTagColor(
                  tag
                )}; color: ${getTextColor(tag)};">${tag}</span>` // Inline styles for tags, onClick handler
            )
            .join("")}
        </div>
      </div>
      <div class="description">
        ${item.description}
      </div>
      ${
        item.downloadLink
          ? `<a class="download-link" href="${item.downloadLink}" download>Source</a>`
          : ""
      }
    `;

      li.innerHTML = html;
      readingList.appendChild(li);
    });
  };

  const useModel = window.use;
  window.documentVisibilityChanged = new Event("documentVisibilityChanged");

  const debounceFunc = (func, wait) => {
    // Renamed to avoid confusion with imported debounce
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  const performSearch = (query, data) => {
    console.log(
      "performSearch called - query:",
      query,
      "data.length:",
      data.length
    );
    if (!query) {
      console.log("performSearch - No query, returning original data");
      return data;
    }
    const filtered = data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
    console.log("performSearch - Filtered results length:", filtered.length);
    return filtered;
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    console.log("handleSearchInput - query:", query);
    const filteredData = performSearch(
      query,
      sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }))
    );
    setReadingListData(filteredData);
    console.log(
      "handleSearchInput - setReadingListData called - new readingListData.length:",
      filteredData.length
    );
  };

  // --- Sorting and Filtering Functions (Now in React Component) ---
  const sortReadingList = (sortBy, sortOrder) => {
    console.log(
      "sortReadingList called - sortBy:",
      sortBy,
      "sortOrder:",
      sortOrder,
      "readingListData.length:",
      readingListData.length
    );
    let sortedData = [...readingListData]; // Sorting a copy of readingListData
    sortedData.sort((a, b) => {
      let comparison = 0;
      if (sortBy === "release") {
        const dateA = new Date(a.releaseDate);
        const dateB = new Date(b.releaseDate);
        comparison = dateA - dateB;
      } else if (sortBy === "time") {
        comparison = a.readTime - b.readTime;
      }
      return sortOrder === "asc" ? comparison : comparison * -1;
    });
    setReadingListData(sortedData); // Updating readingListData state
    console.log(
      "sortReadingList - setReadingListData called - sortedData.length:",
      sortedData.length
    );
  };

  const filterByType = (filterType) => {
    console.log(
      "filterByType called - filterType:",
      filterType,
      "readingListData.length:",
      readingListData.length
    );
    let filteredData;
    if (filterType === "all") {
      filteredData = sourcesRawData.map((item, index) => ({
        // Resetting to original data for "all" filter
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
    } else {
      filteredData = sourcesRawData
        .map((item, index) => ({
          // Filtering from original data source
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
        .filter((item) => item.tags.includes(filterType));
    }
    setReadingListData(filteredData); // Updating readingListData state
    console.log(
      "filterByType - setReadingListData called - filteredData.length:",
      filteredData.length
    );
  };

  const filterReadStatus = (onlyUnread) => {
    console.log(
      "filterReadStatus called - onlyUnread:",
      onlyUnread,
      "readingListData.length:",
      readingListData.length
    );
    let filteredData;
    if (!onlyUnread) {
      filteredData = sourcesRawData.map((item, index) => ({
        // Resetting to original data for "all" read status
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
    } else {
      filteredData = sourcesRawData
        .map((item, index) => ({
          // Filtering from original data source
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
        .filter((item) => !item.isRead);
    }
    setReadingListData(filteredData); // Updating readingListData state
    console.log(
      "filterReadStatus - setReadingListData called - filteredData.length:",
      filteredData.length
    );
  };

  const handleTagFilter = (tag) => {
    console.log("handleTagFilter called - tag:", tag);
    let newActiveTagFilters = [...activeTagFilters];

    if (newActiveTagFilters.includes(tag)) {
      newActiveTagFilters = newActiveTagFilters.filter(
        (activeTag) => activeTag !== tag
      ); // Remove tag if already active
    } else {
      newActiveTagFilters = [...newActiveTagFilters, tag]; // Add tag if not active
    }
    setActiveTagFilters(newActiveTagFilters);
    console.log("handleTagFilter - setActiveTagFilters:", newActiveTagFilters);
  };

  useEffect(() => {
    console.log("useEffect - activeTagFilters changed:", activeTagFilters);
    applyTagFilters();
  }, [activeTagFilters]); // Re-apply tag filters when activeTagFilters changes

  const applyTagFilters = () => {
    console.log("applyTagFilters called - activeTagFilters:", activeTagFilters);
    let filteredData = sourcesRawData.map((item, index) => ({
      // Start with fresh data
      ...item,
      embedding: item.embedding || [],
      originalIndex: index,
    }));

    if (activeTagFilters.length > 0) {
      filteredData = filteredData.filter((item) => {
        return activeTagFilters.every((filterTag) =>
          item.tags.includes(filterTag)
        );
      });
    }
    setReadingListData(filteredData);
    console.log(
      "applyTagFilters - setReadingListData - filteredData.length:",
      filteredData.length
    );
  };

  const toggleReadFilter = (button) => {
    let nextReadFilterState;
    if (readFilterState === 0) {
      nextReadFilterState = 1;
    } else if (readFilterState === 1) {
      nextReadFilterState = 2;
    } else {
      nextReadFilterState = 0;
    }
    setReadFilterState(nextReadFilterState);
    console.log("toggleReadFilter - setReadFilterState:", nextReadFilterState);
  };

  useEffect(() => {
    console.log("useEffect - readFilterState changed:", readFilterState);
    if (readFilterState === 1) {
      filterReadStatus(true); // Filter: Read (actually Unread in original logic, now corrected to Read)
    } else if (readFilterState === 2) {
      filterReadStatus(false); // Filter: Unread (actually All in original logic for 'Unread' button, now corrected to Unread)
    } else {
      filterByType("all"); // Reset to all, also clears type filters if any were active
      applyTagFilters(); // Re-apply tag filters on top of "all" data.
    }
  }, [readFilterState]); // Re-apply read status filter when readFilterState changes

  const toggleSort = (button, criteria) => {
    const currentOrder = button.dataset.order || "unsorted";
    let newOrder;

    if (currentOrder === "unsorted") {
      newOrder = "asc";
    } else if (currentOrder === "asc") {
      newOrder = "desc";
    } else {
      newOrder = "unsorted";
    }

    // Clear active states & arrows
    const buttons = document.querySelectorAll(".sort-bar button");
    buttons.forEach((btn) => {
      btn.classList.remove("active");
      const arrowSpan = btn.querySelector(".sort-arrow");
      if (arrowSpan) arrowSpan.textContent = "";
    });

    if (newOrder !== "unsorted") {
      button.classList.add("active");
      button.dataset.order = newOrder;

      const arrowSpan = button.querySelector(".sort-arrow");
      if (arrowSpan) {
        arrowSpan.textContent = newOrder === "asc" ? "â†‘" : "â†“";
      }

      sortReadingList(criteria, newOrder); // Call sortReadingList in React component
    } else {
      button.dataset.order = "unsorted";
      // reset the list, but do NOT re-draw the graph - re-render the initial list instead of resetList
      setReadingListData(
        sourcesRawData.map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
      );
    }
  };

  const toggleFilterType = (button, type) => {
    const isActive = button.classList.contains("active");
    const buttons = document.querySelectorAll(".sort-bar button");
    buttons.forEach((btn) => {
      if (btn !== document.getElementById("filter-read")) {
        btn.classList.remove("active");
        btn.style.removeProperty("--active-color");
        btn.style.removeProperty("--active-text-color");
      }
    });

    if (!isActive) {
      button.classList.add("active");
      switch (type) {
        case "YT":
          button.style.setProperty("--active-color", "#FF0000");
          button.style.setProperty("--active-text-color", "#FFFFFF");
          break;
        case "Arxiv":
          button.style.setProperty("--active-color", "#A51C30");
          button.style.setProperty("--active-text-color", "#FFFFFF");
          break;
        case "Site":
          button.style.setProperty("--active-color", "#F5F5DC");
          button.style.setProperty("--active-text-color", "#000000");
          break;
      }
      filterByType(type); // Call filterByType in React component
    } else {
      button.style.removeProperty("--active-color");
      button.style.removeProperty("--active-text-color");
      filterByType("all"); // Reset to all types
    }
  };

  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = React.memo(
