// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife } from "../functionality.js";
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library
// =====================
function Library() {
  console.log("Library Component RENDER");

  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [readingListData, setReadingListData] = useState(sourcesRawData.map((item, index) => ({ // Initialize directly from raw data
    ...item,
    embedding: item.embedding || [],
    originalIndex: index,
    isVisible: true, // Add isVisible property to control display
    sortOrderRelease: 'desc', // Add sort order states
    sortOrderTime: 'desc',
    filterType: 'all', // Add filter type state
    onlyUnread: false, // Add unread filter state
    currentSortBy: null, // Track current sort
  })));

  console.log("Library State Initialized:", {
    theme,
    readingListData,
  });

  // --- Helper Functions ---
  const toggleTheme = () => {
    console.log("toggleTheme called");
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    console.log("scrollToTop called");
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // --- Effects ---
  useEffect(() => {
    console.log("Library useEffect - Theme update - theme:", theme);
    document.body.setAttribute("data-theme", theme);
    console.log("Library useEffect - Theme update COMPLETE");
  }, [theme]);

  useEffect(() => {
    console.log("Library useEffect - GameOfLife runGameOfLife('gameOfLife')");
    runGameOfLife("gameOfLife");
    console.log("Library useEffect - GameOfLife COMPLETE");
  }, []);

  useEffect(() => {
    console.log("Library useEffect - Loading reading list data - skipped as data is now directly initialized");
    console.log(
      "Library useEffect - documentSimilaritiesData loaded successfully:",
      similaritiesRawData
    );
  }, []);

  useEffect(() => {
    console.log(
      "Library useEffect - initializeReadingListHTML (readingListData) - React rendering list directly now, useEffect skipped"
    );
  }, [readingListData]); // Removed dependency because list is now directly rendered in JSX

  const useModel = window.use;
  window.documentVisibilityChanged = new Event("documentVisibilityChanged");

  const debounce = (func, wait) => {
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  const performSearch = (query, data) => {
    console.log(
      "performSearch called - query:",
      query,
      "data.length:",
      data.length
    );
    if (!query) {
      console.log("performSearch - No query, returning original data");
      return data;
    }
    const filtered = data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
    console.log("performSearch - Filtered results length:", filtered.length);
    return filtered;
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    console.log("handleSearchInput - query:", query);
    const filteredData = performSearch(
      query,
      sourcesRawData.map((item, index) => ({ // Search against the original data
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
        isVisible: true, // Reset visibility on search
      }))
    );
    setReadingListData(prevData => { // Update isVisible based on search result
      return prevData.map(item => ({
        ...item,
        isVisible: filteredData.some(filteredItem => filteredItem.originalIndex === item.originalIndex)
      }));
    });
    console.log(
      "handleSearchInput - setReadingListData called - new readingListData.length:",
      filteredData.length
    );
  };

  // --- Sorting and Filtering Functions ---
  const sortReadingList = (sortBy, sortOrder) => { // Removed currentData argument
    console.log(
      "sortReadingList called - sortBy:",
      sortBy,
      "sortOrder:",
      sortOrder
    );

    setReadingListData(prevData => {
      let sortedData = [...prevData];
      sortedData.sort((a, b) => {
        let comparison = 0;
        if (sortBy === "release") {
          const dateA = new Date(a.releaseDate);
          const dateB = new Date(b.releaseDate);
          comparison = dateA - dateB;
        } else if (sortBy === "time") {
          comparison = a.readTime - b.readTime;
        }
        return sortOrder === "asc" ? comparison : comparison * -1;
      });
      return sortedData;
    });
  };

  const filterReadingListByType = (filterType) => { // Removed currentData argument
    console.log(
      "filterReadingListByType called - filterType:",
      filterType
    );
    setReadingListData(prevData => {
      return prevData.map(item => ({
        ...item,
        isVisible: filterType === "all" || item.tags.includes(filterType)
      }));
    });
  };

  const filterReadStatus = (onlyUnread) => { // Removed currentData argument
    console.log(
      "filterReadStatus called - onlyUnread:",
      onlyUnread
    );
    setReadingListData(prevData => {
      return prevData.map(item => ({
        ...item,
        isVisible: !onlyUnread || !item.isRead
      }));
    });
  };

  // --- Button Handlers ---
  const handleSortButtonClick = (event, sortBy) => {
    let sortOrder = 'desc';
    if (sortBy === 'release') {
      sortOrder = readingListData[0].sortOrderRelease === 'desc' ? 'asc' : 'desc';
      setReadingListData(prevData => prevData.map(item => ({...item, sortOrderRelease: sortOrder}))); // Update sortOrder state
      sortReadingList(sortBy, sortOrder);
    } else if (sortBy === 'time') {
      sortOrder = readingListData[0].sortOrderTime === 'desc' ? 'asc' : 'desc';
      setReadingListData(prevData => prevData.map(item => ({...item, sortOrderTime: sortOrder}))); // Update sortOrder state
      sortReadingList(sortBy, sortOrder);
    }

    // Visual arrow update (simplified for single file and functionality)
    const buttons = event.target.parentNode.querySelectorAll('button[id^="sort-"]');
    buttons.forEach(btn => btn.querySelector('.sort-arrow').textContent = ''); // Reset all arrows
    const arrowSpan = event.target.querySelector('.sort-arrow');
    arrowSpan.textContent = sortOrder === 'asc' ? '▲' : '▼';
  };

  const handleFilterTypeButtonClick = (event, filterType) => {
    filterReadingListByType(filterType);
    setReadingListData(prevData => prevData.map(item => ({...item, filterType: filterType}))); // Update filterType state

    // Visual button update (simplified)
    const buttons = event.target.parentNode.querySelectorAll('button[id^="filter-"]');
    buttons.forEach(btn => btn.classList.remove('active-filter')); // Reset active states
    event.target.classList.add('active-filter');
    event.target.parentNode.querySelector('#filter-read').textContent = `Filter: ${filterType === 'all' ? 'All' : filterType}`; // Update "Filter: All" button text
  };

  const handleReadFilterButtonClick = (event) => {
    const onlyUnread = !readingListData[0].onlyUnread; // Toggle based on current state
    filterReadStatus(onlyUnread);
    setReadingListData(prevData => prevData.map(item => ({...item, onlyUnread: onlyUnread}))); // Update onlyUnread state

    // Visual button update (simplified)
    const button = event.target;
    if (onlyUnread) {
      button.classList.add('active-filter');
      button.textContent = 'Filter: Unread';
    } else {
      button.classList.remove('active-filter');
      button.textContent = 'Filter: All Read Status';
    }
  };


  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = React.memo(
    ({ width, height, readingListData, documentSimilaritiesData }) => {
      // Using readingListData as prop
      console.log(
        "DocumentGraph Component RENDER - readingListData.length:",
        readingListData?.length,
        "documentSimilaritiesData:",
        documentSimilaritiesData // Still logging to see if prop is passed correctly
      );

      const containerRef = useRef(null);
      const [error, setError] = useState(null);
      const [isSmallScreen, setIsSmallScreen] = useState(false);
      const svgRef = useRef(null);
      const simulationRef = useRef(null);
      const nodeTitleGroupRef = useRef(null);

      console.log("DocumentGraph State Initialized:", { isSmallScreen });

      const checkScreenSize = () => {
        const smallScreen = window.innerWidth <= 768;
        setIsSmallScreen(smallScreen);
        console.log("checkScreenSize - isSmallScreen:", smallScreen);
      };

      const debouncedCheckScreenSize = debounce(checkScreenSize, 100);

      useEffect(() => {
        console.log("DocumentGraph useEffect - Screen Size Check - Initial");
        checkScreenSize();
        window.addEventListener("resize", debouncedCheckScreenSize);
        return () => {
          window.removeEventListener("resize", debouncedCheckScreenSize);
          console.log("DocumentGraph useEffect - Screen Size Check - Cleanup");
        };
      }, []);

      useEffect(() => {
        console.log(
          "DocumentGraph useEffect - Initialize Graph or Cleanup - isSmallScreen:",
          isSmallScreen,
          "readingListData.length:",
          readingListData?.length,
          "documentSimilaritiesData:",
          documentSimilaritiesData // Still logging to see if prop is passed correctly
        );

        if (!documentSimilaritiesData) {
          // Check prop now
          console.warn(
            "DocumentGraph useEffect - documentSimilaritiesData is still null/undefined. Graph initialization skipped."
          );
          return;
        }

        if (!isSmallScreen && readingListData && readingListData.length > 0) {
          console.log(
            "DocumentGraph useEffect - Initializing graph - readingListData.length:",
            readingListData.length
          );

          const currentVisibleDocs = getVisibleDocuments();
          console.log(
            "DocumentGraph useEffect - getVisibleDocuments returned - currentVisibleDocs.length:",
            currentVisibleDocs.length
          );
          initializeGraph(currentVisibleDocs);
        } else if (
          !isSmallScreen &&
          (!readingListData || readingListData.length === 0)
        ) {
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - readingListData is empty or invalid"
          );
          cleanupGraph();
          if (svgRef.current) {
            const svg = d3.select(svgRef.current);
            svg.selectAll("*").remove();
            svg
              .append("rect")
              .attr("width", "100%")
              .attr("height", "100%")
              .attr("fill", "var(--graph-background)");
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);

            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
          }
        } else {
          console.log(
            "DocumentGraph useEffect - Skipping graph update (small screen or no data)"
          );
        }
        console.log(
          "DocumentGraph useEffect - Initialize Graph or Cleanup - COMPLETE"
        );
      }, [
        isSmallScreen,
        width,
        height,
        documentSimilaritiesData, // Prop dependency
        readingListData, // Dependency on readingListData is crucial for updates
      ]);

      const getVisibleDocuments = useCallback(() => {
        console.log(
          "getVisibleDocuments called - readingListData.length:",
          readingListData?.length
        );
        if (!readingListData || readingListData.length === 0) {
          console.log("getVisibleDocuments - readingListData is empty or null");
          return [];
        }

        // Directly filter from the React state now, no need to query DOM visibility
        const visibleDocs = readingListData.filter(item => item.isVisible);
        console.log(
          "getVisibleDocuments - Visible docs from state:",
          visibleDocs.length
        );
        return visibleDocs;

      }, [readingListData]);

      const cleanupGraph = useCallback(() => {
        console.log("cleanupGraph called");
        if (simulationRef.current) {
          simulationRef.current.stop();
          simulationRef.current = null;
          console.log("cleanupGraph - Simulation stopped");
        }
        if (containerRef.current) {
          const container = d3.select(containerRef.current);
          container.selectAll("*").remove();
          console.log("cleanupGraph - D3 container cleared");
        }
        console.log("cleanupGraph - COMPLETE");
      }, []);

      const initializeGraph = async (visibleDocsForGraph) => {
        console.log(
          "initializeGraph function is being called - visibleDocsForGraph.length:",
          visibleDocsForGraph.length
        );
        console.log(
          "initializeGraph - containerRef.current:",
          containerRef.current
        );
        if (!containerRef.current) {
          console.warn(
            "initializeGraph - Exiting early: containerRef.current is null"
          );
          return;
        }
        if (!documentSimilaritiesData) {
          // Check prop here
          console.warn(
            "initializeGraph - Exiting early: documentSimilaritiesData is not yet loaded"
          );
          return;
        }
        cleanupGraph();

        try {
          console.log("initializeGraph - Starting D3 graph creation...");
          const container = d3.select(containerRef.current);
          const svg = container
            .append("svg")
            .attr("width", width)
            .attr("height", height)
            .attr("className", "rounded-lg");

          svgRef.current = svg;
          console.log("initializeGraph - SVG element appended");

          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");
          console.log("initializeGraph - Background rectangle appended");

          if (!visibleDocsForGraph.length) {
            console.log(
              "initializeGraph - No visible docs to display (visibleDocsForGraph is empty), showing 'No documents' message"
            );
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);

            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
            return;
          } else {
            console.log(
              "initializeGraph - visibleDocsForGraph is NOT empty, proceeding to draw graph with",
              visibleDocsForGraph.length,
              "documents"
            );
          }

          const initialTranslateX = width / 3 - 25;
          const initialTranslateY = height / 4 + 10;
          const initialScale = 0.45;

          const g = svg.append("g");
          console.log("initializeGraph - Group 'g' appended to SVG");

          const zoom = d3
            .zoom()
            .scaleExtent([initialScale, 3])
            .translateExtent([
              [0, 0],
              [width, height],
            ])
            .on("zoom", (event) => {
              g.attr("transform", event.transform);
            });

          svg.call(
            zoom.transform,
            d3.zoomIdentity
              .translate(initialTranslateX, initialTranslateY)
              .scale(initialScale)
          );

          svg.call(zoom);
          console.log(
            "initializeGraph - Zoom behavior initialized and applied"
          );

          const visibleDocIndicesMap = {};
          visibleDocsForGraph.forEach((doc, index) => {
            visibleDocIndicesMap[doc.originalIndex] = index;
          });
          console.log(
            "initializeGraph - visibleDocIndicesMap:",
            visibleDocIndicesMap
          );

          const nodes = visibleDocsForGraph.map((doc, i) => ({
            id: String(i),
            title: doc.title,
            fullText: doc.description,
            tags: doc.tags,
            readTime: doc.readTime,
            releaseDate: doc.releaseDate,
            isRead: doc.isRead,
          }));
          console.log(
            "initializeGraph - Nodes created - nodes.length:",
            nodes.length
          );

          let links = documentSimilaritiesData // Use prop here
            .filter((link) => {
              return (
                link.source in visibleDocIndicesMap &&
                link.target in visibleDocIndicesMap
              );
            })
            .map((link) => ({
              ...link,
              source: String(visibleDocIndicesMap[link.source]),
              target: String(visibleDocIndicesMap[link.target]),
            }));
          console.log(
            "initializeGraph - Filtered Links - links.length:",
            links?.length
          );

          if (!links || links.length === 0) {
            console.warn(
              "initializeGraph - No pre-calculated similarities found for visible documents. Links array will be empty."
            );
            links = [];
          } else {
            console.log(
              "initializeGraph - Loaded pre-calculated similarities from document_similarities.json"
            );
          }

          initializeSimulation(
            nodes,
            links,
            width,
            height,
            g,
            svg,
            visibleDocsForGraph.map((doc) => doc.readTime)
          );
          console.log("initializeGraph - initializeSimulation called");
        } catch (error) {
          console.error(
            "initializeGraph - ERROR during graph initialization:",
            error
          );
          setError("Error initializing graph. See console for details.");
        }
        console.log("initializeGraph function - COMPLETE");
      };

      const initializeSimulation = (
        nodes,
        links,
        width,
        height,
        g,
        svg,
        readTimes
      ) => {
        console.log(
          "initializeSimulation called - nodes.length:",
          nodes.length,
          "links.length:",
          links.length
        );
        nodes.forEach((node) => {
          node.x = width / 2 + (Math.random() - 0.5) * 40;
          node.y = height / 2 + (Math.random() - 0.5) * 40;
        });
        console.log("initializeSimulation - Initial node positions set");

        simulationRef.current = d3
          .forceSimulation()
          .nodes(nodes)
          .force(
            "link",
            d3
              .forceLink(links)
              .id((d) => d.id)
              .distance(50)
              .strength(0.5)
          )
          .force("charge", d3.forceManyBody().strength(-300))
          .force("center", d3.forceCenter(width / 2, height / 2))
          .force("x", d3.forceX(width / 2).strength(0.1))
          .force("y", d3.forceY(height / 2).strength(0.1))
          .force(
            "collision",
            d3
              .forceCollide()
              .strength(0.1)
              .radius(
                (d) =>
                  normalizeReadTime(
                    d.readTime,
                    Math.min(...readTimes),
                    Math.max(...readTimes)
                  ) + 10
              )
          )
          .force("boundary", forceBoundary(width, height, nodes))
          .alpha(0.3)
          .velocityDecay(0.4)
          .alphaDecay(0.05)
          .alphaMin(0.001);
        console.log("initializeSimulation - Force simulation created");

        const link = g
          .append("g")
          .selectAll("line")
          .data(links)
          .join("line")
          .attr("stroke", "var(--edge-color)")
          .attr("stroke-width", (d) => d.width)
          .attr("stroke-opacity", "var(--edge-opacity)");
        console.log("initializeSimulation - Links (edges) appended");

        const nodeGroup = g.append("g").attr("className", "nodes");
        const node = nodeGroup
          .selectAll("g")
          .data(nodes)
          .join("g")
          .attr("transform", (d) => `translate(${d.x},${d.y})`)
          .call(
            d3
              .drag()
              .on("start", dragstarted)
              .on("drag", dragged)
              .on("end", dragended)
          );
        console.log(
          "initializeSimulation - Nodes (groups) appended and drag behavior added"
        );

        node
          .append("circle")
          .attr("r", (d) =>
            normalizeReadTime(
              d.readTime,
              Math.min(...readTimes),
              Math.max(...readTimes)
            )
          )
          .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
          .attr("stroke", "var(--node-stroke-color)")
          .on("mouseover", function (event, d) {
            d3.select(this).attr("fill", "#8ACE00");
            link
              .attr("stroke", (l) =>
                l.source.id === d.id || l.target.id === d.id
                  ? "#8ACE00"
                  : "var(--edge-color)"
              )
              .attr("stroke-width", (l) =>
                l.source.id === d.id || l.target.id === d.id ? 3 : l.width
              );

            const titleGroup = nodeGroup
              .append("g")
              .attr("className", "node-title-group")
              .attr("transform", `translate(${d.x},${d.y})`);
            nodeTitleGroupRef.current = titleGroup;

            titleGroup
              .append("text")
              .attr("className", "node-title")
              .attr("x", 10)
              .attr("y", 5)
              .attr("fill", "var(--text-color)")
              .text(d.title);
          })
          .on("mouseout", function (event, d) {
            d3.select(this).attr("fill", (d) =>
              d.isRead ? "#4a9eff" : "var(--node-color)"
            );
            link
              .attr("stroke", "var(--edge-color)")
              .attr("stroke-width", (l) => l.width);
            if (nodeTitleGroupRef.current) {
              nodeTitleGroupRef.current.remove();
              nodeTitleGroupRef.current = null;
            }
          });
        console.log(
          "initializeSimulation - Node circles appended and event handlers added"
        );

        simulationRef.current.on("tick", () => {
          link
            .attr("x1", (d) => boundPosition(d.source.x, width, -200))
            .attr("y1", (d) => boundPosition(d.source.y, height, -200))
            .attr("x2", (d) => boundPosition(d.target.x, width, -200))
            .attr("y2", (d) => boundPosition(d.target.y, height, -200));

          node.attr("transform", (d) => {
            const x = boundPosition(d.x, width, -200);
            const y = boundPosition(d.y, height, -200);
            return `translate(${x},${y})`;
          });
        });
        console.log("initializeSimulation - Simulation 'tick' handler set");
        console.log("initializeSimulation - COMPLETE");
      };

      const dragstarted = (event, d) => {
        if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
        d.fx = d.x;
        d.fy = d.y;

        simulationRef.current.force("link").strength(0.8);
        simulationRef.current.alphaTarget(0.3).restart();
      };

      const dragged = (event, d) => {
        simulationRef.current.alphaTarget(0.01);
        d.fx = boundPosition(event.x, width, -200);
        d.fy = boundPosition(event.y, height, -200);
      };

      const dragended = (event, d) => {
        if (!event.active) simulationRef.current.alphaTarget(0.01);
        d.fx = null;
        d.fy = null;
      };

      const forceBoundary = (width, height, nodes) => {
        const padding = 4;
        const xMin = padding;
        const xMax = width - padding;
        const yMin = padding;
        const yMax = height - padding;
        const strength = 0.05;

        function force(alpha) {
          nodes.forEach((node) => {
            if (node.x < xMin) {
              node.vx += (xMin - node.x) * strength * alpha;
            }
            if (node.x > xMax) {
              node.vx += (xMax - node.x) * strength * alpha;
            }
            if (node.y < yMin) {
              node.vy += (yMin - node.y) * strength * alpha;
            }
            if (node.y > yMax) {
              node.vy += (yMax - node.y) * strength * alpha;
            }
          });
        }

        return force;
      };

      const boundPosition = (position, dimension, padding) => {
        return Math.max(padding, Math.min(dimension - padding, position));
      };

      const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
        const minRadius = 5;
        const maxRadius = 20;
        if (maxReadTime === minReadTime) return (minRadius + maxRadius) / 2;
        const logMin = Math.log(minReadTime + 1);
        const logMax = Math.log(maxReadTime + 1);
        const logReadTime = Math.log(readTime + 1);
        return (
          ((logReadTime - logMin) / (logMax - logMin)) *
            (maxRadius - minRadius) +
          minRadius
        );
      };

      if (isSmallScreen) {
        return null;
      }

      if (error) {
        return (
          <div
            className="flex items-center justify-center h-32 rounded-lg"
            style={{
              backgroundColor: "var(--graph-background)",
              color: "var(--text-color)",
            }}
          >
            <p className="text-red-600">{error}</p>
          </div>
        );
      }

      return <div ref={containerRef} className="w-full h-full" />;
    }
  );
  DocumentGraph.displayName = "DocumentGraph"; // Explicitly set the display name for memoization

  // --- JSX Markup ---
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) => handleSortButtonClick(event, "release")}
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) => handleSortButtonClick(event, "time")}
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) => handleReadFilterButtonClick(event)}
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) => handleFilterTypeButtonClick(event, "YT")}
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) => handleFilterTypeButtonClick(event, "Arxiv")}
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) => handleFilterTypeButtonClick(event, "Site")}
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list"> {/* React-rendered list here */}
            {readingListData.filter(item => item.isVisible).map((item, index) => (
              <li key={item.originalIndex} style={{ display: item.isVisible ? 'block' : 'none' }}>
                <div className="list-item">
                  <div className="item-details">
                    <div className="item-title">
                      <strong>"{item.title}"</strong>
                    </div>
                    <p className="item-author">Author: {item.author}</p>
                    <p className="item-read-time">Read Time: {item.readTime} minutes</p>
                    <p className="item-release-date">Release Date: {item.releaseDate}</p>
                    <div className="item-tags">
                      Tags: {item.tags.map((tag, tagIndex) => (
                        <span key={tagIndex} className="tag">{tag}</span>
                      ))}
                    </div>
                  </div>
                  <div className="item-links">
                    <a href={item.url} target="_blank" rel="noopener noreferrer" className="read-more">
                      Read more
                    </a>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>

        {/* Pass similaritiesRawData directly as prop */}
        <div className="graph-container">
          <div id="root" className="graph-inner">
            <div className="space-y-6">
              <DocumentGraph
                width={400}
                height={400}
                readingListData={readingListData.filter(item => item.isVisible)} // Pass only visible documents
                documentSimilaritiesData={similaritiesRawData} // Pass similaritiesRawData here
              />
            </div>
          </div>
        </div>
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
