import React, { useState, useEffect, useRef } from "react";
import "../css/landing-style.css"; 
import "../css/library.css"
import "../functionality.js";
import { Link } from "react-router-dom";

function TemplateComponent() {
  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");

  // For multiple collapsible sections, maintain an object keyed by section name.
  const [sections, setSections] = useState({
    section1: false,
    section2: false,
    // add more sections as needed
  });

  const scrollToTopButtonRef = useRef(null);

  // --- Helper Functions ---
  const toggleTheme = () => {
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  const toggleSection = (sectionKey) => {
    setSections((prev) => ({
      ...prev,
      [sectionKey]: !prev[sectionKey],
    }));
  };

  // --- Effects ---
  // Update body attribute for theme changes.
  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  // Control the visibility of the scroll-to-top button.
  useEffect(() => {
    const scrollButton = scrollToTopButtonRef.current;
    const handleScroll = () => {
      if (window.scrollY > 300) {
        scrollButton.classList.remove("opacity-0");
        scrollButton.classList.add("opacity-100");
      } else {
        scrollButton.classList.remove("opacity-100");
        scrollButton.classList.add("opacity-0");
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // You can add additional useEffect hooks for dynamic UI updates if needed.

  // --- JSX Markup ---
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>Your Template Title</title>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">YourLogo</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            {/* Replace these SVGs with your preferred icons */}
            <svg className="sun-icon" viewBox="0 0 24 24" fill="none">
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg className="moon-icon" viewBox="0 0 24 24" fill="none">
              <path d="M21 12.79A9 9 0 1 1 11.21 3"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          {/* Update navigation links as needed */}
          <Link to="/section1" className="nav-link">
            Section1
          </Link>
          <Link to="/section2" className="nav-link">
            Section2
          </Link>
          <Link to="/" className="nav-link">
            Home
          </Link>
        </nav>
      </header>

      {/* Main Content Container */}
      <div className="container">
        <main>
          {/* --- Section 1 --- */}
          <section className={`series ${sections.section1 ? "collapsed" : ""}`}>
            <div
              className="series-header"
              onClick={() => toggleSection("section1")}
            >
              <h2 className="series-title">Section Title 1</h2>
              <svg
                className="collapse-icon plus-icon"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
              >
                <line x1="12" y1="5" x2="12" y2="19"></line>
                <line x1="5" y1="12" x2="19" y2="12"></line>
              </svg>
              <svg
                className="collapse-icon minus-icon"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
              >
                <line x1="5" y1="12" x2="19" y2="12"></line>
              </svg>
            </div>
            <div className="series-content">
              <div className="posts">
                {/* Replace with your content items */}
                <Link to="/your-content" className="post-card">
                  <div className="post-main">
                    <h3 className="post-title">Content Title</h3>
                    <p className="post-description">
                      A brief description of the content goes here.
                    </p>
                    <div className="metadata">
                      <span>Date</span> · <span>Reading Time</span>
                    </div>
                    <div className="tags">
                      <span className="tag">Tag1</span>
                      <span className="tag">Tag2</span>
                    </div>
                  </div>
                </Link>
              </div>
            </div>
          </section>

          {/* --- Section 2 --- */}
          <section className={`series ${sections.section2 ? "collapsed" : ""}`}>
            <div
              className="series-header"
              onClick={() => toggleSection("section2")}
            >
              <h2 className="series-title">Section Title 2</h2>
              <svg
                className="collapse-icon plus-icon"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
              >
                <line x1="12" y1="5" x2="12" y2="19"></line>
                <line x1="5" y1="12" x2="19" y2="12"></line>
              </svg>
              <svg
                className="collapse-icon minus-icon"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
              >
                <line x1="5" y1="12" x2="19" y2="12"></line>
              </svg>
            </div>
            <div className="series-content">
              <div className="posts">
                {/* Add more content items as needed */}
              </div>
            </div>
          </section>
        </main>
      </div>

      {/* Scroll-to-Top Button */}
      <div
        id="scrollToTop"
        className="fixed bottom-6 right-6 opacity-0 transition-opacity duration-200 cursor-pointer"
        onClick={scrollToTop}
        ref={scrollToTopButtonRef}
      >
        <div className="bg-zinc-800 hover:bg-zinc-700 rounded-full p-3 shadow-lg">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
          >
            <path d="M18 15l-6-6-6 6" />
          </svg>
        </div>
      </div>

      {/* Additional Elements */}
      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>
      <p className="hidden-message">
        Your hidden message or SEO text goes here.
      </p>
    </>
  );
}

export default TemplateComponent;
