// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife, searchItems } from "../functionality.js"; // searchItems is not used anymore, but keeping import for now
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library
// =====================
function Library() {
  console.log("Library Component RENDER"); // Component render log

  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [currentReadingListData, setCurrentReadingListData] = useState([]); // Initialize as empty array
  const [readingListData, setReadingListData] = useState([]); // State to hold fetched data
  const [documentSimilaritiesData, setDocumentSimilaritiesData] =
    useState(null); // NEW STATE VARIABLE for similarities data
  const [filteredDocs, setFilteredDocs] = useState([]); // Not used, can remove if confirmed

  console.log("Library State Initialized:", {
    theme,
    currentReadingListData,
    readingListData,
    documentSimilaritiesData,
  });

  // --- Helper Functions ---
  const toggleTheme = () => {
    console.log("toggleTheme called");
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    console.log("scrollToTop called");
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // --- Effects ---
  useEffect(() => {
    console.log("Library useEffect - Theme update - theme:", theme);
    document.body.setAttribute("data-theme", theme);
    console.log("Library useEffect - Theme update COMPLETE");
  }, [theme]);

  useEffect(() => {
    console.log("Library useEffect - GameOfLife runGameOfLife('gameOfLife')");
    runGameOfLife("gameOfLife");
    console.log("Library useEffect - GameOfLife COMPLETE");
  }, []);

  useEffect(() => {
    console.log("Library useEffect - Loading reading list data...");
    try {
      const readingListData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));

      setReadingListData(readingListData);
      setCurrentReadingListData(readingListData);
      setDocumentSimilaritiesData(similaritiesRawData); // Load similarities data here
      console.log(
        "Library useEffect - Reading list data loaded successfully. Length:",
        readingListData.length
      );
      console.log(
        "Library useEffect - documentSimilaritiesData loaded successfully:",
        similaritiesRawData
      ); // Log loaded similarities data
    } catch (error) {
      console.error(
        "Library useEffect - Error importing reading list data:",
        error
      );
    }
    console.log("Library useEffect - Loading reading list data - COMPLETE");
  }, []);

  useEffect(() => {
    console.log(
      "Library useEffect - initializeReadingListHTML (currentReadingListData) - currentReadingListData.length:",
      currentReadingListData.length
    );
    if (currentReadingListData.length > 0) {
      console.log(
        "Library useEffect - currentReadingListData:",
        currentReadingListData
      );
      window.initializeReadingListHTML(currentReadingListData);
    } else {
      console.warn(
        "Library useEffect - currentReadingListData is empty or not yet loaded, skipping initializeReadingListHTML."
      );
    }
    console.log(
      "Library useEffect - initializeReadingListHTML (currentReadingListData) - COMPLETE"
    );
  }, [currentReadingListData]);

  const useModel = window.use;
  window.documentVisibilityChanged = new Event("documentVisibilityChanged");

  const debounce = (func, wait) => {
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  const performSearch = (query, data) => {
    console.log(
      "performSearch called - query:",
      query,
      "data.length:",
      data.length
    );
    if (!query) {
      console.log("performSearch - No query, returning original data");
      return data;
    }
    const filtered = data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
    console.log("performSearch - Filtered results length:", filtered.length);
    return filtered;
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    console.log("handleSearchInput - query:", query);
    const filteredData = performSearch(query, readingListData);
    console.log(
      "handleSearchInput - performSearch returned - filteredData.length:",
      filteredData.length
    ); // LOG
    setCurrentReadingListData(filteredData);
    console.log(
      "handleSearchInput - setCurrentReadingListData called - new currentReadingListData.length:",
      filteredData.length
    );
    console.log(
      "handleSearchInput - Dispatching documentVisibilityChanged event"
    );
    window.dispatchEvent(window.documentVisibilityChanged);
    console.log(
      "handleSearchInput - documentVisibilityChanged event dispatched - COMPLETE"
    );
  };

  const toggleSort = (target, sortBy, sortOrder, data) => {
    // Added data argument and using currentReadingListData
    console.log("toggleSort called", {
      sortBy,
      sortOrder,
      dataLength: data.length,
    });
    // Placeholder for actual sort logic - Assuming it modifies 'data' and returns sorted data
    let sortedData = [...data]; // Create a copy to avoid direct mutation - PLACEHOLDER
    if (sortBy === "release") {
      sortedData.sort(
        (a, b) => new Date(b.releaseDate) - new Date(a.releaseDate)
      ); // Example sort
    } else if (sortBy === "time") {
      sortedData.sort((a, b) => b.readTime - a.readTime); // Example sort
    }
    console.log(
      "toggleSort - Sorting logic applied - sortedData.length:",
      sortedData.length
    ); // LOG
    setCurrentReadingListData(sortedData);
    console.log(
      "toggleSort - setCurrentReadingListData called - new currentReadingListData.length:",
      sortedData.length
    ); // LOG
    window.dispatchEvent(window.documentVisibilityChanged);
    console.log(
      "toggleSort - documentVisibilityChanged event dispatched - COMPLETE"
    );
  };

  const toggleReadFilter = (target, data) => {
    // Added data argument and using currentReadingListData
    console.log("toggleReadFilter called - data.length:", data.length);
    // Placeholder for filter logic - Assuming it modifies 'data' and returns filtered data
    let filteredData = [...data]; // Create a copy - PLACEHOLDER
    // Example filter - Showing all for now, actual logic would go here
    console.log(
      "toggleReadFilter - Filter logic applied (showing all) - filteredData.length:",
      filteredData.length
    ); // LOG
    setCurrentReadingListData(filteredData);
    console.log(
      "toggleReadFilter - setCurrentReadingListData called - new currentReadingListData.length:",
      filteredData.length
    ); // LOG
    window.dispatchEvent(window.documentVisibilityChanged);
    console.log(
      "toggleReadFilter - documentVisibilityChanged event dispatched - COMPLETE"
    );
  };

  const toggleFilterType = (target, filterType, data) => {
    // Added data argument and using currentReadingListData
    console.log("toggleFilterType called", {
      filterType,
      dataLength: data.length,
    });
    // Placeholder for filter logic - Assuming it modifies 'data' and returns filtered data
    let filteredData = data.filter((item) => item.tags.includes(filterType)); // Example filter
    console.log(
      "toggleFilterType - Filter logic applied - filteredData.length:",
      filteredData.length
    ); // LOG
    setCurrentReadingListData(filteredData);
    console.log(
      "toggleFilterType - setCurrentReadingListData called - new currentReadingListData.length:",
      filteredData.length
    ); // LOG
    window.dispatchEvent(window.documentVisibilityChanged);
    console.log(
      "toggleFilterType - documentVisibilityChanged event dispatched - COMPLETE"
    );
  };

  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = ({
    width,
    height,
    readingListData, // Expects currentReadingListData from Library now
    documentSimilaritiesData,
  }) => {
    console.log(
      "DocumentGraph Component RENDER - readingListData.length:",
      readingListData?.length,
      "documentSimilaritiesData:",
      documentSimilaritiesData
    ); // Component render log

    const containerRef = useRef(null);
    const [error, setError] = useState(null); // Consider using error for graph errors
    // visibleDocs state and setVisibleDocs are REMOVED
    const [isSmallScreen, setIsSmallScreen] = useState(false);
    const svgRef = useRef(null);
    const simulationRef = useRef(null);
    const nodeTitleGroupRef = useRef(null); // Ref to hold the node title group

    console.log("DocumentGraph State Initialized:", { isSmallScreen }); // visibleDocs removed from log

    const checkScreenSize = () => {
      const smallScreen = window.innerWidth <= 768;
      setIsSmallScreen(smallScreen);
      console.log("checkScreenSize - isSmallScreen:", smallScreen);
    };

    const debouncedCheckScreenSize = debounce(checkScreenSize, 100);

    useEffect(() => {
      console.log("DocumentGraph useEffect - Screen Size Check - Initial");
      checkScreenSize();
      window.addEventListener("resize", debouncedCheckScreenSize);
      return () => {
        window.removeEventListener("resize", debouncedCheckScreenSize);
        console.log("DocumentGraph useEffect - Screen Size Check - Cleanup");
      };
    }, []);

    useEffect(() => {
      console.log(
        "DocumentGraph useEffect - Initialize Graph or Cleanup - isSmallScreen:",
        isSmallScreen,
        "readingListData.length:",
        readingListData?.length, // LOG - readingListData prop length
        "documentSimilaritiesData:",
        documentSimilaritiesData
      );

      if (!documentSimilaritiesData) {
        console.warn(
          "DocumentGraph useEffect - documentSimilaritiesData is still null/undefined. Graph initialization skipped."
        );
        return; // Exit if similarities data is not yet loaded
      }

      if (!isSmallScreen && readingListData && readingListData.length > 0) {
        console.log(
          "DocumentGraph useEffect - Initializing graph - readingListData.length:",
          readingListData.length
        );

        const currentVisibleDocs = getVisibleDocuments(); // <---- Calculate visibleDocs HERE
        console.log(
          "DocumentGraph useEffect - getVisibleDocuments returned - currentVisibleDocs.length:",
          currentVisibleDocs.length
        );
        initializeGraph(currentVisibleDocs); // <---- Pass currentVisibleDocs to initializeGraph
      } else if (
        !isSmallScreen &&
        (!readingListData || readingListData.length === 0)
      ) {
        console.log(
          "DocumentGraph useEffect - Cleaning up graph - readingListData is empty or invalid"
        );
        cleanupGraph();
        if (svgRef.current) {
          const svg = d3.select(svgRef.current);
          svg.selectAll("*").remove();
          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");
          const textGroup = svg
            .append("g")
            .attr("transform", `translate(${width / 2}, ${height / 2})`);

          textGroup
            .append("text")
            .attr("text-anchor", "middle")
            .attr("fill", "var(--text-color)")
            .text("No documents to display");
        }
      } else {
        console.log(
          "DocumentGraph useEffect - Skipping graph update (small screen or no data)"
        );
      }
      console.log(
        "DocumentGraph useEffect - Initialize Graph or Cleanup - COMPLETE"
      );
    }, [
      // visibleDocs dependency REMOVED
      isSmallScreen,
      width,
      height,
      documentSimilaritiesData,
      readingListData, // Keep readingListData dependency
    ]);

    const getVisibleDocuments = useCallback(() => {
      console.log(
        "getVisibleDocuments called - readingListData.length:",
        readingListData?.length
      );
      if (!readingListData || readingListData.length === 0) {
        console.log("getVisibleDocuments - readingListData is empty or null");
        return [];
      }

      console.log(
        "getVisibleDocuments - Returning readingListData directly. Length:",
        readingListData.length
      ); // Modified log
      return readingListData; // <--- Return readingListData directly
    }, [readingListData]); // useCallback now depends on readingListData

    // documentVisibilityChanged useEffect REMOVED

    const cleanupGraph = useCallback(() => {
      console.log("cleanupGraph called");
      if (simulationRef.current) {
        simulationRef.current.stop();
        simulationRef.current = null;
        console.log("cleanupGraph - Simulation stopped");
      }
      if (containerRef.current) {
        const container = d3.select(containerRef.current);
        container.selectAll("*").remove();
        console.log("cleanupGraph - D3 container cleared");
      }
      console.log("cleanupGraph - COMPLETE");
    }, []);

    const initializeGraph = async (visibleDocsForGraph) => {
      // <---- Accept visibleDocs as argument
      console.log(
        "initializeGraph function is being called - visibleDocsForGraph.length:",
        visibleDocsForGraph.length // LOG - Argument length
      );
      console.log(
        "initializeGraph - containerRef.current:",
        containerRef.current
      );
      if (!containerRef.current) {
        console.warn(
          "initializeGraph - Exiting early: containerRef.current is null"
        );
        return;
      }
      if (!documentSimilaritiesData) {
        console.warn(
          "initializeGraph - Exiting early: documentSimilaritiesData is not yet loaded"
        );
        return;
      }
      cleanupGraph();

      try {
        console.log("initializeGraph - Starting D3 graph creation...");
        const container = d3.select(containerRef.current);
        const svg = container
          .append("svg")
          .attr("width", width)
          .attr("height", height)
          .attr("className", "rounded-lg");

        svgRef.current = svg;
        console.log("initializeGraph - SVG element appended");

        svg
          .append("rect")
          .attr("width", "100%")
          .attr("height", "100%")
          .attr("fill", "var(--graph-background)");
        console.log("initializeGraph - Background rectangle appended");

        if (!visibleDocsForGraph.length) {
          // Use argument here
          console.log(
            "initializeGraph - No visible docs to display (visibleDocsForGraph is empty), showing 'No documents' message"
          );
          const textGroup = svg
            .append("g")
            .attr("transform", `translate(${width / 2}, ${height / 2})`);

          textGroup
            .append("text")
            .attr("text-anchor", "middle")
            .attr("fill", "var(--text-color)")
            .text("No documents to display");
          return;
        } else {
          console.log(
            "initializeGraph - visibleDocsForGraph is NOT empty, proceeding to draw graph with",
            visibleDocsForGraph.length,
            "documents"
          );
        }

        const initialTranslateX = width / 3 - 25;
        const initialTranslateY = height / 4 + 10;
        const initialScale = 0.45;

        const g = svg.append("g");
        console.log("initializeGraph - Group 'g' appended to SVG");

        const zoom = d3
          .zoom()
          .scaleExtent([initialScale, 3])
          .translateExtent([
            [0, 0],
            [width, height],
          ])
          .on("zoom", (event) => {
            g.attr("transform", event.transform);
          });

        svg.call(
          zoom.transform,
          d3.zoomIdentity
            .translate(initialTranslateX, initialTranslateY)
            .scale(initialScale)
        );

        svg.call(zoom);
        console.log("initializeGraph - Zoom behavior initialized and applied");

        // Create a mapping from visible doc original indices to new node indices
        const visibleDocIndicesMap = {};
        visibleDocsForGraph.forEach((doc, index) => {
          // Use argument here
          visibleDocIndicesMap[doc.originalIndex] = index;
        });
        console.log(
          "initializeGraph - visibleDocIndicesMap:",
          visibleDocIndicesMap
        );

        const nodes = visibleDocsForGraph.map((doc, i) => ({
          // Use argument here
          id: String(i), // Use new index as node ID
          title: doc.title,
          fullText: doc.description,
          tags: doc.tags,
          readTime: doc.readTime,
          releaseDate: doc.releaseDate,
          isRead: doc.isRead,
        }));
        console.log(
          "initializeGraph - Nodes created - nodes.length:",
          nodes.length
        );

        // Directly use imported documentSimilaritiesData and remap indices
        let links = documentSimilaritiesData // Use prop here
          .filter((link) => {
            return (
              link.source in visibleDocIndicesMap &&
              link.target in visibleDocIndicesMap
            );
          })
          .map((link) => ({
            ...link,
            source: String(visibleDocIndicesMap[link.source]), // Remap source index
            target: String(visibleDocIndicesMap[link.target]), // Remap target index
          }));
        console.log(
          "initializeGraph - Filtered Links - links.length:",
          links?.length
        );

        if (!links || links.length === 0) {
          console.warn(
            "initializeGraph - No pre-calculated similarities found for visible documents. Links array will be empty."
          );
          links = []; // Ensure links is at least an empty array for simulation initialization
        } else {
          console.log(
            "initializeGraph - Loaded pre-calculated similarities from document_similarities.json"
          );
        }

        initializeSimulation(
          nodes,
          links,
          width,
          height,
          g,
          svg,
          visibleDocsForGraph.map((doc) => doc.readTime) // Use argument here
        );
        console.log("initializeGraph - initializeSimulation called");
      } catch (error) {
        console.error(
          "initializeGraph - ERROR during graph initialization:",
          error
        );
        setError("Error initializing graph. See console for details."); // Set error state
      }
      console.log("initializeGraph function - COMPLETE");
    };

    const initializeSimulation = (
      nodes,
      links,
      width,
      height,
      g,
      svg,
      readTimes
    ) => {
      console.log(
        "initializeSimulation called - nodes.length:",
        nodes.length,
        "links.length:",
        links.length
      );
      nodes.forEach((node) => {
        node.x = width / 2 + (Math.random() - 0.5) * 40;
        node.y = height / 2 + (Math.random() - 0.5) * 40;
      });
      console.log("initializeSimulation - Initial node positions set");

      simulationRef.current = d3
        .forceSimulation()
        .nodes(nodes)
        .force(
          "link",
          d3
            .forceLink(links)
            .id((d) => d.id)
            .distance(50)
            .strength(0.5) // Link strength - kept at 0.5
        )
        .force("charge", d3.forceManyBody().strength(-300))
        .force("center", d3.forceCenter(width / 2, height / 2))
        .force("x", d3.forceX(width / 2).strength(0.1))
        .force("y", d3.forceY(height / 2).strength(0.1))
        .force(
          "collision",
          d3
            .forceCollide()
            .strength(0.1)
            .radius(
              (d) =>
                normalizeReadTime(
                  d.readTime,
                  Math.min(...readTimes),
                  Math.max(...readTimes)
                ) + 10
            )
        )
        .force("boundary", forceBoundary(width, height, nodes))
        .alpha(0.3)
        .velocityDecay(0.4) // Increased velocity decay to 0.4
        .alphaDecay(0.05)
        .alphaMin(0.001);
      console.log("initializeSimulation - Force simulation created");

      const link = g
        .append("g")
        .selectAll("line")
        .data(links)
        .join("line")
        .attr("stroke", "var(--edge-color)")
        .attr("stroke-width", (d) => d.width)
        .attr("stroke-opacity", "var(--edge-opacity)");
      console.log("initializeSimulation - Links (edges) appended");

      const nodeGroup = g.append("g").attr("className", "nodes");
      const node = nodeGroup
        .selectAll("g")
        .data(nodes)
        .join("g")
        .attr("transform", (d) => `translate(${d.x},${d.y})`)
        .call(
          d3
            .drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended)
        );
      console.log(
        "initializeSimulation - Nodes (groups) appended and drag behavior added"
      );

      node
        .append("circle")
        .attr("r", (d) =>
          normalizeReadTime(
            d.readTime,
            Math.min(...readTimes),
            Math.max(...readTimes)
          )
        )
        .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
        .attr("stroke", "var(--node-stroke-color)")
        .on("mouseover", function (event, d) {
          d3.select(this).attr("fill", "#8ACE00");
          link
            .attr("stroke", (l) =>
              l.source.id === d.id || l.target.id === d.id
                ? "#8ACE00"
                : "var(--edge-color)"
            )
            .attr("stroke-width", (l) =>
              l.source.id === d.id || l.target.id === d.id ? 3 : l.width
            );

          // Create node title group and store reference
          const titleGroup = nodeGroup
            .append("g")
            .attr("className", "node-title-group")
            .attr("transform", `translate(${d.x},${d.y})`);
          nodeTitleGroupRef.current = titleGroup; // Store the reference

          titleGroup
            .append("text")
            .attr("className", "node-title")
            .attr("x", 10)
            .attr("y", 5)
            .attr("fill", "var(--text-color)")
            .text(d.title);
        })
        .on("mouseout", function (event, d) {
          d3.select(this).attr("fill", (d) =>
            d.isRead ? "#4a9eff" : "var(--node-color)"
          );
          link
            .attr("stroke", "var(--edge-color)")
            .attr("stroke-width", (l) => l.width);
          // Remove the specific node title group using the stored reference
          if (nodeTitleGroupRef.current) {
            nodeTitleGroupRef.current.remove();
            nodeTitleGroupRef.current = null; // Clear the reference
          }
        });
      console.log(
        "initializeSimulation - Node circles appended and event handlers added"
      );

      simulationRef.current.on("tick", () => {
        link
          .attr("x1", (d) => boundPosition(d.source.x, width, -200))
          .attr("y1", (d) => boundPosition(d.source.y, height, -200))
          .attr("x2", (d) => boundPosition(d.target.x, width, -200))
          .attr("y2", (d) => boundPosition(d.target.y, height, -200));

        node.attr("transform", (d) => {
          const x = boundPosition(d.x, width, -200);
          const y = boundPosition(d.y, height, -200);
          return `translate(${x},${y})`;
        });
      });
      console.log("initializeSimulation - Simulation 'tick' handler set");
      console.log("initializeSimulation - COMPLETE");
    };

    const dragstarted = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      d.fx = d.x;
      d.fy = d.y;

      // Increase link strength during drag start
      simulationRef.current.force("link").strength(0.8); // Increased strength
      simulationRef.current.alphaTarget(0.3).restart(); // Reheat simulation for stronger effect
    };

    const dragged = (event, d) => {
      simulationRef.current.alphaTarget(0.01);
      d.fx = boundPosition(event.x, width, -200);
      d.fy = boundPosition(event.y, height, -200);
    };

    const dragended = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      d.fx = null;
      d.fy = null;
    };

    const forceBoundary = (width, height, nodes) => {
      // Add nodes as argument
      const padding = 4;
      const xMin = padding;
      const xMax = width - padding;
      const yMin = padding;
      const yMax = height - padding;
      const strength = 0.05; // Adjust strength as needed

      function force(alpha) {
        nodes.forEach((node) => {
          // Use the nodes argument here
          if (node.x < xMin) {
            node.vx += (xMin - node.x) * strength * alpha;
          }
          if (node.x > xMax) {
            node.vx += (xMax - node.x) * strength * alpha;
          }
          if (node.y < yMin) {
            node.vy += (yMin - node.y) * strength * alpha;
          }
          if (node.y > yMax) {
            node.vy += (yMax - node.y) * strength * alpha;
          }
        });
      }

      return force;
    };

    const boundPosition = (position, dimension, padding) => {
      return Math.max(padding, Math.min(dimension - padding, position));
    };

    const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
      const minRadius = 5;
      const maxRadius = 20;
      if (maxReadTime === minReadTime) return (minRadius + maxRadius) / 2; // Handle case where min and max are the same to avoid division by zero.
      const logMin = Math.log(minReadTime + 1);
      const logMax = Math.log(maxReadTime + 1);
      const logReadTime = Math.log(readTime + 1);
      return (
        ((logReadTime - logMin) / (logMax - logMin)) * (maxRadius - minRadius) +
        minRadius
      );
    };

    if (isSmallScreen) {
      return null;
    }

    if (error) {
      return (
        <div
          className="flex items-center justify-center h-32 rounded-lg"
          style={{
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="text-red-600">{error}</p>
        </div>
      );
    }

    return <div ref={containerRef} className="w-full h-full" />;
  };

  // --- JSX Markup ---
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) =>
                  toggleSort(
                    event.target,
                    "release",
                    "desc",
                    currentReadingListData
                  )
                } // Pass readingListData
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) =>
                  toggleSort(
                    event.target,
                    "time",
                    "desc",
                    currentReadingListData
                  )
                } // Pass readingListData
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) =>
                  toggleReadFilter(event.target, currentReadingListData)
                } // Pass readingListData
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) =>
                  toggleFilterType(event.target, "YT", currentReadingListData)
                } // Pass readingListData
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) =>
                  toggleFilterType(
                    event.target,
                    "Arxiv",
                    currentReadingListData
                  )
                } // Pass readingListData
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) =>
                  toggleFilterType(event.target, "Site", currentReadingListData)
                } // Pass readingListData
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list"></ul>
        </div>

        {documentSimilaritiesData && ( // Conditional rendering of DocumentGraph - IMPORTANT
          <div className="graph-container">
            <div id="root" className="graph-inner">
              <div className="space-y-6">
                <DocumentGraph
                  width={400}
                  height={400}
                  readingListData={currentReadingListData} // Pass currentReadingListData
                  documentSimilaritiesData={documentSimilaritiesData}
                />
              </div>
            </div>
          </div>
        )}
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
