// library.js
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife, searchItems, toggleSort, toggleReadFilter, toggleFilterType } from "../functionality.js"; // Import sort and filter functions
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library
// =====================
function Library() {
  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [filteredData, setFilteredData] = useState([]); // Renamed from currentReadingListData and Initialize as empty array
  const [readingListData, setReadingListData] = useState([]); // State to hold fetched data
  const [documentSimilaritiesData, setDocumentSimilaritiesData] =
    useState(null); // NEW STATE VARIABLE for similarities data
  const [searchQuery, setSearchQuery] = useState(""); // State for search query

  // --- Helper Functions ---
  const toggleTheme = () => {
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // --- Effects ---
  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    runGameOfLife("gameOfLife");
  }, []);

  useEffect(() => {
    try {
      const initialReadingListData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));

      setReadingListData(initialReadingListData);
      setFilteredData(initialReadingListData); // Initialize filteredData with all data
      setDocumentSimilaritiesData(similaritiesRawData);
    } catch (error) {
      console.error("Error importing reading list data:", error);
    }
  }, []);

  const performSearch = (query, data) => {
    if (!query) return data;
    return data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    const filteredDocs = performSearch(query, readingListData);
    setFilteredData(filteredDocs);
  };

  const handleSort = useCallback((event, sortBy, order) => { // Accept event
    const sortedData = toggleSort(event.target, sortBy, order, filteredData); // Pass event.target and filteredData
    if (Array.isArray(sortedData)) { // Check if sortedData is an array
      setFilteredData([...sortedData]); // Update filteredData with sorted data
    } else {
      console.error("toggleSort did not return an array:", sortedData);
      setFilteredData([]); // Fallback to empty array or handle as needed
    }
  }, [filteredData]);

  const handleReadFilter = useCallback((event) => { // Accept event
    const filtered = toggleReadFilter(event.target, filteredData); // Pass event.target and filteredData
    if (Array.isArray(filtered)) { // Check if filtered is an array
      setFilteredData([...filtered]); // Update filteredData with filtered data
    } else {
      console.error("toggleReadFilter did not return an array:", filtered);
      setFilteredData([]); // Fallback to empty array or handle as needed
    }
  }, [filteredData]);

  const handleTypeFilter = useCallback((event, filterType) => { // Accept event
    const filtered = toggleFilterType(event.target, filterType, filteredData); // Pass event.target and filteredData
    if (Array.isArray(filtered)) { // Check if filtered is an array
      setFilteredData([...filtered]); // Update filteredData with filtered data
    } else {
      console.error("toggleFilterType did not return an array:", filtered);
      setFilteredData([]); // Fallback to empty array or handle as needed
    }
  }, [filteredData]);


  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = ({
    width,
    height,
    readingListData: docs, // Renamed prop to docs for clarity within the component
    documentSimilaritiesData,
  }) => {
    // Accept documentSimilaritiesData prop
    // Expect readingListData as prop
    // Accept readingListData as prop
    const containerRef = useRef(null);
    const [error, setError] = useState(null);
    const [isSmallScreen, setIsSmallScreen] = useState(false);
    const svgRef = useRef(null);
    const simulationRef = useRef(null);
    const nodeTitleGroupRef = useRef(null); // Ref to hold the node title group

    const checkScreenSize = () => {
      const smallScreen = window.innerWidth <= 768;
      setIsSmallScreen(smallScreen);
    };

    const debouncedCheckScreenSize = debounce(checkScreenSize, 100);

    useEffect(() => {
      checkScreenSize();
      window.addEventListener("resize", debouncedCheckScreenSize);
      return () => {
        window.removeEventListener("resize", debouncedCheckScreenSize);
      };
    }, []);

    useEffect(() => {
      if (!isSmallScreen && docs.length > 0) {
        initializeGraph();
      }
    }, [
      isSmallScreen,
      docs, // Use docs prop here
      width,
      height,
      documentSimilaritiesData,
    ]);


    const cleanupGraph = useCallback(() => {
      if (simulationRef.current) {
        simulationRef.current.stop();
        simulationRef.current = null;
      }
      if (containerRef.current) {
        const container = d3.select(containerRef.current);
        container.selectAll("*").remove();
      }
    }, []);

    const initializeGraph = async () => {
      console.log("initializeGraph function is being called");
      if (!containerRef.current) {
        console.log("Exiting early: containerRef.current is null");
        return;
      }
      if (!documentSimilaritiesData) {
        // Check if documentSimilaritiesData is loaded
        console.log(
          "Exiting early: documentSimilaritiesData is not yet loaded"
        );
        return;
      }
      cleanupGraph();

      const container = d3.select(containerRef.current);
      const svg = container
        .append("svg")
        .attr("width", width)
        .attr("height", height)
        .attr("className", "rounded-lg");

      svgRef.current = svg;

      svg
        .append("rect")
        .attr("width", "100%")
        .attr("height", "100%")
        .attr("fill", "var(--graph-background)");

      if (!docs.length) { // Use docs prop here
        const textGroup = svg
          .append("g")
          .attr("transform", `translate(${width / 2}, ${height / 2})`);

        textGroup
          .append("text")
          .attr("text-anchor", "middle")
          .attr("fill", "var(--text-color)")
          .text("No documents to display");

        return;
      }

      const initialTranslateX = width / 3 - 25;
      const initialTranslateY = height / 4 + 10;
      const initialScale = 0.45;

      const g = svg.append("g");

      const zoom = d3
        .zoom()
        .scaleExtent([initialScale, 3])
        .translateExtent([
          [0, 0],
          [width, height],
        ])
        .on("zoom", (event) => {
          g.attr("transform", event.transform);
        });

      svg.call(
        zoom.transform,
        d3.zoomIdentity
          .translate(initialTranslateX, initialTranslateY)
          .scale(initialScale)
      );

      svg.call(zoom);

      // Create a mapping from visible doc original indices to new node indices
      const visibleDocIndicesMap = {};
      docs.forEach((doc, index) => { // Use docs prop here
        visibleDocIndicesMap[doc.originalIndex] = index;
      });
      console.log("visibleDocIndicesMap:", visibleDocIndicesMap); // ADDED LOG

      const nodes = docs.map((doc, i) => ({ // Use docs prop here
        id: String(i), // Use new index as node ID
        title: doc.title,
        fullText: doc.description,
        tags: doc.tags,
        readTime: doc.readTime,
        releaseDate: doc.releaseDate,
        isRead: doc.isRead,
      }));

      // Directly use imported documentSimilaritiesData and remap indices
      let links = documentSimilaritiesData // Use prop here
        .filter((link) => {
          return (
            link.source in visibleDocIndicesMap &&
            link.target in visibleDocIndicesMap
          );
        })
        .map((link) => ({
          ...link,
          source: String(visibleDocIndicesMap[link.source]), // Remap source index
          target: String(visibleDocIndicesMap[link.target]), // Remap target index
        }));

      console.log("Filtered Links:", links); // ADDED LOG

      if (!links || links.length === 0) {
        console.log(
          "No pre-calculated similarities found for visible documents in document_similarities.json."
        );
        links = []; // Ensure links is at least an empty array for simulation initialization
      } else {
        console.log(
          "Loaded pre-calculated similarities from document_similarities.json"
        );
      }

      initializeSimulation(
        nodes,
        links,
        width,
        height,
        g,
        svg,
        docs.map((doc) => doc.readTime) // Pass readTimes array, use docs prop here
      );
    };

    const initializeSimulation = (
      nodes,
      links,
      width,
      height,
      g,
      svg,
      readTimes
    ) => {
      nodes.forEach((node) => {
        node.x = width / 2 + (Math.random() - 0.5) * 40;
        node.y = height / 2 + (Math.random() - 0.5) * 40;
      });

      simulationRef.current = d3
        .forceSimulation()
        .nodes(nodes)
        .force(
          "link",
          d3
            .forceLink(links)
            .id((d) => d.id)
            .distance(50)
            .strength(0.5) // Link strength - kept at 0.5
        )
        .force("charge", d3.forceManyBody().strength(-300))
        .force("center", d3.forceCenter(width / 2, height / 2))
        .force("x", d3.forceX(width / 2).strength(0.1))
        .force("y", d3.forceY(height / 2).strength(0.1))
        .force(
          "collision",
          d3
            .forceCollide()
            .strength(0.1)
            .radius(
              (d) =>
                normalizeReadTime(
                  d.readTime,
                  Math.min(...readTimes),
                  Math.max(...readTimes)
                ) + 10
            )
        )
        .force("boundary", forceBoundary(width, height, nodes))
        .alpha(0.3)
        .velocityDecay(0.4) // Increased velocity decay to 0.4
        .alphaDecay(0.05)
        .alphaMin(0.001);

      const link = g
        .append("g")
        .selectAll("line")
        .data(links)
        .join("line")
        .attr("stroke", "var(--edge-color)")
        .attr("stroke-width", (d) => d.width)
        .attr("stroke-opacity", "var(--edge-opacity)");

      const nodeGroup = g.append("g").attr("className", "nodes");
      const node = nodeGroup
        .selectAll("g")
        .data(nodes)
        .join("g")
        .attr("transform", (d) => `translate(${d.x},${d.y})`)
        .call(
          d3
            .drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended)
        );

      node
        .append("circle")
        .attr("r", (d) =>
          normalizeReadTime(
            d.readTime,
            Math.min(...readTimes),
            Math.max(...readTimes)
          )
        )
        .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
        .attr("stroke", "var(--node-stroke-color)")
        .on("mouseover", function (event, d) {
          d3.select(this).attr("fill", "#8ACE00");
          link
            .attr("stroke", (l) =>
              l.source.id === d.id || l.target.id === d.id
                ? "#8ACE00"
                : "var(--edge-color)"
            )
            .attr("stroke-width", (l) =>
              l.source.id === d.id || l.target.id === d.id ? 3 : l.width
            );

          // Create node title group and store reference
          const titleGroup = nodeGroup
            .append("g")
            .attr("className", "node-title-group")
            .attr("transform", `translate(${d.x},${d.y})`);
          nodeTitleGroupRef.current = titleGroup; // Store the reference

          titleGroup
            .append("text")
            .attr("className", "node-title")
            .attr("x", 10)
            .attr("y", 5)
            .attr("fill", "var(--text-color)")
            .text(d.title);
        })
        .on("mouseout", function (event, d) {
          d3.select(this).attr("fill", (d) =>
            d.isRead ? "#4a9eff" : "var(--node-color)"
          );
          link
            .attr("stroke", "var(--edge-color)")
            .attr("stroke-width", (l) => l.width);
          // Remove the specific node title group using the stored reference
          if (nodeTitleGroupRef.current) {
            nodeTitleGroupRef.current.remove();
            nodeTitleGroupRef.current = null; // Clear the reference
          }
        });

      simulationRef.current.on("tick", () => {
        link
          .attr("x1", (d) => boundPosition(d.source.x, width, -200))
          .attr("y1", (d) => boundPosition(d.source.y, height, -200))
          .attr("x2", (d) => boundPosition(d.target.x, width, -200))
          .attr("y2", (d) => boundPosition(d.target.y, height, -200));

        node.attr("transform", (d) => {
          const x = boundPosition(d.x, width, -200);
          const y = boundPosition(d.y, height, -200);
          return `translate(${x},${y})`;
        });
      });
    };

    const dragstarted = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      d.fx = d.x;
      d.fy = d.y;

      // Increase link strength during drag start
      simulationRef.current.force("link").strength(0.8); // Increased strength
      simulationRef.current.alphaTarget(0.3).restart(); // Reheat simulation for stronger effect
    };

    const dragged = (event, d) => {
      simulationRef.current.alphaTarget(0.01);
      d.fx = boundPosition(event.x, width, -200);
      d.fy = boundPosition(event.y, height, -200);
    };

    const dragended = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      d.fx = null;
      d.fy = null;
    };

    const forceBoundary = (width, height, nodes) => {
      // Add nodes as argument
      const padding = 4;
      const xMin = padding;
      const xMax = width - padding;
      const yMin = padding;
      const yMax = height - padding;
      const strength = 0.05; // Adjust strength as needed

      function force(alpha) {
        nodes.forEach((node) => {
          // Use the nodes argument here
          if (node.x < xMin) {
            node.vx += (xMin - node.x) * strength * alpha;
          }
          if (node.x > xMax) {
            node.vx += (xMax - node.x) * strength * alpha;
          }
          if (node.y < yMin) {
            node.vy += (yMin - node.y) * strength * alpha;
          }
          if (node.y > yMax) {
            node.vy += (yMax - node.y) * strength * alpha;
          }
        });
      }

      return force;
    };

    const boundPosition = (position, dimension, padding) => {
      return Math.max(padding, Math.min(dimension - padding, position));
    };

    const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
      const minRadius = 5;
      const maxRadius = 20;
      if (maxReadTime === minReadTime) return (minRadius + maxRadius) / 2; // Handle case where min and max are the same to avoid division by zero.
      const logMin = Math.log(minReadTime + 1);
      const logMax = Math.log(maxReadTime + 1);
      const logReadTime = Math.log(readTime + 1);
      return (
        ((logReadTime - logMin) / (logMax - logMin)) * (maxRadius - minRadius) +
        minRadius
      );
    };

    if (isSmallScreen) {
      return null;
    }

    if (error) {
      return (
        <div
          className="flex items-center justify-center h-32 rounded-lg"
          style={{
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="text-red-600">{error}</p>
        </div>
      );
    }

    return <div ref={containerRef} className="w-full h-full" />;
  };

  // --- JSX Markup ---
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              value={searchQuery}
              onInput={handleSearchInput}
            />
            <button onClick={() => searchItems(filteredData)}> {/* Use filteredData for searchItems if needed */}
              Search {/* Consider if this button is still needed, search is already onInput */}
            </button>
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) => handleSort(event, "release", "desc")}
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) => handleSort(event, "time", "desc")}
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={handleReadFilter}
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) => handleTypeFilter(event, "YT")}
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) => handleTypeFilter(event, "Arxiv")}
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) => handleTypeFilter(event, "Site")}
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list">
            {filteredData.map((item, index) => (
              <li key={index} style={{ display: item.display !== false ? '' : 'none' }}>
                <article className="reading-item">
                  <div className="item-header">
                    <div className="item-title">
                      <a href={item.url} target="_blank" rel="noopener noreferrer">
                        <strong>"{item.title}"</strong>
                      </a>
                    </div>
                    <span className="item-source">{item.source}</span>
                  </div>
                  <div className="item-details">
                    <span className="item-read-time">Time to read: {item.readTime} minutes</span>
                    <span className="item-release-date">Released: {item.releaseDate}</span>
                  </div>
                  <div className="item-tags">
                    {item.tags && item.tags.map((tag, tagIndex) => (
                      <span key={tagIndex} className="tag">{tag}</span>
                    ))}
                  </div>
                  <p className="item-description">{item.description}</p>
                </article>
              </li>
            ))}
          </ul>
        </div>
        <div className="graph-container">
          <div id="root" className="graph-inner">
            <div className="space-y-6">
              <DocumentGraph
                width={400}
                height={400}
                readingListData={filteredData} // Pass filteredData to graph
                documentSimilaritiesData={documentSimilaritiesData} // Pass documentSimilaritiesData to graph
              />
            </div>
          </div>
        </div>
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
