// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife, searchItems } from "../functionality.js"; // searchItems is not used anymore, but keeping import for now
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library
// =====================
function Library() {
  console.log("Library Component RENDER START"); // Component render log

  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  console.log("useState setTheme initialized:", theme);
  const scrollToTopButtonRef = useRef(null);
  console.log("useRef scrollToTopButtonRef initialized:", scrollToTopButtonRef);
  const [currentReadingListData, setCurrentReadingListData] = useState([]); // Initialize as empty array
  console.log(
    "useState currentReadingListData initialized:",
    currentReadingListData
  );
  const [readingListData, setReadingListData] = useState([]); // State to hold fetched data
  console.log("useState readingListData initialized:", readingListData);
  const [documentSimilaritiesData, setDocumentSimilaritiesData] =
    useState(null); // NEW STATE VARIABLE for similarities data
  console.log(
    "useState documentSimilaritiesData initialized:",
    documentSimilaritiesData
  );
  const [filteredDocs, setFilteredDocs] = useState([]); // Not used, can remove if confirmed
  console.log("useState filteredDocs initialized:", filteredDocs);

  console.log("Library State Initialized:", {
    theme,
    currentReadingListData,
    readingListData,
    documentSimilaritiesData,
    filteredDocs,
  });

  // --- Helper Functions ---
  const toggleTheme = () => {
    console.log("toggleTheme called");
    setTheme((current) => {
      console.log("toggleTheme - setTheme callback - current theme:", current);
      const newTheme = current === "dark" ? "light" : "dark";
      console.log("toggleTheme - setTheme callback - new theme:", newTheme);
      return newTheme;
    });
    console.log("toggleTheme finished");
  };

  const scrollToTop = () => {
    console.log("scrollToTop called");
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
    console.log("scrollToTop finished");
  };

  // --- Effects ---
  useEffect(() => {
    console.log("Library useEffect - Theme update - START - theme:", theme);
    document.body.setAttribute("data-theme", theme);
    console.log(
      "Library useEffect - Theme update - data-theme attribute set to:",
      theme
    );
    console.log("Library useEffect - Theme update - COMPLETE");
  }, [theme]);

  useEffect(() => {
    console.log("Library useEffect - GameOfLife - START");
    console.log(
      "Library useEffect - GameOfLife - Calling runGameOfLife('gameOfLife')"
    );
    runGameOfLife("gameOfLife");
    console.log(
      "Library useEffect - GameOfLife - runGameOfLife execution finished"
    );
    console.log("Library useEffect - GameOfLife - COMPLETE");
  }, []);

  useEffect(() => {
    console.log("Library useEffect - Loading reading list data - START");
    try {
      console.log("Library useEffect - Loading reading list data - TRY block");
