      clearTimeout(timeout);
      console.log("debounce inner function - clearTimeout called");
      timeout = setTimeout(() => {
        console.log(
          "debounce inner function - setTimeout callback - executing func:",
          func
        );
        func.apply(this, args);
        console.log(
          "debounce inner function - setTimeout callback - func.apply finished"
        );
      }, wait);
      console.log("debounce inner function - setTimeout set");
