    const [error, setError] = useState(null);
    console.log("DocumentGraph useState error initialized:", error);
    const [visibleDocs, setVisibleDocs] = useState([]);
    console.log("DocumentGraph useState visibleDocs initialized:", visibleDocs);
    const [isSmallScreen, setIsSmallScreen] = useState(false);
    console.log(
      "DocumentGraph useState isSmallScreen initialized:",
      isSmallScreen
    );
    const svgRef = useRef(null);
    console.log("DocumentGraph useRef svgRef initialized:", svgRef);
    const simulationRef = useRef(null);
    console.log(
      "DocumentGraph useRef simulationRef initialized:",
