    );

    console.log("DocumentGraph State Initialized:", {
      isSmallScreen,
      visibleDocs,
      error,
    });

    const checkScreenSize = () => {
      console.log("DocumentGraph checkScreenSize called");
      const smallScreen = window.innerWidth <= 768;
      console.log(
        "DocumentGraph checkScreenSize - window.innerWidth:",
        window.innerWidth,
        ", smallScreen:",
