      );
      console.log("DocumentGraph checkScreenSize finished");
    };

    const debouncedCheckScreenSize = debounce(checkScreenSize, 100);
    console.log(
      "DocumentGraph debouncedCheckScreenSize created using debounce"
    );

    useEffect(() => {
      console.log(
        "DocumentGraph useEffect - Screen Size Check - START - Initial"
      );
      checkScreenSize();
      console.log(
        "DocumentGraph useEffect - Screen Size Check - Initial checkScreenSize() called"
      );
      window.addEventListener("resize", debouncedCheckScreenSize);
      console.log(
        "DocumentGraph useEffect - Screen Size Check - resize event listener added"
      );
      return () => {
        window.removeEventListener("resize", debouncedCheckScreenSize);
        console.log(
          "DocumentGraph useEffect - Screen Size Check - Cleanup - resize event listener removed"
        );
        console.log(
          "DocumentGraph useEffect - Screen Size Check - Cleanup - COMPLETE"
        );
      };
    }, []);

    useEffect(() => {
      console.log(
        "DocumentGraph useEffect - Visibility Change Listener - START"
      );
      const handleVisibilityChange = () => {
        console.log("DocumentGraph useEffect - handleVisibilityChange called");
        const currentVisibleDocs = getVisibleDocuments();
        console.log(
          "DocumentGraph useEffect - handleVisibilityChange - getVisibleDocuments returned:",
