        );
        setVisibleDocs(currentVisibleDocs);
        console.log(
          "DocumentGraph useEffect - handleVisibilityChange - setVisibleDocs called"
        );
        console.log(
          "DocumentGraph useEffect - handleVisibilityChange finished"
        );
      };

      const throttledHandleVisibilityChange = debounce(
        handleVisibilityChange,
