    }, [getVisibleDocuments, debounce]); // Added debounce as a dependency. Although debounce itself doesn't change, the function it returns does if debounce implementation changes. In practice, it's probably not necessary but good practice.

    useEffect(() => {
      console.log(
        "DocumentGraph useEffect - Initialize Graph or Cleanup - START - isSmallScreen:",
        isSmallScreen,
        ", visibleDocs.length:",
        visibleDocs.length,
        ", readingListData.length:",
        readingListData?.length,
        ", documentSimilaritiesData:",
        documentSimilaritiesData
      );

      // ** ADDED LOGGING HERE TO CHECK PROPS IMMEDIATELY **
      console.log("DocumentGraph useEffect - PROP VALUES at start of effect:", {
        readingListData: readingListData,
        documentSimilaritiesData: documentSimilaritiesData,
      });

      if (!documentSimilaritiesData) {
        console.warn(
          "DocumentGraph useEffect - documentSimilaritiesData is still null/undefined. Graph initialization skipped."
        );
        return;
      }

      if (!isSmallScreen && readingListData && readingListData.length > 0) {
        console.log(
          "DocumentGraph useEffect - Initializing graph - readingListData.length:",
          readingListData.length,
          ", visibleDocs.length:",
          visibleDocs.length
        );
        initializeGraph(visibleDocs);
        console.log("DocumentGraph useEffect - initializeGraph called");
      } else if (
        !isSmallScreen &&
        (!readingListData || readingListData.length === 0)
      ) {
        console.log(
          "DocumentGraph useEffect - Cleaning up graph - readingListData is empty or invalid"
        );
        cleanupGraph();
        console.log("DocumentGraph useEffect - cleanupGraph called");
        if (svgRef.current) {
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - svgRef.current is valid - proceeding to clear SVG"
          );
          const svg = d3.select(svgRef.current);
          svg.selectAll("*").remove();
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - All elements removed from SVG"
          );
          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - Background rectangle appended to SVG"
          );
          const textGroup = svg
            .append("g")
            .attr("transform", `translate(${width / 2}, ${height / 2})`);
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - Text group appended to SVG"
          );

          textGroup
            .append("text")
            .attr("text-anchor", "middle")
            .attr("fill", "var(--text-color)")
            .text("No documents to display");
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - 'No documents' text appended to text group"
          );
        } else {
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - svgRef.current is NULL, skipping SVG clear"
          );
        }
      } else {
        console.log(
          "DocumentGraph useEffect - Skipping graph update (small screen or no data)"
        );
      }
      console.log(
        "DocumentGraph useEffect - Initialize Graph or Cleanup - COMPLETE"
      );
    }, [
      isSmallScreen,
      visibleDocs,
      width,
      height,
      documentSimilaritiesData,
      readingListData,
      initializeGraph, // Include initializeGraph in dependencies as it's used inside this useEffect
      cleanupGraph, // Include cleanupGraph in dependencies as it's used inside this useEffect
    ]);

    const getVisibleDocuments = useCallback(() => {
      console.log(
        "DocumentGraph getVisibleDocuments called - readingListData.length:",
        readingListData?.length
      );
      if (!readingListData || readingListData.length === 0) {
        console.log(
          "DocumentGraph getVisibleDocuments - readingListData is empty or null"
        );
        return [];
      }

      const items = Array.from(document.querySelectorAll("#reading-list li"));
      console.log(
        "DocumentGraph getVisibleDocuments - All list items found in DOM:",
        items.length
      );
      const visibleItems = items.filter(
        (item) => item.style.display !== "none"
      );
      console.log(
        "DocumentGraph getVisibleDocuments - Visible list items after filter:",
        visibleItems.length
      );

      if (visibleItems.length === 0) {
        console.log(
          "DocumentGraph getVisibleDocuments - No visible items, returning empty array."
        );
        return [];
      }

      const mappedVisibleItems = visibleItems
        .map((item) => {
          const titleElement = item.querySelector(".item-title strong");
          if (!titleElement) {
            console.warn(
              "DocumentGraph getVisibleDocuments - No titleElement found for item:",
              item
            );
            return null;
          }
          const titleText = titleElement.textContent;
          const title = titleText.match(/"([^"]+)"/)?.[1];
          if (!title) {
            console.warn(
              "DocumentGraph getVisibleDocuments - No title extracted from titleText:",
              titleText
            );
            return null;
          }
          const foundDoc = readingListData.find((doc) => doc.title === title);
          if (!foundDoc) {
            console.warn(
              "DocumentGraph getVisibleDocuments - No matching doc found in readingListData for title:",
              title,
              ". readingListData might be out of sync?"
            );
          }
          return foundDoc;
        })
        .filter(Boolean);
      console.log(
        "DocumentGraph getVisibleDocuments - Mapped visible items:",
        mappedVisibleItems
      );
      return mappedVisibleItems;
    }, [readingListData]);

    const cleanupGraph = useCallback(() => {
