        );
        console.log(
          "DocumentGraph initializeGraph - containerRef.current:",
          containerRef.current
        );

        if (!containerRef.current) {
          console.warn(
            "DocumentGraph initializeGraph - Exiting early: containerRef.current is NULL (before SVG creation) - CONFIRMING AGAIN"
          );
          return;
        }

        // **IMMEDIATE CHECK - RIGHT BEFORE D3 SELECT**
        console.log(
          "DocumentGraph initializeGraph - containerRef.current just before d3.select:",
