          );
          return;
        }

        if (!documentSimilaritiesData) {
          console.warn(
            "DocumentGraph initializeGraph - Exiting early: documentSimilaritiesData is not yet loaded"
          );
          return;
        }
        if (!readingListData || readingListData.length === 0) {
          console.warn(
            "DocumentGraph initializeGraph - Exiting early: readingListData is not yet loaded or empty"
          );
          return;
        }

        cleanupGraph();
        console.log("DocumentGraph initializeGraph - cleanupGraph called");

        try {
          console.log(
            "DocumentGraph initializeGraph - Starting D3 graph creation..."
          );
          const container = d3.select(containerRef.current); // <--- LINE SUSPECTED TO CAUSE ERROR
          console.log(
            "DocumentGraph initializeGraph - D3 container selected:",
            container
          );

          const svg = container
            .append("svg")
            .attr("width", width)
            .attr("height", height)
            .attr("className", "rounded-lg");

          console.log(
            "DocumentGraph initializeGraph - SVG element created:",
            svg
          );
          svgRef.current = svg;
          console.log("DocumentGraph initializeGraph - svgRef.current set");

          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");
          console.log(
            "DocumentGraph initializeGraph - Background rectangle appended"
          );

          if (!visibleDocsForGraph.length) {
            console.log(
              "DocumentGraph initializeGraph - No visible docs to display (visibleDocsForGraph is empty), showing 'No documents' message"
            );
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);

            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
            return;
          } else {
            console.log(
              "DocumentGraph initializeGraph - visibleDocsForGraph is NOT empty, proceeding to draw graph with",
              visibleDocsForGraph.length,
              "documents"
            );
          }

          const initialTranslateX = width / 3 - 25;
          const initialTranslateY = height / 4 + 10;
          const initialScale = 0.45;

          const g = svg.append("g");
          console.log(
            "DocumentGraph initializeGraph - Group 'g' appended to SVG"
          );

          const zoom = d3
            .zoom()
            .scaleExtent([initialScale, 3])
            .translateExtent([
              [0, 0],
              [width, height],
            ])
            .on("zoom", (event) => {
              console.log(
                "DocumentGraph initializeGraph - zoom event handler - zoom event:",
                event
              );
              g.attr("transform", event.transform);
              console.log(
                "DocumentGraph initializeGraph - zoom event handler - g.attr('transform') called"
              );
            });

          svg.call(
            zoom.transform,
            d3.zoomIdentity
              .translate(initialTranslateX, initialTranslateY)
              .scale(initialScale)
          );
          console.log(
            "DocumentGraph initializeGraph - Zoom transform initialized and applied"
          );

          svg.call(zoom);
          console.log(
            "DocumentGraph initializeGraph - Zoom behavior initialized and applied"
          );

          // Create a mapping from visible doc original indices to new node indices
          const visibleDocIndicesMap = {};
          visibleDocsForGraph.forEach((doc, index) => {
            visibleDocIndicesMap[doc.originalIndex] = index;
          });
          console.log(
            "DocumentGraph initializeGraph - visibleDocIndicesMap:",
            visibleDocIndicesMap
          );

          const nodes = visibleDocsForGraph.map((doc, i) => {
            const node = {
              id: String(i), // Use new index as node ID
              title: doc.title,
              fullText: doc.description,
              tags: doc.tags,
              readTime: doc.readTime,
              releaseDate: doc.releaseDate,
              isRead: doc.isRead,
            };
            console.log("DocumentGraph initializeGraph - Creating node:", node);
            return node;
          });
          console.log(
            "DocumentGraph initializeGraph - Nodes created - nodes.length:",
            nodes.length
          );

          // Directly use imported documentSimilaritiesData and remap indices
          let links = documentSimilaritiesData // Use prop here
            .filter((link) => {
              const shouldIncludeLink =
                link.source in visibleDocIndicesMap &&
                link.target in visibleDocIndicesMap;
              console.log(
                "DocumentGraph initializeGraph - Filtering link:",
                link,
                "shouldIncludeLink:",
                shouldIncludeLink
              );
              return shouldIncludeLink;
            })
            .map((link) => {
              const mappedLink = {
                ...link,
                source: String(visibleDocIndicesMap[link.source]), // Remap source index
                target: String(visibleDocIndicesMap[link.target]), // Remap target index
              };
              console.log(
                "DocumentGraph initializeGraph - Mapping link:",
                mappedLink
              );
              return mappedLink;
            });
          console.log(
            "DocumentGraph initializeGraph - Filtered Links - links.length:",
            links?.length
          );

          if (!links || links.length === 0) {
            console.warn(
              "DocumentGraph initializeGraph - No pre-calculated similarities found for visible documents. Links array will be empty."
            );
            links = []; // Ensure links is at least an empty array for simulation initialization
          } else {
            console.log(
              "DocumentGraph initializeGraph - Loaded pre-calculated similarities from document_similarities.json"
            );
          }

          initializeSimulation(
            nodes,
            links,
            width,
            height,
            g,
            svg,
            visibleDocsForGraph.map((doc) => doc.readTime) // Use argument here
          );
          console.log(
            "DocumentGraph initializeGraph - initializeSimulation called"
          );
        } catch (error) {
          console.error(
            "DocumentGraph initializeGraph - ERROR during graph initialization:",
            error
          );
          setError("Error initializing graph. See console for details.");
          console.log(
            "DocumentGraph initializeGraph - setError called due to error"
          );
        }
        console.log("DocumentGraph initializeGraph function - COMPLETE");
      },
      [cleanupGraph, documentSimilaritiesData, height, readingListData, width]
    );

    const initializeSimulation = (
      nodes,
      links,
      width,
      height,
      g,
      svg,
      readTimes
    ) => {
      console.log(
        "DocumentGraph initializeSimulation called - nodes.length:",
        nodes.length,
        ", links.length:",
        links.length,
        ", readTimes:",
        readTimes
      );
      nodes.forEach((node) => {
        node.x = width / 2 + (Math.random() - 0.5) * 40;
        node.y = height / 2 + (Math.random() - 0.5) * 40;
        console.log(
          "DocumentGraph initializeSimulation - Initial node position set for node:",
          node.title,
          "x:",
          node.x,
          "y:",
          node.y
        );
      });
      console.log(
        "DocumentGraph initializeSimulation - Initial node positions set"
      );

      simulationRef.current = d3
        .forceSimulation()
        .nodes(nodes)
        .force(
          "link",
          d3
            .forceLink(links)
            .id((d) => d.id)
            .distance(50)
            .strength(0.5)
        )
        .force("charge", d3.forceManyBody().strength(-300))
        .force("center", d3.forceCenter(width / 2, height / 2))
        .force("x", d3.forceX(width / 2).strength(0.1))
        .force("y", d3.forceY(height / 2).strength(0.1))
        .force(
          "collision",
          d3
            .forceCollide()
            .strength(0.1)
            .radius(
              (d) =>
                normalizeReadTime(
                  d.readTime,
                  Math.min(...readTimes),
                  Math.max(...readTimes)
                ) + 10
            )
        )
        .force("boundary", forceBoundary(width, height, nodes))
        .alpha(0.3)
        .velocityDecay(0.4)
        .alphaDecay(0.05)
        .alphaMin(0.001);
      console.log(
        "DocumentGraph initializeSimulation - Force simulation created"
      );

      const link = g
        .append("g")
        .selectAll("line")
        .data(links)
        .join("line")
        .attr("stroke", "var(--edge-color)")
        .attr("stroke-width", (d) => d.width)
        .attr("stroke-opacity", "var(--edge-opacity)");
      console.log(
        "DocumentGraph initializeSimulation - Links (edges) appended"
      );

      const nodeGroup = g.append("g").attr("className", "nodes");
      const node = nodeGroup
        .selectAll("g")
        .data(nodes)
        .join("g")
        .attr("transform", (d) => `translate(${d.x},${d.y})`)
        .call(
          d3
            .drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended)
        );
      console.log(
        "DocumentGraph initializeSimulation - Nodes (groups) appended and drag behavior added"
      );

      node
        .append("circle")
        .attr("r", (d) =>
          normalizeReadTime(
            d.readTime,
            Math.min(...readTimes),
            Math.max(...readTimes)
          )
        )
        .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
        .attr("stroke", "var(--node-stroke-color)")
        .on("mouseover", function (event, d) {
          console.log(
            "DocumentGraph initializeSimulation - Node mouseover event - node:",
            d.title
          );
          d3.select(this).attr("fill", "#8ACE00");
          console.log(
            "DocumentGraph initializeSimulation - Node mouseover event - node circle fill changed"
          );
          link
            .attr("stroke", (l) =>
              l.source.id === d.id || l.target.id === d.id
                ? "#8ACE00"
                : "var(--edge-color)"
            )
            .attr("stroke-width", (l) =>
              l.source.id === d.id || l.target.id === d.id ? 3 : l.width
            );
          console.log(
            "DocumentGraph initializeSimulation - Node mouseover event - related links stroke and width changed"
          );

          const titleGroup = nodeGroup
            .append("g")
            .attr("className", "node-title-group")
            .attr("transform", `translate(${d.x},${d.y})`);
          nodeTitleGroupRef.current = titleGroup;
          console.log(
            "DocumentGraph initializeSimulation - Node mouseover event - nodeTitleGroupRef.current set"
          );

          titleGroup
            .append("text")
            .attr("className", "node-title")
            .attr("x", 10)
            .attr("y", 5)
            .attr("fill", "var(--text-color)")
            .text(d.title);
          console.log(
            "DocumentGraph initializeSimulation - Node mouseover event - node title text appended"
          );
        })
        .on("mouseout", function (event, d) {
          console.log(
            "DocumentGraph initializeSimulation - Node mouseout event - node:",
            d.title
          );
          d3.select(this).attr("fill", (d) =>
            d.isRead ? "#4a9eff" : "var(--node-color)"
          );
          console.log(
            "DocumentGraph initializeSimulation - Node mouseout event - node circle fill reset"
          );
          link
            .attr("stroke", "var(--edge-color)")
            .attr("stroke-width", (l) => l.width);
          console.log(
            "DocumentGraph initializeSimulation - Node mouseout event - links stroke and width reset"
          );
          if (nodeTitleGroupRef.current) {
            nodeTitleGroupRef.current.remove();
            nodeTitleGroupRef.current = null;
            console.log(
              "DocumentGraph initializeSimulation - Node mouseout event - nodeTitleGroupRef.current removed and reset to null"
            );
          } else {
            console.log(
              "DocumentGraph initializeSimulation - Node mouseout event - nodeTitleGroupRef.current is null, nothing to remove"
            );
          }
        });
      console.log(
        "DocumentGraph initializeSimulation - Node circles appended and event handlers added"
      );

      simulationRef.current.on("tick", () => {
        link
          .attr("x1", (d) => {
            const x1 = boundPosition(d.source.x, width, -200);
            console.log(
              "DocumentGraph initializeSimulation - tick event - link x1 boundPosition:",
              x1
            );
            return x1;
          })
          .attr("y1", (d) => {
            const y1 = boundPosition(d.source.y, height, -200);
            console.log(
              "DocumentGraph initializeSimulation - tick event - link y1 boundPosition:",
              y1
            );
            return y1;
          })
          .attr("x2", (d) => {
            const x2 = boundPosition(d.target.x, width, -200);
            console.log(
              "DocumentGraph initializeSimulation - tick event - link x2 boundPosition:",
              x2
            );
            return x2;
          })
          .attr("y2", (d) => {
            const y2 = boundPosition(d.target.y, height, -200);
            console.log(
              "DocumentGraph initializeSimulation - tick event - link y2 boundPosition:",
              y2
            );
            return y2;
          });
        console.log(
          "DocumentGraph initializeSimulation - tick event - link attributes updated"
        );

        node.attr("transform", (d) => {
          const x = boundPosition(d.x, width, -200);
          const y = boundPosition(d.y, height, -200);
          const transform = `translate(${x},${y})`;
          console.log(
            "DocumentGraph initializeSimulation - tick event - node transform:",
            transform,
            "node:",
            d.title
          );
          return transform;
        });
        console.log(
          "DocumentGraph initializeSimulation - tick event - node transform attributes updated"
        );
      });
      console.log(
        "DocumentGraph initializeSimulation - Simulation 'tick' handler set"
      );
      console.log("DocumentGraph initializeSimulation - COMPLETE");
    };

    const dragstarted = (event, d) => {
      console.log(
        "DocumentGraph dragstarted called - event:",
        event,
        "node:",
        d.title
      );
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      console.log(
        "DocumentGraph dragstarted - simulationRef.current.alphaTarget(0.01).restart() called"
      );
      d.fx = d.x;
      d.fy = d.y;
      console.log(
        "DocumentGraph dragstarted - d.fx and d.fy set to current x and y"
      );

      simulationRef.current.force("link").strength(0.8);
      console.log(
        "DocumentGraph dragstarted - simulationRef.current.force('link').strength(0.8) called"
      );
      simulationRef.current.alphaTarget(0.3).restart();
      console.log(
        "DocumentGraph dragstarted - simulationRef.current.alphaTarget(0.3).restart() called"
      );
    };

    const dragged = (event, d) => {
      console.log(
        "DocumentGraph dragged called - event:",
        event,
        "node:",
        d.title
      );
      simulationRef.current.alphaTarget(0.01);
      console.log(
        "DocumentGraph dragged - simulationRef.current.alphaTarget(0.01) called"
      );
      d.fx = boundPosition(event.x, width, -200);
      d.fy = boundPosition(event.y, height, -200);
      console.log(
        "DocumentGraph dragged - d.fx and d.fy set to bound event.x and event.y"
      );
    };

    const dragended = (event, d) => {
      console.log(
        "DocumentGraph dragended called - event:",
        event,
        "node:",
        d.title
      );
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      console.log(
        "DocumentGraph dragended - simulationRef.current.alphaTarget(0.01) called"
      );
      d.fx = null;
      d.fy = null;
      console.log("DocumentGraph dragended - d.fx and d.fy reset to null");
    };

    const forceBoundary = (width, height, nodes) => {
      console.log(
        "DocumentGraph forceBoundary created - width:",
        width,
        "height:",
        height,
        "nodes.length:",
        nodes.length
      );
      const padding = 4;
      const xMin = padding;
      const xMax = width - padding;
      const yMin = padding;
      const yMax = height - padding;
      const strength = 0.05;

      function force(alpha) {
        console.log(
          "DocumentGraph forceBoundary - force function called - alpha:",
          alpha
        );
        nodes.forEach((node) => {
          if (node.x < xMin) {
            node.vx += (xMin - node.x) * strength * alpha;
            console.log(
              "DocumentGraph forceBoundary - force function - node:",
              node.title,
              "x < xMin, adjusting vx"
            );
          }
          if (node.x > xMax) {
            node.vx += (xMax - node.x) * strength * alpha;
            console.log(
              "DocumentGraph forceBoundary - force function - node:",
              node.title,
              "x > xMax, adjusting vx"
            );
          }
          if (node.y < yMin) {
            node.vy += (yMin - node.y) * strength * alpha;
            console.log(
              "DocumentGraph forceBoundary - force function - node:",
              node.title,
              "y < yMin, adjusting vy"
            );
          }
          if (node.y > yMax) {
            node.vy += (yMax - node.y) * strength * alpha;
            console.log(
              "DocumentGraph forceBoundary - force function - node:",
              node.title,
              "y > yMax, adjusting vy"
            );
          }
        });
      }

      return force;
    };

    const boundPosition = (position, dimension, padding) => {
      const boundedPosition = Math.max(
        padding,
        Math.min(dimension - padding, position)
      );
      console.log(
        "DocumentGraph boundPosition called - position:",
        position,
        "dimension:",
        dimension,
        "padding:",
        padding,
        "boundedPosition:",
        boundedPosition
      );
      return boundedPosition;
    };

    const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
      console.log(
        "DocumentGraph normalizeReadTime called - readTime:",
        readTime,
        "minReadTime:",
        minReadTime,
        "maxReadTime:",
        maxReadTime
      );
      const minRadius = 5;
      const maxRadius = 20;
      if (maxReadTime === minReadTime) {
        const avgRadius = (minRadius + maxRadius) / 2;
        console.log(
          "DocumentGraph normalizeReadTime - maxReadTime === minReadTime, returning average radius:",
          avgRadius
        );
        return avgRadius;
      }
      const logMin = Math.log(minReadTime + 1);
      const logMax = Math.log(maxReadTime + 1);
      const logReadTime = Math.log(readTime + 1);
      const normalizedRadius =
        ((logReadTime - logMin) / (logMax - logMin)) * (maxRadius - minRadius) +
        minRadius;
      console.log(
        "DocumentGraph normalizeReadTime - normalizedRadius:",
        normalizedRadius
      );
      return normalizedRadius;
    };

    if (isSmallScreen) {
      console.log(
        "DocumentGraph Component - isSmallScreen is true, returning null"
      );
      return null;
    }

    if (error) {
      console.log(
        "DocumentGraph Component - error state is true, rendering error message"
      );
      return (
        <div
          className="flex items-center justify-center h-32 rounded-lg"
          style={{
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="text-red-600">{error}</p>
        </div>
      );
    }

    console.log("DocumentGraph Component - Rendering graph container");
    return (
      <div ref={containerRef} className="w-full h-full">
        {error && <p className="text-red-600">{error}</p>}{" "}
        {/* Display error message here if error state is set */}
      </div>
    );
  };

  // --- JSX Markup ---
  console.log("Library Component - JSX Rendering START");
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) => {
                  console.log(
                    "Sort by Release Date button clicked - event:",
                    event
                  );
                  // Assuming toggleSort and other functions are defined elsewhere and working correctly.
                  window.toggleSort(
                    // Accessing them via window to match the original structure, adjust if these are meant to be within Library scope.
                    event.target,
                    "release",
                    "desc",
                    currentReadingListData
                  );
                }}
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) => {
                  console.log(
                    "Sort by Time to Read button clicked - event:",
                    event
                  );
                  window.toggleSort(
                    event.target,
                    "time",
                    "desc",
                    currentReadingListData
                  );
                }}
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) => {
                  console.log("Filter: All button clicked - event:", event);
                  window.toggleReadFilter(event.target, currentReadingListData);
                }}
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) => {
                  console.log("Filter: YT button clicked - event:", event);
                  window.toggleFilterType(
                    event.target,
                    "YT",
                    currentReadingListData
                  );
                }}
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) => {
                  console.log("Filter: Arxiv button clicked - event:", event);
                  window.toggleFilterType(
                    event.target,
                    "Arxiv",
                    currentReadingListData
                  );
                }}
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) => {
                  console.log("Filter: Site button clicked - event:", event);
                  window.toggleFilterType(
                    event.target,
                    "Site",
                    currentReadingListData
                  );
                }}
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list"></ul>
        </div>

        {documentSimilaritiesData &&
          readingListData?.length > 0 && ( // Conditional rendering for DocumentGraph - CHECKING readingListData?.length > 0
            <div className="graph-container">
              <div id="root" className="graph-inner">
                <div className="space-y-6">
                  {readingListData?.length > 0 &&
                    documentSimilaritiesData && ( // Double check inside also
                      <DocumentGraph
                        width={400}
                        height={400}
                        readingListData={currentReadingListData}
                        documentSimilaritiesData={documentSimilaritiesData}
                      />
                    )}
                  {!readingListData?.length > 0 &&
                    documentSimilaritiesData && ( // Fallback message if no data
                      <div
                        className="flex items-center justify-center h-32 rounded-lg"
                        style={{
                          backgroundColor: "var(--graph-background)",
                          color: "var(--text-color)",
                        }}
                      >
                        <p className="text-gray-500">
                          No documents to display in graph.
                        </p>
                      </div>
                    )}
                </div>
              </div>
            </div>
          )}
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
console.log("Library Component RENDER END"); // Component render log
