                  )
                } // Pass readingListData
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) =>
                  toggleSort(
                    event.target,
                    "time",
                    "desc",
                    currentReadingListData
                  )
                } // Pass readingListData
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) =>
                  toggleReadFilter(event.target, currentReadingListData)
                } // Pass readingListData
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) =>
                  toggleFilterType(event.target, "YT", currentReadingListData)
                } // Pass readingListData
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) =>
                  toggleFilterType(
                    event.target,
                    "Arxiv",
                    currentReadingListData
                  )
                } // Pass readingListData
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) =>
                  toggleFilterType(event.target, "Site", currentReadingListData)
                } // Pass readingListData
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list"></ul>
        </div>
        <div className="graph-container">
          <div id="root" className="graph-inner">
            <div className="space-y-6">
              <DocumentGraph
                width={400}
                height={400}
                readingListData={currentReadingListData}
              />{" "}
              {/* Pass readingListData to graph */}
            </div>
          </div>
        </div>
      </div>
    </>
  );
