import React, { useState, useEffect, useRef } from "react";
import "../css/styles.css"; // Assuming styles.css is in the same directory or adjust path
import "../functionality.js"; // Assuming functionality.js is in the same directory or adjust path
import { Routes, Route, Link } from "react-router-dom";
import Posts from "./Posts";
import Library from "./Library";
import IndexPage from "./IndexPage";

function Energy() {
  // State for theme toggle
  const [theme, setTheme] = useState("dark"); // Assuming 'dark' is default from your HTML

