        );

        if (activeLink) {
          activeLink.classList.add("active");

          let parent = activeLink.parentElement;
          while (parent && parent !== sidebarLinks) {
            if (parent.tagName === "LI") {
              const parentLink = parent.querySelector(":scope > a");
              if (parentLink) {
