// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife, searchItems } from "../functionality.js"; // searchItems is not used anymore, but keeping import for now
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library
// =====================
function Library() {
  console.log("Library Component RENDER START");

  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [currentReadingListData, setCurrentReadingListData] = useState([]);
  const [readingListData, setReadingListData] = useState([]);
  const [documentSimilaritiesData, setDocumentSimilaritiesData] =
    useState(null);
  const [filteredDocs, setFilteredDocs] = useState([]);

  console.log("Library State Initialized", {
    theme,
    currentReadingListData,
    readingListData,
    documentSimilaritiesData,
    filteredDocs,
  });

  const toggleTheme = () => {
    console.log("toggleTheme");
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    console.log("scrollToTop");
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  useEffect(() => {
    console.log("Library useEffect Theme update", { theme });
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    console.log("Library useEffect GameOfLife");
    runGameOfLife("gameOfLife");
  }, []);

  useEffect(() => {
    console.log("Library useEffect Loading reading list data");
    try {
      const listData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
      setReadingListData(listData);
      setCurrentReadingListData(listData);
      setDocumentSimilaritiesData(similaritiesRawData);
      console.log("Library useEffect Reading list data loaded", {
        length: listData.length,
        similaritiesLength: similaritiesRawData?.length,
      });
    } catch (error) {
      console.error("Library useEffect Error loading data", error);
    }
  }, []);

  useEffect(() => {
    console.log("Library useEffect initializeReadingListHTML", {
      currentReadingListDataLength: currentReadingListData.length,
    });
    if (currentReadingListData.length > 0) {
      window.initializeReadingListHTML(currentReadingListData);
    } else {
      console.warn(
        "Library useEffect Skipping initializeReadingListHTML due to empty currentReadingListData"
      );
    }
  }, [currentReadingListData]);

  window.documentVisibilityChanged = new Event("documentVisibilityChanged");

  const debounce = (func, wait) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  const performSearch = (query, data) => {
    if (!query) return data;
    const filtered = data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
    console.log("performSearch", {
      query,
      dataLength: data.length,
      filteredLength: filtered.length,
    });
    return filtered;
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    console.log("handleSearchInput", { query });
    const filteredData = performSearch(query, readingListData);
    setCurrentReadingListData(filteredData);
    window.dispatchEvent(window.documentVisibilityChanged);
  };

  const DocumentGraph = ({
    width,
    height,
    readingListData,
    documentSimilaritiesData,
  }) => {
    console.log("DocumentGraph RENDER", {
      readingListDataLength: readingListData?.length,
      documentSimilaritiesData,
    });

    const containerRef = useRef(null);
    const [error, setError] = useState(null);
    const [visibleDocs, setVisibleDocs] = useState([]);
    const [isSmallScreen, setIsSmallScreen] = useState(false);
    const svgRef = useRef(null);
    const simulationRef = useRef(null);
    const nodeTitleGroupRef = useRef(null);

    const checkScreenSize = () => setIsSmallScreen(window.innerWidth <= 768);
    const debouncedCheckScreenSize = debounce(checkScreenSize, 100);

    useEffect(() => {
      checkScreenSize();
      window.addEventListener("resize", debouncedCheckScreenSize);
      return () =>
        window.removeEventListener("resize", debouncedCheckScreenSize);
    }, []);

    useEffect(() => {
      const handleVisibilityChange = () =>
        setVisibleDocs(getVisibleDocuments());
      const throttledHandleVisibilityChange = debounce(
        handleVisibilityChange,
        200
      );
      window.addEventListener(
        "documentVisibilityChanged",
        throttledHandleVisibilityChange
      );
      return () =>
        window.removeEventListener(
          "documentVisibilityChanged",
          throttledHandleVisibilityChange
        );
    }, [getVisibleDocuments]);

    useEffect(() => {
      console.log("DocumentGraph useEffect Initialize Graph or Cleanup", {
        isSmallScreen,
        visibleDocsLength: visibleDocs.length,
        readingListDataLength: readingListData?.length,
        documentSimilaritiesDataExists: !!documentSimilaritiesData,
      });

      if (
        !documentSimilaritiesData ||
        !readingListData ||
        readingListData.length === 0
      ) {
        console.warn(
          "DocumentGraph useEffect Data not ready, skipping graph init"
        );
        return;
      }

      if (!isSmallScreen) {
        console.log("DocumentGraph useEffect Initializing graph");
        initializeGraph(visibleDocs);
      } else {
        console.log("DocumentGraph useEffect Cleaning up graph (small screen)");
        cleanupGraph();
        if (svgRef.current) {
          const svg = d3.select(svgRef.current);
          svg.selectAll("*").remove();
          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");
          const textGroup = svg
            .append("g")
            .attr("transform", `translate(${width / 2}, ${height / 2})`);
          textGroup
            .append("text")
            .attr("text-anchor", "middle")
            .attr("fill", "var(--text-color)")
            .text("No documents to display");
        }
      }
    }, [
      isSmallScreen,
      visibleDocs,
      width,
      height,
      documentSimilaritiesData,
      readingListData,
      initializeGraph,
      cleanupGraph,
    ]);

    const getVisibleDocuments = useCallback(() => {
      if (!readingListData || readingListData.length === 0) return [];
      const items = Array.from(document.querySelectorAll("#reading-list li"));
      const visibleItems = items.filter(
        (item) => item.style.display !== "none"
      );

      return visibleItems
        .map((item) => {
          const titleElement = item.querySelector(".item-title strong");
          const titleText = titleElement?.textContent;
          const title = titleText?.match(/"([^"]+)"/)?.[1];
          return readingListData.find((doc) => doc.title === title);
        })
        .filter(Boolean);
    }, [readingListData]);

    const cleanupGraph = useCallback(() => {
      console.log("DocumentGraph cleanupGraph");
      if (simulationRef.current) {
        simulationRef.current.stop();
        simulationRef.current = null;
      }
      if (containerRef.current) {
        d3.select(containerRef.current).selectAll("*").remove();
      }
    }, []);

    const initializeGraph = useCallback(
      async (visibleDocsForGraph) => {
        console.log("DocumentGraph initializeGraph START", {
          visibleDocsForGraphLength: visibleDocsForGraph.length,
        });

        if (!containerRef.current) {
          console.warn(
            "DocumentGraph initializeGraph containerRef.current is null, exiting"
          );
          return;
        }
        if (!documentSimilaritiesData) {
          console.warn(
            "DocumentGraph initializeGraph documentSimilaritiesData is null, exiting"
          );
          return;
        }
        if (!readingListData || readingListData.length === 0) {
          console.warn(
            "DocumentGraph initializeGraph readingListData is invalid, exiting"
          );
          return;
        }

        cleanupGraph();

        try {
          console.log("DocumentGraph initializeGraph D3 graph creation START");
          const container = d3.select(containerRef.current);
          const svg = container
            .append("svg")
            .attr("width", width)
            .attr("height", height)
            .attr("className", "rounded-lg");
          svgRef.current = svg;

          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");

          if (!visibleDocsForGraph.length) {
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);
            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
            return;
          }

          const initialTranslateX = width / 3 - 25;
          const initialTranslateY = height / 4 + 10;
          const initialScale = 0.45;

          const g = svg.append("g");

          const zoom = d3
            .zoom()
            .scaleExtent([initialScale, 3])
            .translateExtent([
              [0, 0],
              [width, height],
            ])
            .on("zoom", (event) => g.attr("transform", event.transform));

          svg.call(
            zoom.transform,
            d3.zoomIdentity
              .translate(initialTranslateX, initialTranslateY)
              .scale(initialScale)
          );
          svg.call(zoom);

          const visibleDocIndicesMap = {};
          visibleDocsForGraph.forEach((doc, index) => {
            visibleDocIndicesMap[doc.originalIndex] = index;
          });

          const nodes = visibleDocsForGraph.map((doc, i) => ({
            id: String(i),
            title: doc.title,
            fullText: doc.description,
            tags: doc.tags,
            readTime: doc.readTime,
            releaseDate: doc.releaseDate,
            isRead: doc.isRead,
          }));

          let links = documentSimilaritiesData
            .filter(
              (link) =>
                link.source in visibleDocIndicesMap &&
                link.target in visibleDocIndicesMap
            )
            .map((link) => ({
              ...link,
              source: String(visibleDocIndicesMap[link.source]),
              target: String(visibleDocIndicesMap[link.target]),
            }));

          if (!links || links.length === 0) links = [];

          initializeSimulation(
            nodes,
            links,
            width,
            height,
            g,
            svg,
            visibleDocsForGraph.map((doc) => doc.readTime)
          );
          console.log("DocumentGraph initializeGraph Simulation initialized");
        } catch (error) {
          console.error("DocumentGraph initializeGraph ERROR", error);
          setError("Error initializing graph.");
        }
        console.log("DocumentGraph initializeGraph END");
      },
      [cleanupGraph, documentSimilaritiesData, height, readingListData, width]
    );

    const initializeSimulation = (
      nodes,
      links,
      width,
      height,
      g,
      svg,
      readTimes
    ) => {
      simulationRef.current = d3
        .forceSimulation()
        .nodes(nodes)
        .force(
          "link",
          d3
            .forceLink(links)
            .id((d) => d.id)
            .distance(50)
            .strength(0.5)
        )
        .force("charge", d3.forceManyBody().strength(-300))
        .force("center", d3.forceCenter(width / 2, height / 2))
        .force("x", d3.forceX(width / 2).strength(0.1))
        .force("y", d3.forceY(height / 2).strength(0.1))
        .force(
          "collision",
          d3
            .forceCollide()
            .strength(0.1)
            .radius(
              (d) =>
                normalizeReadTime(
                  d.readTime,
                  Math.min(...readTimes),
                  Math.max(...readTimes)
                ) + 10
            )
        )
        .force("boundary", forceBoundary(width, height, nodes))
        .alpha(0.3)
        .velocityDecay(0.4)
        .alphaDecay(0.05)
        .alphaMin(0.001);

      const link = g
        .append("g")
        .selectAll("line")
        .data(links)
        .join("line")
        .attr("stroke", "var(--edge-color)")
        .attr("stroke-width", (d) => d.width)
        .attr("stroke-opacity", "var(--edge-opacity)");

      const nodeGroup = g.append("g").attr("className", "nodes");
      const node = nodeGroup
        .selectAll("g")
        .data(nodes)
        .join("g")
        .attr("transform", (d) => `translate(${d.x},${d.y})`)
        .call(
          d3
            .drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended)
        );

      node
        .append("circle")
        .attr("r", (d) =>
          normalizeReadTime(
            d.readTime,
            Math.min(...readTimes),
            Math.max(...readTimes)
          )
        )
        .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
        .attr("stroke", "var(--node-stroke-color)")
        .on("mouseover", function (event, d) {
          d3.select(this).attr("fill", "#8ACE00");
          link
            .attr("stroke", (l) =>
              l.source.id === d.id || l.target.id === d.id
                ? "#8ACE00"
                : "var(--edge-color)"
            )
            .attr("stroke-width", (l) =>
              l.source.id === d.id || l.target.id === d.id ? 3 : l.width
            );

          const titleGroup = nodeGroup
            .append("g")
            .attr("className", "node-title-group")
            .attr("transform", `translate(${d.x},${d.y})`);
          nodeTitleGroupRef.current = titleGroup;

          titleGroup
            .append("text")
            .attr("className", "node-title")
            .attr("x", 10)
            .attr("y", 5)
            .attr("fill", "var(--text-color)")
            .text(d.title);
        })
        .on("mouseout", function (event, d) {
          d3.select(this).attr("fill", (d) =>
            d.isRead ? "#4a9eff" : "var(--node-color)"
          );
          link
            .attr("stroke", "var(--edge-color)")
            .attr("stroke-width", (l) => l.width);
          if (nodeTitleGroupRef.current) {
            nodeTitleGroupRef.current.remove();
            nodeTitleGroupRef.current = null;
          }
        });

      simulationRef.current.on("tick", () => {
        link
          .attr("x1", (d) => boundPosition(d.source.x, width, -200))
          .attr("y1", (d) => boundPosition(d.source.y, height, -200))
          .attr("x2", (d) => boundPosition(d.target.x, width, -200))
          .attr("y2", (d) => boundPosition(d.target.y, height, -200));

        node.attr(
          "transform",
          (d) =>
            `translate(${boundPosition(d.x, width, -200)},${boundPosition(
              d.y,
              height,
              -200
            )})`
        );
      });
    };

    const dragstarted = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      d.fx = d.x;
      d.fy = d.y;
      simulationRef.current.force("link").strength(0.8);
      simulationRef.current.alphaTarget(0.3).restart();
    };

    const dragged = (event, d) => {
      simulationRef.current.alphaTarget(0.01);
      d.fx = boundPosition(event.x, width, -200);
      d.fy = boundPosition(event.y, height, -200);
    };

    const dragended = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      d.fx = null;
      d.fy = null;
    };

    const forceBoundary = (width, height, nodes) => {
      const padding = 4;
      const xMin = padding;
      const xMax = width - padding;
      const yMin = padding;
      const yMax = height - padding;
      const strength = 0.05;

      function force(alpha) {
        nodes.forEach((node) => {
          if (node.x < xMin) node.vx += (xMin - node.x) * strength * alpha;
          if (node.x > xMax) node.vx += (xMax - node.x) * strength * alpha;
          if (node.y < yMin) node.vy += (yMin - node.y) * strength * alpha;
          if (node.y > yMax) node.vy += (yMax - node.y) * strength * alpha;
        });
      }
      return force;
    };

    const boundPosition = (position, dimension, padding) =>
      Math.max(padding, Math.min(dimension - padding, position));

    const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
      const minRadius = 5;
      const maxRadius = 20;
      if (maxReadTime === minReadTime) return (minRadius + maxRadius) / 2;
      const logMin = Math.log(minReadTime + 1);
      const logMax = Math.log(maxReadTime + 1);
      const logReadTime = Math.log(readTime + 1);
      return (
        ((logReadTime - logMin) / (logMax - logMin)) * (maxRadius - minRadius) +
        minRadius
      );
    };

    if (isSmallScreen) return null;

    if (error) {
      return (
        <div
          className="flex items-center justify-center h-32 rounded-lg"
          style={{
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="text-red-600">{error}</p>
        </div>
      );
    }

    return <div ref={containerRef} className="w-full h-full" />;
  };

  return (
    <>
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) =>
                  window.toggleSort(
                    event.target,
                    "release",
                    "desc",
                    currentReadingListData
                  )
                }
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) =>
                  window.toggleSort(
                    event.target,
                    "time",
                    "desc",
                    currentReadingListData
                  )
                }
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) =>
                  window.toggleReadFilter(event.target, currentReadingListData)
                }
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) =>
                  window.toggleFilterType(
                    event.target,
                    "YT",
                    currentReadingListData
                  )
                }
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) =>
                  window.toggleFilterType(
                    event.target,
                    "Arxiv",
                    currentReadingListData
                  )
                }
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) =>
                  window.toggleFilterType(
                    event.target,
                    "Site",
                    currentReadingListData
                  )
                }
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list"></ul>
        </div>

        {documentSimilaritiesData && readingListData?.length > 0 && (
          <div className="graph-container">
            <div id="root" className="graph-inner">
              <div className="space-y-6">
                {readingListData?.length > 0 && documentSimilaritiesData && (
                  <DocumentGraph
                    width={400}
                    height={400}
                    readingListData={currentReadingListData}
                    documentSimilaritiesData={documentSimilaritiesData}
                  />
                )}
                {!readingListData?.length > 0 && documentSimilaritiesData && (
                  <div
                    className="flex items-center justify-center h-32 rounded-lg"
                    style={{
                      backgroundColor: "var(--graph-background)",
                      color: "var(--text-color)",
                    }}
                  >
                    <p className="text-gray-500">
                      No documents to display in graph.
                    </p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>

      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

