import React, { useState, useEffect, useRef } from 'react';
import "../css/styles.css"; 
import Posts from "./Posts";
import Library from "./Library";
import IndexPage from "./IndexPage";

function MatterMotionArticle() {
  // State for theme toggle
  const [theme, setTheme] = useState('dark'); // Assuming 'dark' is default from your HTML

  // State for TOC visibility
  const [isTocOpen, setIsTocOpen] = useState(false);

  // Ref for citation copy button to manage icons (if you keep the icon switching logic in JS)
  const citationCopyButtonRef = useRef(null);
  const citationCheckIconRef = useRef(null);

  // Function to toggle theme (if you want to manage theme in React state - otherwise CSS-only)
  const toggleTheme = () => {
    setTheme(currentTheme => (currentTheme === 'dark' ? 'light' : 'dark'));
  };

  // Function to toggle TOC visibility
  const toggleTOC = () => {
    setIsTocOpen(!isTocOpen);
  };

  // Function to copy citation (needs to be implemented in React context)
  const copyCitation = (event) => {
    const citationText = `@misc{bradley-energy-2024,
      title={Matter & Motion: The Energy Revolution},
      author={Bradley, Ben},
      year={2024},
      month={jan},
      note={Blog post},
      howpublished={\\url{bbradz.github.com}}
    }`;

    navigator.clipboard
      .writeText(citationText)
      .then(() => {
        if (citationCopyButtonRef.current) {
          // Change the button content to a checkmark
          citationCopyButtonRef.current.innerHTML = `
          <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="#73daca" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round">
            <polyline points="20 6 9 17 4 12" />
          </svg>`;

          // Revert back after 2 seconds
          setTimeout(() => {
            if (citationCopyButtonRef.current) {
              citationCopyButtonRef.current.innerHTML = `
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <rect x="9" y="9" width="13" height="13" rx="2" ry="2"></rect>
                <path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1" />
              </svg>`;
            }
          }, 2000);
        }
      })
      .catch((err) => {
        console.error("Could not copy citation: ", err);
        alert("Failed to copy citation to clipboard.");
      });
  };

  // Function to scroll to top
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth' // for smooth scrolling
    });
  };

  // Function to handle history back (if needed in React - or just use browser back)
  const goBack = () => {
    window.history.back();
  };

  // Effect to set data-theme on body (if you manage theme via React state and CSS variables)
  useEffect(() => {
    document.body.setAttribute('data-theme', theme);
  }, [theme]);

  // Effect to load external scripts (Polyfill and MathJax).
  // It might be better to include these in public/index.html if they are global dependencies.
  // If you need to load them conditionally or per-component, use useEffect like this:
  useEffect(() => {
    const polyfillScript = document.createElement('script');
    polyfillScript.src = "https://polyfill.io/v3/polyfill.min.js?features=es6";
    document.head.appendChild(polyfillScript);

    const mathJaxScript = document.createElement('script');
    mathJaxScript.id = "MathJax-script";
    mathJaxScript.async = true;
    mathJaxScript.src = "https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js";
    document.head.appendChild(mathJaxScript);

    // Cleanup when component unmounts (though these scripts are likely intended to be global)
    return () => {
      document.head.removeChild(polyfillScript);
      document.head.removeChild(mathJaxScript);
    };
  }, []); // Empty dependency array means this effect runs only once after initial render


  return (
    <html lang="en" data-theme={theme}>
      {" "}
      {/* Data-theme set by React state if managed here */}
      <head>
        <meta charSet="UTF-8" /> {/* Corrected attribute name to charSet */}
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        {/* CSS is imported at the top of this file - remove <link> tag */}
        <title>bb.radz aatter & motion</title>
        {/* Polyfill and MathJax scripts are handled via useEffect - remove <script> tags from here */}
      </head>
      <body>
        <header className="header">
          <div className="logo-section">
            <p className="logo">BBradz</p>
            <button
              className="theme-toggle"
              onClick={toggleTheme} // React event handler
              aria-label="Toggle theme"
            >
              {/* Theme toggle icons - SVGs remain the same */}
              <svg
                className="sun-icon"
                viewBox="0 0 24 24"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <circle cx="12" cy="12" r="4"></circle>
                <path d="M12 2v2" /> {/* Self-closing path tags */}
                <path d="M12 20v2" />
                <path d="M4.93 4.93l1.41 1.41" />
                <path d="M17.66 17.66l1.41 1.41" />
                <path d="M2 12h2" />
                <path d="M20 12h2" />
                <path d="M6.34 17.66l-1.41 1.41" />
                <path d="M19.07 4.93l-1.41 1.41" />
              </svg>
              <svg
                className="moon-icon"
                viewBox="0 0 24 24"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M21 12.79A9 9 0 1 1 11.21 3
                     7 7 0 0 0 21 12.79z"
                />{" "}
                {/* Self-closing path tag */}
              </svg>
            </button>
          </div>
          <nav className="nav-links">
            <a href="../posts.html" className="nav-link">
              Posts
            </a>{" "}
            {/* Adjust these hrefs to React routing if you use React Router, or keep as relative if these are still HTML pages */}
            <a href="../library.html" className="nav-link">
              Library
            </a>
            <a href="../index.html" className="nav-link">
              About Me
            </a>
          </nav>
        </header>
        <div className="container">
          <h1 id="title">Matter & Motion</h1>
          <div className="header-content">
            <div className="header-left">
              <div className="metadata">
                Ben Bradley, Feb 21st, 2024 • 9 min read (1.7K words)
              </div>
              <div className="tags">
                <span className="tag">Energy</span>
                <span className="tag">Green Tech</span>
                <span className="tag">Sustainability</span>
              </div>
            </div>
            <button onClick={goBack} className="back-link">
              Back
            </button>{" "}
            {/* React event handler */}
          </div>

          <div className="toc-container" onClick={toggleTOC}>
            <div className="toc-header">
              <span>▶</span>
              <span>
                <b>Table of Contents</b>
              </span>
            </div>
            <div
              className="toc-content"
              id="toc"
              style={{ display: isTocOpen ? "block" : "none" }}
            >
              {" "}
              {/* Conditional display using state */}
              <ol>
                <li>
                  <a href="#A">The Benefits of Scale 📈</a>
                </li>
                <li>
                  <a href="#B">Bright times ahead ☀️</a>
                </li>
                <li>
                  <a href="#C">
                    Wright’s Law in other parts of the Energy Revolution
                  </a>
                </li>
                <li>
                  <a href="#D">Nuclear 🧩</a>
                </li>
                <li>
                  <a href="#E">In Conclusion</a>
                </li>
                <li>
                  <a href="#F">Sources</a>
                </li>
              </ol>
            </div>
          </div>

          <div className="article-content">
            <div className="centered-item-holder">
              <img
                src="/assets/pics/matter_and_motion.gif" // Updated image path from public/assets
                style={{
                  maxWidth: "960px",
                  maxHeight: "550px",
                  width: "600px",
                  height: "400px",
                }}
                alt=""
              />
            </div>

            <p>Look around you, actually look.</p>
            <p>
              One simple question: how much work do you think it took to make
              everything within arms reach of you?
            </p>
            <p>
              From city-sized semiconductor fabs to GPT gpu clusters with more
              compute power than global powers its impossible to imagine the
              scope of energy needed to build and run the factories, machines,
              transportation, information distribution system, and (lest we
              forget—) workers that bring you the products you use and enjoy
              every day. The modern industrial complex sprawls across oceans and
              continents taking every form economically justifiable but, at the
              end of the day, with enough abstraction it can be boiled down into
              2 distinct ingredients: material to be molded and energy to do the
              molding.
            </p>
            {/* ... rest of your article content - paragraphs, headings, lists, images, etc. */}
