            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
            <button onClick={() => searchItems(filteredData)}>
              {" "}
              {/* Use filteredData for searchItems */}
              Search
            </button>
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) =>
                  toggleSort(
                    event.target,
                    "release",
                    "desc",
                    readingListData, // Pass readingListData for sorting, update filteredData
                    setFilteredData
                  )
                }
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) =>
                  toggleSort(
                    event.target,
                    "time",
                    "desc",
                    readingListData, // Pass readingListData for sorting, update filteredData
                    setFilteredData
                  )
                }
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={
                  (event) =>
                    toggleReadFilter(
                      event.target,
                      readingListData,
                      setFilteredData
                    ) // Pass readingListData for filtering, update filteredData
                }
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={
                  (event) =>
                    toggleFilterType(
                      event.target,
                      "YT",
                      readingListData,
                      setFilteredData
                    ) // Pass readingListData for filtering, update filteredData
                }
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) =>
                  toggleFilterType(
                    event.target,
                    "Arxiv",
                    readingListData, // Pass readingListData for filtering, update filteredData
                    setFilteredData
                  )
                }
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={
                  (event) =>
                    toggleFilterType(
                      event.target,
                      "Site",
                      readingListData,
                      setFilteredData
                    ) // Pass readingListData for filtering, update filteredData
                }
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list">
            {filteredData.map(
              (
                item,
                index // Render list using filteredData - KEY CHANGE
              ) => (
                <li
                  key={index}
                  className="item"
                  style={{ display: "list-item" }}
                >
                  <div className="item-header">
                    <div className="item-title">
                      <strong>"{item.title}"</strong>
                    </div>
                  </div>
                  <div className="item-details">
                    <span className="item-type">{item.type}</span>
                    <span className="item-read-time">
                      {item.readTime} minutes
                    </span>
                    <span className="item-release-date">
                      Released: {item.releaseDate}
                    </span>
                    <span className="item-tags">
                      Tags: {item.tags.join(", ")}
                    </span>
                  </div>
                  <div className="item-description">
                    <p>{item.description}</p>
                  </div>
                  <div className="item-actions">
                    <a
                      href={item.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Read more
                    </a>
                  </div>
                </li>
              )
            )}
          </ul>
        </div>
        <div className="graph-container">
          <div id="root" className="graph-inner">
            <div className="space-y-6">
              <DocumentGraph
                width={400}
                height={400}
                readingListData={filteredData} // Pass filteredData to graph - KEY CHANGE
                documentSimilaritiesData={documentSimilaritiesData}
              />
            </div>
          </div>
        </div>
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
