      releaseDate: "October 28, 2024",
      isRead: true,
      type: "Site",
      content:
        "Modern Monetary Theory Explaining the principles and implications of Modern Monetary Theory. Economics Finance Monetary Policy",
    },
    {
      title: "Renewable Energy Revolution",
      description:
        "Analyzing the progress and future of renewable energy technologies.",
      tags: ["Renewable Energy", "Technology", "Sustainability"],
      readTime: 22,
      releaseDate: "March 10, 2024",
      isRead: false,
      type: "Site",
      content:
        "Renewable Energy Revolution Analyzing the progress and future of renewable energy technologies. Renewable Energy Technology Sustainability",
    },
  ];
  window.readingListData = readingListData; // Make it globally available for the graph component

  return (
    <>
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <header className="header">
        <div className="logo-section">
