// Library.js
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife } from "../functionality.js";
// ↑ Example import: adjust if your functionality is different
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// DocumentGraph Component
// =====================
function DocumentGraph({ width, height, docs, documentSimilaritiesData }) {
  const containerRef = useRef(null);
  const svgRef = useRef(null);
  const simulationRef = useRef(null);
  const nodeTitleGroupRef = useRef(null);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const [error, setError] = useState(null);

  // Detect screen size for simple conditional rendering
  useEffect(() => {
    const checkScreenSize = () => {
      setIsSmallScreen(window.innerWidth <= 768);
    };
    checkScreenSize();
    window.addEventListener("resize", checkScreenSize);
    return () => window.removeEventListener("resize", checkScreenSize);
  }, []);

  // Re-initialize the graph whenever docs or size changes
  useEffect(() => {
    if (!isSmallScreen && docs && docs.length > 0) {
      initializeGraph();
    } else {
      cleanupGraph(); // If no docs or small screen, just clear out the SVG
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isSmallScreen, docs, width, height, documentSimilaritiesData]);

  // Remove any existing SVG elements
  const cleanupGraph = () => {
    if (simulationRef.current) {
      simulationRef.current.stop();
      simulationRef.current = null;
    }
    if (containerRef.current) {
      d3.select(containerRef.current).selectAll("*").remove();
    }
  };

  // Main graph initialization
  const initializeGraph = () => {
    cleanupGraph();
    if (!containerRef.current) return;
    if (!documentSimilaritiesData) return;

    const container = d3.select(containerRef.current);
    const svg = container
      .append("svg")
      .attr("width", width)
      .attr("height", height)
      .attr("className", "rounded-lg");
    svgRef.current = svg;

    // Background rectangle
    svg
      .append("rect")
      .attr("width", "100%")
      .attr("height", "100%")
      .attr("fill", "var(--graph-background)");

    // If no docs, display a message
    if (!docs.length) {
      const textGroup = svg
        .append("g")
        .attr("transform", `translate(${width / 2}, ${height / 2})`);
      textGroup
        .append("text")
        .attr("text-anchor", "middle")
        .attr("fill", "var(--text-color)")
        .text("No documents to display");
      return;
    }

    // Set up initial zoom/pan
    const g = svg.append("g");
    const initialTranslateX = width / 3 - 25;
    const initialTranslateY = height / 4 + 10;
    const initialScale = 0.45;

    const zoom = d3
      .zoom()
      .scaleExtent([initialScale, 3])
      .translateExtent([
        [0, 0],
        [width, height],
      ])
      .on("zoom", (event) => {
        g.attr("transform", event.transform);
      });

    // Initialize zoom state
    svg.call(
      zoom.transform,
      d3.zoomIdentity
        .translate(initialTranslateX, initialTranslateY)
        .scale(initialScale)
    );
    svg.call(zoom);

    // Build D3-friendly nodes array
    const nodes = docs.map((doc, i) => ({
      id: String(i),
      title: doc.title,
      fullText: doc.description,
      tags: doc.tags,
      readTime: doc.readTime,
      releaseDate: doc.releaseDate,
      isRead: doc.isRead,
      originalIndex: doc.originalIndex,
    }));

    // Build links from document_similarities.json, but only for these docs
    const docIndexMap = {};
    docs.forEach((doc, i) => {
      docIndexMap[doc.originalIndex] = i;
    });

    let links = documentSimilaritiesData
      .filter(
        (link) => link.source in docIndexMap && link.target in docIndexMap
      )
      .map((link) => ({
        source: String(docIndexMap[link.source]),
        target: String(docIndexMap[link.target]),
        width: link.width || 1,
      }));

    if (!links || links.length === 0) {
      console.log("No pre-calculated similarities for these docs.");
      links = []; // fallback
    }

    initializeSimulation(
      nodes,
      links,
      width,
      height,
      g,
      svg,
      docs.map((d) => d.readTime)
    );
  };

  // Set up force simulation
  const initializeSimulation = (
    nodes,
    links,
    width,
    height,
    g,
    svg,
    readTimes
  ) => {
    // Give them random starting positions near center
    nodes.forEach((node) => {
      node.x = width / 2 + (Math.random() - 0.5) * 40;
      node.y = height / 2 + (Math.random() - 0.5) * 40;
    });

    const linkForce = d3
      .forceLink(links)
      .id((d) => d.id)
      .distance(50)
      .strength(0.5);

    simulationRef.current = d3
      .forceSimulation(nodes)
      .force("link", linkForce)
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .force("x", d3.forceX(width / 2).strength(0.1))
      .force("y", d3.forceY(height / 2).strength(0.1))
      .force(
        "collision",
        d3
          .forceCollide()
          .strength(0.1)
          .radius(
            (d) =>
              normalizeReadTime(
                d.readTime,
                Math.min(...readTimes),
                Math.max(...readTimes)
              ) + 10
          )
      )
      .alpha(0.3)
      .velocityDecay(0.4)
      .alphaDecay(0.05)
      .alphaMin(0.001);

    // edges
    const link = g
      .append("g")
      .selectAll("line")
      .data(links)
      .join("line")
      .attr("stroke", "var(--edge-color)")
      .attr("stroke-width", (d) => d.width || 1)
      .attr("stroke-opacity", "var(--edge-opacity)");

    // nodes
    const nodeGroup = g.append("g").attr("className", "nodes");
    const node = nodeGroup
      .selectAll("g")
      .data(nodes)
      .join("g")
      .call(
        d3
          .drag()
          .on("start", dragstarted)
          .on("drag", dragged)
          .on("end", dragended)
      );

    node
      .append("circle")
      .attr("r", (d) =>
        normalizeReadTime(
          d.readTime,
          Math.min(...readTimes),
          Math.max(...readTimes)
        )
      )
      .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
      .attr("stroke", "var(--node-stroke-color)")
      .on("mouseover", function (event, d) {
        d3.select(this).attr("fill", "#8ACE00");
        link
          .attr("stroke", (l) =>
            l.source.id === d.id || l.target.id === d.id
              ? "#8ACE00"
              : "var(--edge-color)"
          )
          .attr("stroke-width", (l) =>
            l.source.id === d.id || l.target.id === d.id ? 3 : l.width
          );

        // Display a floating label
        const titleGroup = nodeGroup
          .append("g")
          .attr("className", "node-title-group")
          .attr("transform", `translate(${d.x},${d.y})`);
        nodeTitleGroupRef.current = titleGroup;

        titleGroup
          .append("text")
          .attr("className", "node-title")
          .attr("x", 10)
          .attr("y", 5)
          .attr("fill", "var(--text-color)")
          .text(d.title);
      })
      .on("mouseout", function (event, d) {
        d3.select(this).attr("fill", (d) =>
          d.isRead ? "#4a9eff" : "var(--node-color)"
        );
        link
          .attr("stroke", "var(--edge-color)")
          .attr("stroke-width", (l) => l.width);
        if (nodeTitleGroupRef.current) {
          nodeTitleGroupRef.current.remove();
          nodeTitleGroupRef.current = null;
        }
      });

    simulationRef.current.on("tick", () => {
      link
        .attr("x1", (d) => d.source.x)
        .attr("y1", (d) => d.source.y)
        .attr("x2", (d) => d.target.x)
        .attr("y2", (d) => d.target.y);

      node.attr("transform", (d) => `translate(${d.x},${d.y})`);
    });

    // -----------
    // Drag Helpers
    // -----------
    function dragstarted(event, d) {
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      d.fx = d.x;
      d.fy = d.y;
      // Increase link strength during drag
      simulationRef.current.force("link").strength(0.8);
      simulationRef.current.alphaTarget(0.3).restart();
    }

    function dragged(event, d) {
      simulationRef.current.alphaTarget(0.01);
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragended(event, d) {
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      d.fx = null;
      d.fy = null;
    }

    function normalizeReadTime(readTime, minReadTime, maxReadTime) {
      const minRadius = 5;
      const maxRadius = 20;
      if (maxReadTime === minReadTime) {
        return (minRadius + maxRadius) / 2;
      }
      const logMin = Math.log(minReadTime + 1);
      const logMax = Math.log(maxReadTime + 1);
      const logReadTime = Math.log(readTime + 1);
      return (
        ((logReadTime - logMin) / (logMax - logMin)) * (maxRadius - minRadius) +
        minRadius
      );
    }
  };

  if (isSmallScreen) {
    // Decide how you want to display the graph on small screens
    return null;
  }

  if (error) {
    return (
      <div
        className="flex items-center justify-center h-32 rounded-lg"
        style={{
          backgroundColor: "var(--graph-background)",
          color: "var(--text-color)",
        }}
      >
        <p className="text-red-600">{error}</p>
      </div>
    );
  }

  return <div ref={containerRef} className="w-full h-full" />;
}

// =====================
// Library Component
// =====================
function Library() {
  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [readingListData, setReadingListData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [documentSimilaritiesData, setDocumentSimilaritiesData] = useState([]);

  // --- Effects ---
  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    runGameOfLife("gameOfLife");
  }, []);

  useEffect(() => {
    try {
      // Load your initial reading list
      const readingList = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
      setReadingListData(readingList);
      setFilteredData(readingList); // By default, show them all
      setDocumentSimilaritiesData(similaritiesRawData);
    } catch (error) {
      console.error("Error importing reading list data:", error);
    }
  }, []);

  // --- Helper Functions ---
  const toggleTheme = () => {
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // Searching (basic example)
  const handleSearchInput = (e) => {
    const query = e.target.value;
    if (!query) {
      // If no query, reset to full list
      setFilteredData(readingListData);
      return;
    }
    // Otherwise, filter
    const newFiltered = readingListData.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredData(newFiltered);
  };

  // Example sort (descending release date)
  const sortByRelease = () => {
    const sorted = [...filteredData].sort(
      (a, b) => new Date(b.releaseDate) - new Date(a.releaseDate)
    );
    setFilteredData(sorted);
  };

  // Example sort (descending time to read)
  const sortByTime = () => {
    const sorted = [...filteredData].sort((a, b) => b.readTime - a.readTime);
    setFilteredData(sorted);
  };

  // Example filter by "YT" type
  const filterYT = () => {
    const onlyYT = readingListData.filter((d) => d.type === "YT");
    setFilteredData(onlyYT);
  };

  // Clear all filters
  const showAll = () => {
    setFilteredData(readingListData);
  };

  // --- JSX Markup ---
  return (
    <>
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            {/* Sun & Moon Icons */}
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3A7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          {/* Search Bar */}
          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onChange={handleSearchInput}
            />
            <button
              onClick={() => {
                /* You could trigger a manual search here */
              }}
            >
              Search
            </button>
          </div>

          {/* Some example sort/filter buttons */}
          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button onClick={sortByRelease}>
                Sort by Release Date (Desc)
              </button>
              <button onClick={sortByTime}>Sort by Time to Read (Desc)</button>
              <button onClick={showAll}>All Items</button>
              <button onClick={filterYT}>YT Only</button>
            </div>
          </div>

          {/* Render the reading list in React */}
          <ul>
            {filteredData.map((doc, i) => (
              <li key={i}>
                <div className="item-title">
                  <strong>{doc.title}</strong>
                </div>
                <div>Release Date: {doc.releaseDate}</div>
                <div>Time to Read: {doc.readTime} min</div>
                <div>Type: {doc.type}</div>
                {/* More fields as desired */}
              </li>
            ))}
          </ul>
        </div>

        {/* Graph on the right */}
        <div className="graph-container">
          <div className="graph-inner">
            <div className="space-y-6">
              <DocumentGraph
                width={400}
                height={400}
                docs={filteredData} // pass currently visible docs
                documentSimilaritiesData={documentSimilaritiesData}
              />
            </div>
          </div>
        </div>
      </div>

      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
