// library.js - CLEANED VERSION
import React, { useState, useRef, useEffect, useCallback, useMemo } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife } from "../functionality.js";
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Library Component
// =====================
function Library() {
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [readingListDataRaw, setReadingListDataRaw] = useState([]); // Raw data from input.json
  const [currentReadingListData, setCurrentReadingListData] = useState([]); // Data filtered/sorted for display
  const [documentSimilaritiesData, setDocumentSimilaritiesData] = useState(null);

  // --- Theme Toggle ---
  const toggleTheme = useCallback(() => {
    setTheme((currentTheme) => (currentTheme === "dark" ? "light" : "dark"));
  }, []);

  // --- Scroll to Top ---
  const scrollToTop = useCallback(() => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, []);

  // --- Theme Effect ---
  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  // --- Game of Life Effect ---
  useEffect(() => {
    runGameOfLife("gameOfLife");
  }, []);

  // --- Load Reading List Data Effect ---
  useEffect(() => {
    const loadReadingList = async () => {
      try {
        const data = sourcesRawData.map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }));
        setReadingListDataRaw(data);
        setCurrentReadingListData(data);
        setDocumentSimilaritiesData(similaritiesRawData);
      } catch (error) {
        console.error("Error loading reading list data:", error);
      }
    };
    loadReadingList();
  }, []);

  // --- Initialize Reading List HTML Effect ---
  useEffect(() => {
    if (currentReadingListData.length > 0) {
      window.initializeReadingListHTML(currentReadingListData);
    }
  }, [currentReadingListData]);

  const useModel = window.use; // Keeping as is, assuming external dependency
  window.documentVisibilityChanged = new Event("documentVisibilityChanged"); // Keeping as is, assuming external dependency

  // --- Debounce Function ---
  const debounce = useCallback((func, wait) => {
    let timeout;
    return function executedFunction(...args) {
      const later = () => {
        clearTimeout(timeout);
        func(...args);
      };
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
    };
  }, []);

  // --- Search Function ---
  const performSearch = useCallback((query, data) => {
    if (!query) {
      return data;
    }
    const lowerCaseQuery = query.toLowerCase();
    return data.filter((item) =>
      item.title.toLowerCase().includes(lowerCaseQuery)
    );
  }, []);

  // --- Handle Search Input ---
  const handleSearchInput = useCallback(
    (e) => {
      const query = e.target.value;
      const filteredData = performSearch(query, readingListDataRaw);
      setCurrentReadingListData(filteredData);
    },
    [performSearch, readingListDataRaw]
  );

  // --- Sort Reading List Function (exposed to window) ---
  window.sortReadingList = useCallback(
    (sortBy, sortOrder, currentData) => {
      const sortedData = [...currentData];
      sortedData.sort((a, b) => {
        let comparison = 0;
        if (sortBy === "release") {
          comparison = new Date(a.releaseDate) - new Date(b.releaseDate);
        } else if (sortBy === "time") {
          comparison = a.readTime - b.readTime;
        }
        return sortOrder === "asc" ? comparison : comparison * -1;
      });
      setCurrentReadingListData(sortedData);
    },
    [setCurrentReadingListData]
  );

  // --- Filter Reading List Function (exposed to window) ---
  window.filterReadingList = useCallback(
    (filterType, currentData) => {
      let filteredData;
      if (filterType === "all") {
        filteredData = [...currentData];
      } else {
        filteredData = currentData.filter((item) =>
          item.tags.includes(filterType)
        );
      }
      setCurrentReadingListData(filteredData);
    },
    [setCurrentReadingListData]
  );

  // --- Filter Read Status Function (exposed to window) ---
  window.filterReadStatus = useCallback(
    (currentData, onlyUnread) => {
      const filteredData = onlyUnread
        ? currentData.filter((item) => !item.isRead)
        : [...currentData];
      setCurrentReadingListData(filteredData);
    },
    [setCurrentReadingListData]
  );

  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = React.memo(
    ({ width, height, readingListData, documentSimilaritiesData }) => {
      const containerRef = useRef(null);
      const [error, setError] = useState(null);
      const [isSmallScreen, setIsSmallScreen] = useState(false);
      const svgRef = useRef(null);
      const simulationRef = useRef(null);
      const nodeTitleGroupRef = useRef(null);

      const checkScreenSize = useCallback(() => {
        setIsSmallScreen(window.innerWidth <= 768);
      }, []);

      const debouncedCheckScreenSize = useMemo(() => debounce(checkScreenSize, 100), [debounce, checkScreenSize]);

      useEffect(() => {
        checkScreenSize();
        window.addEventListener("resize", debouncedCheckScreenSize);
        return () => {
          window.removeEventListener("resize", debouncedCheckScreenSize);
        };
      }, [debouncedCheckScreenSize, checkScreenSize]);

      useEffect(() => {
        if (!documentSimilaritiesData) {
          return; // Wait for data to load
        }

        if (!isSmallScreen && readingListData && readingListData.length > 0) {
          const currentVisibleDocs = getVisibleDocuments();
          initializeGraph(currentVisibleDocs);
        } else if (!isSmallScreen) {
          cleanupGraph();
          if (svgRef.current) {
            const svg = d3.select(svgRef.current);
            svg.selectAll("*").remove();
            svg
              .append("rect")
              .attr("width", "100%")
              .attr("height", "100%")
              .attr("fill", "var(--graph-background)");
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);

            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
          }
        }
      }, [
        isSmallScreen,
        width,
        height,
        documentSimilaritiesData,
        readingListData,
        getVisibleDocuments, // Ensure getVisibleDocuments is in dependencies
        initializeGraph,     // Ensure initializeGraph is in dependencies
        cleanupGraph,        // Ensure cleanupGraph is in dependencies
      ]);

      const getVisibleDocuments = useCallback(() => {
        if (!readingListData || readingListData.length === 0) {
          return [];
        }

        const items = Array.from(document.querySelectorAll("#reading-list li"));
        const visibleItems = items.filter(
          (item) => item.style.display !== "none"
        );

        if (visibleItems.length === 0) {
          return [];
        }

        return visibleItems
          .map((item) => {
            const titleElement = item.querySelector(".item-title strong");
            if (!titleElement) return null;
            const titleText = titleElement.textContent;
            const title = titleText.match(/"([^"]+)"/)?.[1];
            if (!title) return null;
            return readingListData.find((doc) => doc.title === title);
          })
          .filter(Boolean);
      }, [readingListData]);

      const cleanupGraph = useCallback(() => {
        if (simulationRef.current) {
          simulationRef.current.stop();
          simulationRef.current = null;
        }
        if (containerRef.current) {
          const container = d3.select(containerRef.current);
          container.selectAll("*").remove();
        }
      }, []);

      const initializeGraph = useCallback(
        async (visibleDocsForGraph) => {
          if (!containerRef.current || !documentSimilaritiesData) {
            return; // Exit if ref or data is not ready
          }
          cleanupGraph();

          try {
            const container = d3.select(containerRef.current);
            const svg = container
              .append("svg")
              .attr("width", width)
              .attr("height", height)
              .attr("className", "rounded-lg");

            svgRef.current = svg;

            svg
              .append("rect")
              .attr("width", "100%")
              .attr("height", "100%")
              .attr("fill", "var(--graph-background)");

            if (!visibleDocsForGraph.length) {
              const textGroup = svg
                .append("g")
                .attr("transform", `translate(${width / 2}, ${height / 2})`);

              textGroup
                .append("text")
                .attr("text-anchor", "middle")
                .attr("fill", "var(--text-color)")
                .text("No documents to display");
              return;
            }

            const initialTranslateX = width / 3 - 25;
            const initialTranslateY = height / 4 + 10;
            const initialScale = 0.45;

            const g = svg.append("g");

            const zoom = d3
              .zoom()
              .scaleExtent([initialScale, 3])
              .translateExtent([
                [0, 0],
                [width, height],
              ])
              .on("zoom", (event) => {
                g.attr("transform", event.transform);
              });

            svg.call(
              zoom.transform,
              d3.zoomIdentity
                .translate(initialTranslateX, initialTranslateY)
                .scale(initialScale)
            );
            svg.call(zoom);


            const visibleDocIndicesMap = {};
            visibleDocsForGraph.forEach((doc, index) => {
              visibleDocIndicesMap[doc.originalIndex] = index;
            });

            const nodes = visibleDocsForGraph.map((doc, i) => ({
              id: String(i),
              title: doc.title,
              fullText: doc.description,
              tags: doc.tags,
              readTime: doc.readTime,
              releaseDate: doc.releaseDate,
              isRead: doc.isRead,
            }));

            let links = documentSimilaritiesData
              .filter((link) => {
                return (
                  link.source in visibleDocIndicesMap &&
                  link.target in visibleDocIndicesMap
                );
              })
              .map((link) => ({
                ...link,
                source: String(visibleDocIndicesMap[link.source]),
                target: String(visibleDocIndicesMap[link.target]),
              }));

            if (!links || links.length === 0) {
              links = []; // Handle case with no links explicitly
            }

            initializeSimulation(
              nodes,
              links,
              width,
              height,
              g,
              svg,
              visibleDocsForGraph.map((doc) => doc.readTime)
            );
          } catch (error) {
            console.error("Error initializing graph:", error);
            setError("Error initializing graph.");
          }
        },
        [cleanupGraph, documentSimilaritiesData, height, initializeSimulation, width]
      );


      const initializeSimulation = useCallback(
        (nodes, links, width, height, g, svg, readTimes) => {
          nodes.forEach((node) => {
            node.x = width / 2 + (Math.random() - 0.5) * 40;
            node.y = height / 2 + (Math.random() - 0.5) * 40;
          });

          simulationRef.current = d3
            .forceSimulation()
            .nodes(nodes)
            .force(
              "link",
              d3
                .forceLink(links)
                .id((d) => d.id)
                .distance(50)
                .strength(0.5)
            )
            .force("charge", d3.forceManyBody().strength(-300))
            .force("center", d3.forceCenter(width / 2, height / 2))
            .force("x", d3.forceX(width / 2).strength(0.1))
            .force("y", d3.forceY(height / 2).strength(0.1))
            .force(
              "collision",
              d3
                .forceCollide()
                .strength(0.1)
                .radius(
                  (d) =>
                    normalizeReadTime(
                      d.readTime,
                      Math.min(...readTimes),
                      Math.max(...readTimes)
                    ) + 10
                )
            )
            .force("boundary", forceBoundary(width, height, nodes))
            .alpha(0.3)
            .velocityDecay(0.4)
            .alphaDecay(0.05)
            .alphaMin(0.001);

          const link = g
            .append("g")
            .selectAll("line")
            .data(links)
            .join("line")
            .attr("stroke", "var(--edge-color)")
            .attr("stroke-width", (d) => d.width)
            .attr("stroke-opacity", "var(--edge-opacity)");

          const nodeGroup = g.append("g").attr("className", "nodes");
          const node = nodeGroup
            .selectAll("g")
            .data(nodes)
            .join("g")
            .attr("transform", (d) => `translate(${d.x},${d.y})`)
            .call(
              d3
                .drag()
                .on("start", dragstarted)
                .on("drag", dragged)
                .on("end", dragended)
            );

          node
            .append("circle")
            .attr("r", (d) =>
              normalizeReadTime(
                d.readTime,
                Math.min(...readTimes),
                Math.max(...readTimes)
              )
            )
            .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
            .attr("stroke", "var(--node-stroke-color)")
            .on("mouseover", function (event, d) {
              d3.select(this).attr("fill", "#8ACE00");
              link
                .attr("stroke", (l) =>
                  l.source.id === d.id || l.target.id === d.id
                    ? "#8ACE00"
                    : "var(--edge-color)"
                )
                .attr("stroke-width", (l) =>
                  l.source.id === d.id || l.target.id === d.id ? 3 : l.width
                );

              const titleGroup = nodeGroup
                .append("g")
                .attr("className", "node-title-group")
                .attr("transform", `translate(${d.x},${d.y})`);
              nodeTitleGroupRef.current = titleGroup;

              titleGroup
                .append("text")
                .attr("className", "node-title")
                .attr("x", 10)
                .attr("y", 5)
                .attr("fill", "var(--text-color)")
                .text(d.title);
            })
            .on("mouseout", function (event, d) {
              d3.select(this).attr("fill", (d) =>
                d.isRead ? "#4a9eff" : "var(--node-color)"
              );
              link
                .attr("stroke", "var(--edge-color)")
                .attr("stroke-width", (l) => l.width);
              if (nodeTitleGroupRef.current) {
                nodeTitleGroupRef.current.remove();
                nodeTitleGroupRef.current = null;
              }
            });

          simulationRef.current.on("tick", () => {
            link
              .attr("x1", (d) => boundPosition(d.source.x, width, -200))
              .attr("y1", (d) => boundPosition(d.source.y, height, -200))
              .attr("x2", (d) => boundPosition(d.target.x, width, -200))
              .attr("y2", (d) => boundPosition(d.target.y, height, -200));

            node.attr("transform", (d) => {
              const x = boundPosition(d.x, width, -200);
              const y = boundPosition(d.y, height, -200);
              return `translate(${x},${y})`;
            });
          });
        },
        [boundPosition, dragended, dragged, dragstarted, forceBoundary, normalizeReadTime, width, height]
      );


      const dragstarted = useCallback((event, d) => {
        if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
        d.fx = d.x;
        d.fy = d.y;
        simulationRef.current.force("link").strength(0.8);
        simulationRef.current.alphaTarget(0.3).restart();
      }, [simulationRef]);

      const dragged = useCallback((event, d) => {
        simulationRef.current.alphaTarget(0.01);
        d.fx = boundPosition(event.x, width, -200);
        d.fy = boundPosition(event.y, height, -200);
      }, [boundPosition, simulationRef, width]);

      const dragended = useCallback((event, d) => {
        if (!event.active) simulationRef.current.alphaTarget(0.01);
        d.fx = null;
        d.fy = null;
      }, [simulationRef]);

      const forceBoundary = useCallback((width, height, nodes) => {
        const padding = 4;
        const xMin = padding;
        const xMax = width - padding;
        const yMin = padding;
        const yMax = height - padding;
        const strength = 0.05;

        function force(alpha) {
          nodes.forEach((node) => {
            if (node.x < xMin) {
              node.vx += (xMin - node.x) * strength * alpha;
            }
            if (node.x > xMax) {
              node.vx += (xMax - node.x) * strength * alpha;
            }
            if (node.y < yMin) {
              node.vy += (yMin - node.y) * strength * alpha;
            }
            if (node.y > yMax) {
              node.vy += (yMax - node.y) * strength * alpha;
            }
          });
        }
        return force;
      }, []);

      const boundPosition = useCallback((position, dimension, padding) => {
        return Math.max(padding, Math.min(dimension - padding, position));
      }, []);

      const normalizeReadTime = useCallback((readTime, minReadTime, maxReadTime) => {
        const minRadius = 5;
        const maxRadius = 20;
        if (maxReadTime <= minReadTime) return (minRadius + maxRadius) / 2; // handle edge case to avoid NaN
        const logMin = Math.log(minReadTime + 1);
        const logMax = Math.log(maxReadTime + 1);
        const logReadTime = Math.log(readTime + 1);
        return (
          ((logReadTime - logMin) / (logMax - logMin)) *
            (maxRadius - minRadius) +
          minRadius
        );
      }, []);


      if (isSmallScreen) {
        return null;
      }

      if (error) {
        return (
          <div
            className="flex items-center justify-center h-32 rounded-lg"
            style={{
              backgroundColor: "var(--graph-background)",
              color: "var(--text-color)",
            }}
          >
            <p className="text-red-600">{error}</p>
          </div>
        );
      }

      return <div ref={containerRef} className="w-full h-full" />;
    }
  );
  DocumentGraph.displayName = "DocumentGraph";

  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) =>
                  window.toggleSort(
                    event.target,
                    "release",
                    "desc",
                    currentReadingListData
                  )
                }
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) =>
                  window.toggleSort(
                    event.target,
                    "time",
                    "desc",
                    currentReadingListData
                  )
                }
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) =>
                  window.toggleReadFilter(event.target, currentReadingListData)
                }
              >
                Filter: All
              </button>
              <button
                id="filter-YT"
                onClick={(event) =>
                  window.toggleFilterType(
                    event.target,
                    "YT",
                    currentReadingListData
                  )
                }
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) =>
                  window.toggleFilterType(
                    event.target,
                    "Arxiv",
                    currentReadingListData
                  )
                }
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) =>
                  window.toggleFilterType(
                    event.target,
                    "Site",
                    currentReadingListData
                  )
                }
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag"></div>
          </div>

          <ul id="reading-list"></ul>
        </div>

        {documentSimilaritiesData && (
          <div className="graph-container">
            <div id="root" className="graph-inner">
              <div className="space-y-6">
                <DocumentGraph
                  width={400}
                  height={400}
                  readingListData={currentReadingListData}
                  documentSimilaritiesData={documentSimilaritiesData}
                />
              </div>
            </div>
          </div>
        )}
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop}
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
