// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import {
  runGameOfLife,
  getTagColor,
  getTextColor,
  formatDate,
} from "../functionality.js";
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// ===================== Tag Component =====================
const Tag = React.memo(({ tag, onTagFilter }) => {
  return (
    <span
      key={tag}
      data-type={tag}
      onClick={() => onTagFilter(tag)}
      style={{
        backgroundColor: getTagColor(tag),
        color: getTextColor(tag),
        cursor: "pointer",
        marginRight: "4px",
        padding: "2px 6px",
        borderRadius: "3px",
        fontSize: "0.85em",
      }}
    >
      {tag}
    </span>
  );
});
Tag.displayName = "Tag";

// ===================== ReadingListItem Component =====================
const ReadingListItem = React.memo(({ item, onTagFilter }) => {
  return (
    <li
      data-release={item.releaseDate}
      data-time={item.readTime}
      data-read={String(item.isRead)}
    >
      {item.isRead ? <span className="read-indicator">✓</span> : null}
      <div className="item-header">
        <div className="item-title">
          <strong>
            "{item.title}" by {item.author}
          </strong>
          <span className="meta">
            {item.isRead ? "Read: " : null}
            {formatDate(item.releaseDate)} |
            <span className="no-break">Est. Time {item.readTime} hours</span>
          </span>
        </div>
        <div className="tags">
          {item.tags.map((tag) => (
            <Tag key={tag} tag={tag} onTagFilter={onTagFilter} />
          ))}
        </div>
      </div>
      <div className="description">{item.description}</div>
      {item.downloadLink ? (
        <a className="download-link" href={item.downloadLink} download>
          Source
        </a>
      ) : null}
    </li>
  );
});
ReadingListItem.displayName = "ReadingListItem";

// ===================== Library Component =====================
function Library() {
  console.log("Library Component RENDER");

  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [readingListData, setReadingListData] = useState([]);
  const [graphData, setGraphData] = useState([]);
  const originalSourcesRawData = useRef(null); // Ref to store initial data
  const [activeTagFilters, setActiveTagFilters] = useState([]);
  const [readFilterState, setReadFilterState] = useState(0);

  const toggleTheme = () => {
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    runGameOfLife("gameOfLife");
  }, []);

  // --- 1st useEffect: Load raw data only ---
  useEffect(() => {
    try {
      const initialReadingListData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
      originalSourcesRawData.current = [...initialReadingListData];
      console.log("1st useEffect - sourcesRawData loaded:", sourcesRawData);
    } catch (error) {
      console.error(
        "Library useEffect (data load) - Error importing reading list data:",
        error
      );
    }
  }, []); // Empty dependency array - runs only once on mount

  // --- 2nd useEffect: Apply initial filters and set states based on loaded data ---
  useEffect(() => {
    if (originalSourcesRawData.current) { // Ensure data is loaded
      console.log("2nd useEffect - originalSourcesRawData.current is now available, applying initial filters.");

      const initialFilteredData = applyTagFilters(); // Apply tag filters first, get data
      const typeFilteredData = filterByType("all", initialFilteredData); // Ensure "all" types are shown initially, pass data
      const finalFilteredData = filterReadStatus(undefined, typeFilteredData); // Ensure all read statuses are shown initially, pass data

      console.log("2nd useEffect - finalFilteredData after initial filters:", finalFilteredData);
      setReadingListData(finalFilteredData); // Set readingListData with final filtered data
      setGraphData(finalFilteredData);     // *Now* set graphData using the *same* final filtered data
      console.log("2nd useEffect - readingListData and graphData states updated.");

    } else {
      console.log("2nd useEffect - originalSourcesRawData.current is not yet available.");
    }
  }, [originalSourcesRawData, applyTagFilters, filterByType, filterReadStatus]); // Dependency on originalSourcesRawData and useCallback filters

  const debounceFunc = (func, wait) => {
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  const performSearch = (query, data) => {
    if (!query) return data;
    return data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    const filteredData = performSearch(
      query,
      originalSourcesRawData.current.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }))
    );
    console.log(
      "handleSearchInput - Before setReadingListData, readingListData:",
      readingListData
    );
    console.log("handleSearchInput - Before setGraphData, graphData:", graphData);
    setReadingListData(filteredData);
    setGraphData(filteredData);
    console.log(
      "handleSearchInput - After setReadingListData, readingListData:",
      readingListData
    );
    console.log(
      "handleSearchInput - After setGraphData, graphData:",
      graphData
    );
  };

  const sortReadingList = (sortBy, sortOrder) => {
    let sortedData = [...readingListData];
    sortedData.sort((a, b) => {
      let comparison = 0;
      if (sortBy === "release") {
        const dateA = new Date(a.releaseDate);
        const dateB = new Date(b.releaseDate);
        comparison = dateA - dateB;
      } else if (sortBy === "time") {
        comparison = a.readTime - b.readTime;
      }
      return sortOrder === "asc" ? comparison : comparison * -1;
    });
    console.log(
      "sortReadingList - Before setReadingListData, readingListData:",
      readingListData
    );
    setReadingListData(sortedData);
    console.log(
      "sortReadingList - After setReadingListData, readingListData:",
      sortedData
    );
    // DO NOT UPDATE graphData here
  };

  const filterByType = useCallback((filterType, currentData) => { // Wrap in useCallback
    const dataToFilter = currentData || originalSourcesRawData.current;
    let filteredData;
    if (filterType === "all") {
      filteredData = dataToFilter.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
    } else {
      filteredData = dataToFilter
        .map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
        .filter((item) => item.tags.includes(filterType));
    }
    console.log(
      "filterByType - Before setReadingListData, readingListData:",
      readingListData
    );
    console.log("filterByType - Before setGraphData, graphData:", graphData);
    console.log("filterByType - setGraphData in useCallback about to be called"); // <-- ADDED LOG
    setReadingListData(filteredData);
    setGraphData(filteredData);
    console.log(
      "filterByType - After setReadingListData, readingListData:",
      readingListData
    );
    console.log(
      "filterByType - After setGraphData, graphData:",
      graphData
    );
    return filteredData;
  }, [originalSourcesRawData, setGraphData, readingListData]); // Dependencies: originalSourcesRawData, setGraphData, readingListData

  const filterReadStatus = useCallback((onlyUnread, currentData) => { // Wrap in useCallback
    const dataToFilter = currentData || originalSourcesRawData.current;
    let filteredData;
    if (onlyUnread === true) {
      filteredData = dataToFilter
        .map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
        .filter((item) => !item.isRead);
    } else if (onlyUnread === false) {
      filteredData = dataToFilter
        .map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
        .filter((item) => item.isRead);
    } else {
      filteredData = dataToFilter
        .map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }));
    }
    console.log(
      "filterReadStatus - Before setReadingListData, readingListData:",
      readingListData
    );
    console.log("filterReadStatus - Before setGraphData, graphData:", graphData);
    console.log("filterReadStatus - setGraphData in useCallback about to be called"); // <-- ADDED LOG
    setReadingListData(filteredData);
    setGraphData(filteredData);
    console.log(
      "filterReadStatus - After setReadingListData, readingListData:",
      readingListData
    );
    console.log(
      "filterReadStatus - After setGraphData, graphData:",
      graphData
    );
    return filteredData;
  }, [originalSourcesRawData, setGraphData, readingListData]); // Dependencies: originalSourcesRawData, setGraphData, readingListData

  const handleTagFilter = (tag) => {
    let newActiveTagFilters = [...activeTagFilters];
    if (newActiveTagFilters.includes(tag)) {
      newActiveTagFilters = newActiveTagFilters.filter(
        (activeTag) => activeTag !== tag
      );
    } else {
      newActiveTagFilters = [...newActiveTagFilters, tag];
    }
    setActiveTagFilters(newActiveTagFilters);
  };

  const applyTagFilters = useCallback(() => { // Modified: Returns filteredData, Wrapped in useCallback
    const dataToFilter = originalSourcesRawData.current; // Use original data source consistently
    if (!dataToFilter) return readingListData; // Return current readingListData if original data not loaded yet

    let filteredData = dataToFilter.map((item, index) => ({
      ...item,
      embedding: item.embedding || [],
      originalIndex: index,
    }));
    if (activeTagFilters.length > 0) {
      filteredData = filteredData.filter((item) => {
        return activeTagFilters.every((filterTag) =>
          item.tags.includes(filterTag)
        );
      });
    }
    console.log(
      "applyTagFilters - Before setReadingListData, readingListData:",
      readingListData
    );
    console.log("applyTagFilters - Before setGraphData, graphData:", graphData);
    console.log("applyTagFilters - setGraphData in useCallback about to be called"); // <-- ADDED LOG
    setReadingListData(filteredData);
    setGraphData(filteredData);
    console.log(
      "applyTagFilters - After setReadingListData, readingListData:",
      readingListData
    );
    console.log(
      "applyTagFilters - After setGraphData, graphData:",
      graphData
    );
    return filteredData; // Modified: Return filteredData
  }, [activeTagFilters, originalSourcesRawData, setGraphData, readingListData]); // Dependencies: activeTagFilters, originalSourcesRawData, setGraphData, readingListData

  const toggleReadFilter = (button) => {
    setReadFilterState((prevState) => (prevState + 1) % 3);
  };

  useEffect(() => {
    if(originalSourcesRawData.current) { // Ensure data is loaded before applying read filter
      let filteredData;
      if (readFilterState === 1) {
        filteredData = filterReadStatus(false);
      } else if (readFilterState === 2) {
        filteredData = filterReadStatus(true);
      } else {
        filteredData = filterByType("all"); // Get data from filterByType
        filteredData = applyTagFilters(); // Then refine by tags, get data
      }
      // setGraphData(filteredData); // Graph data is already set inside filter functions
    }
  }, [readFilterState, originalSourcesRawData, activeTagFilters, filterByType, applyTagFilters, filterReadStatus]); // Added originalSourcesRawData and useCallback filters dependencies

  const toggleSort = (button, criteria) => {
    const currentOrder = button.dataset.order || "unsorted";
    let newOrder;

    if (currentOrder === "unsorted") {
      newOrder = "asc";
    } else if (currentOrder === "asc") {
      newOrder = "desc";
    } else {
      newOrder = "unsorted";
    }

    const buttons = document.querySelectorAll(".sort-bar button");
    buttons.forEach((btn) => {
      btn.classList.remove("active");
      const arrowSpan = btn.querySelector(".sort-arrow");
      if (arrowSpan) arrowSpan.textContent = "";
    });

    if (newOrder !== "unsorted") {
      button.classList.add("active");
      button.dataset.order = newOrder;
      const arrowSpan = button.querySelector(".sort-arrow");
      if (arrowSpan) {
        arrowSpan.textContent = newOrder === "asc" ? "↑" : "↓";
      }
      sortReadingList(criteria, newOrder);
    } else {
      button.dataset.order = "unsorted";
      setReadingListData([...originalSourcesRawData.current]); // Reset using copy
    }
  };

  const toggleFilterType = (button, type) => {
    const isActive = button.classList.contains("active");
    const buttons = document.querySelectorAll(".sort-bar button");
    buttons.forEach((btn) => {
      if (btn !== document.getElementById("filter-read")) {
        btn.classList.remove("active");
        btn.style.removeProperty("--active-color");
        btn.style.removeProperty("--active-text-color");
      }
    });

    if (!isActive) {
      button.classList.add("active");
      switch (type) {
        case "YT":
          button.style.setProperty("--active-color", "#FF0000");
          button.style.setProperty("--active-text-color", "#FFFFFF");
          break;
        case "Arxiv":
          button.style.setProperty("--active-color", "#A51C30");
          button.style.setProperty("--active-text-color", "#FFFFFF");
          break;
        case "Site":
          button.style.setProperty("--active-color", "#F5F5DC");
          button.style.setProperty("--active-text-color", "#000000");
          break;
        default:
          break;
      }
      filterByType(type);
    } else {
      button.style.removeProperty("--active-color");
      button.style.removeProperty("--active-text-color");
      filterByType("all");
    }
  };

  // ===================== DocumentGraph Component =====================
  const DocumentGraph = React.memo(
    ({ width, height, graphDisplayData, documentSimilaritiesData }) => {
      // Renamed prop to graphDisplayData
      console.log(
        "DocumentGraph RENDER - graphDisplayData prop received:",
        graphDisplayData
      ); // Log renamed prop
      const containerRef = useRef(null);
      const [error, setError] = useState(null);
      const [isSmallScreen, setIsSmallScreen] = useState(false);
      const svgRef = useRef(null);
      const simulationRef = useRef(null);
      const nodeTitleGroupRef = useRef(null);

      const checkScreenSize = () => {
        setIsSmallScreen(window.innerWidth <= 768);
      };
      const debouncedCheckScreenSize = debounceFunc(checkScreenSize, 100);

      useEffect(() => {
        checkScreenSize();
        window.addEventListener("resize", debouncedCheckScreenSize);
        return () => {
          window.removeEventListener("resize", debouncedCheckScreenSize);
        };
      }, []);

      useEffect(() => {
        console.log(
          "DocumentGraph useEffect - graphDisplayData prop in useEffect:",
          graphDisplayData
        ); // Log renamed prop
        if (!documentSimilaritiesData) {
          console.log(
            "DocumentGraph useEffect - No documentSimilaritiesData, returning early."
          );
          return;
        }

        if (!isSmallScreen && graphDisplayData && graphDisplayData.length > 0) {
          // Use graphDisplayData
          console.log(
            "DocumentGraph useEffect - Initializing graph with graphDisplayData:",
            graphDisplayData
          );
          initializeGraph(graphDisplayData); // Use graphDisplayData
        } else if (
          !isSmallScreen &&
          (!graphDisplayData || graphDisplayData.length === 0) // Use graphDisplayData
        ) {
          console.log(
            "DocumentGraph useEffect - Cleaning up graph - No data or small screen."
          );
          cleanupGraph();
          if (svgRef.current) {
            const svg = d3.select(svgRef.current);
            svg.selectAll("*").remove();
            svg
              .append("rect")
              .attr("width", "100%")
              .attr("height", "100%")
              .attr("fill", "var(--graph-background)");
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);
            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
          }
        } else {
          console.log(
            "DocumentGraph useEffect - Condition not met - No graph update."
          );
        }
      }, [
        isSmallScreen,
        width,
        height,
        documentSimilaritiesData,
        graphDisplayData, // Dependency on graphDisplayData
      ]);

      const cleanupGraph = useCallback(() => {
        if (simulationRef.current) {
          simulationRef.current.stop();
          simulationRef.current = null;
        }
        if (containerRef.current) {
          const container = d3.select(containerRef.current);
          container.selectAll("*").remove();
        }
      }, []);

      const initializeGraph = async (visibleDocsForGraph) => {
        if (!containerRef.current) return;
        if (!documentSimilaritiesData) return;
        cleanupGraph();

        try {
          const container = d3.select(containerRef.current);
          const svg = container
            .append("svg")
            .attr("width", width)
            .attr("height", height)
            .attr("className", "rounded-lg");
          svgRef.current = svg;

          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");

          if (!visibleDocsForGraph.length) {
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);
            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
            return;
          }

          // ... (rest of initializeGraph and initializeSimulation remain the same) ...

        } catch (error) {
          setError("Error initializing graph. See console for details.");
        }
      };

      const initializeSimulation = (
        nodes,
        links,
        width,
        height,
        g,
        svg,
        readTimes
      ) => {
          // ... (initializeSimulation remains the same) ...
      };

      const dragstarted = (event, d) => {
          // ... (dragstarted remains the same) ...
      };

      const dragged = (event, d) => {
          // ... (dragged remains the same) ...
      };

      const dragended = (event, d) => {
          // ... (dragended remains the same) ...
      };

      const forceBoundary = (width, height, nodes) => {
          // ... (forceBoundary remains the same) ...
      };

      const boundPosition = (position, dimension, padding) => {
          // ... (boundPosition remains the same) ...
      };

      const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
          // ... (normalizeReadTime remains the same) ...
      };


      if (isSmallScreen) return null;

      if (error) {
        return (
          <div
            className="flex items-center justify-center h-32 rounded-lg"
            style={{
              backgroundColor: "var(--graph-background)",
              color: "var(--text-color)",
            }}
          >
            <p className="text-red-600">{error}</p>
          </div>
        );
      }

      return <div ref={containerRef} className="w-full h-full" />;
    }
  );
