// library.js - DEBUGGING VERSION - PLEASE RUN THIS AND CHECK CONSOLE
import React, { useState, useRef, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import {
  runGameOfLife,
  getTagColor,
  getTextColor,
  formatDate,
} from "../functionality.js"; // Import getTagColor, getTextColor, formatDate
import sourcesRawData from "../input.json";
import similaritiesRawData from "../document_similarities.json";
import "../css/library.css";

// =====================
// Tag Component
// =====================
const Tag = React.memo(({ tag, onTagFilter }) => {
  return (
    <span
      key={tag}
      data-type={tag}
      onClick={() => onTagFilter(tag)}
      style={{
        backgroundColor: getTagColor(tag),
        color: getTextColor(tag),
        cursor: "pointer",
        marginRight: "4px",
        padding: "2px 6px",
        borderRadius: "3px",
        fontSize: "0.85em",
      }}
    >
      {tag}
    </span>
  );
});
Tag.displayName = "Tag";

// =====================
// ReadingListItem Component
// =====================
const ReadingListItem = React.memo(({ item, onTagFilter }) => {
  return (
    <li
      data-release={item.releaseDate}
      data-time={item.readTime}
      data-read={String(item.isRead)}
    >
      {item.isRead ? <span className="read-indicator">✓</span> : null}
      <div className="item-header">
        <div className="item-title">
          <strong>
            "{item.title}" by {item.author}
          </strong>
          <span className="meta">
            {item.isRead ? "Read: " : null}
            {formatDate(item.releaseDate)} |
            <span className="no-break">Est. Time {item.readTime} hours</span>
          </span>
        </div>
        <div className="tags">
          {item.tags.map((tag) => (
            <Tag key={tag} tag={tag} onTagFilter={onTagFilter} />
          ))}
        </div>
      </div>
      <div className="description">{item.description}</div>
      {item.downloadLink ? (
        <a className="download-link" href={item.downloadLink} download>
          Source
        </a>
      ) : null}
    </li>
  );
});
ReadingListItem.displayName = "ReadingListItem";

// =====================
// Library Component
// =====================
function Library() {
  console.log("Library Component RENDER");

  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [readingListData, setReadingListData] = useState([]);
  const [activeTagFilters, setActiveTagFilters] = useState([]);
  const [readFilterState, setReadFilterState] = useState(0);
  const [currentSort, setCurrentSort] = useState({ sortBy: null, sortOrder: 'unsorted' }); // Track current sort
  const [displayedReadingListData, setDisplayedReadingListData] = useState([]); // State for display list

  const toggleTheme = () => {
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    runGameOfLife("gameOfLife");
  }, []);

  useEffect(() => {
    try {
      const initialReadingListData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
      setReadingListData(initialReadingListData);
      setDisplayedReadingListData(initialReadingListData); // Initialize displayed data
      console.log("sourcesRawData on load:", sourcesRawData); // LOG: Verify sourcesRawData on initial load
    } catch (error) {
      console.error(
        "Library useEffect - Error importing reading list data:",
        error
      );
    }
  }, []);

  const debounceFunc = (func, wait) => {
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  const performSearch = (query, data) => {
    if (!query) return data;
    const filtered = data.filter((item) =>
      item.title.toLowerCase().includes(query.toLowerCase())
    );
    return filtered;
  };

  const handleSearchInput = (e) => {
    const query = e.target.value;
    const filteredData = performSearch(
      query,
      sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }))
    );
    setReadingListData(filteredData); // Update main data based on search. Graph will react.
    applyFiltersAndSort(filteredData, activeTagFilters, readFilterState, currentSort); // Apply other filters and sort to search result
  };

  const sortReadingList = (sortBy, sortOrder) => {
    let sortedData = [...displayedReadingListData]; // Sort the displayed data
    sortedData.sort((a, b) => {
      let comparison = 0;
      if (sortBy === "release") {
        const dateA = new Date(a.releaseDate);
        const dateB = new Date(b.releaseDate);
        comparison = dateA - dateB;
      } else if (sortBy === "time") {
        comparison = a.readTime - b.readTime;
      }
      return sortOrder === "asc" ? comparison : comparison * -1;
    });
    setDisplayedReadingListData(sortedData); // Update only displayed data, not main data
    setCurrentSort({ sortBy, sortOrder }); // Update current sort state
  };

  const filterByType = (filterType) => {
    let filteredData;
    if (filterType === "all") {
      filteredData = sourcesRawData.map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }));
      console.log(
        `filterByType - filterType: all, filteredData (All count): ${filteredData.length}`,
        filteredData
      ); // LOG: More detail
    } else {
      filteredData = sourcesRawData
        .map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }))
        .filter((item) => item.tags.includes(filterType));
      console.log(
        `filterByType - filterType: ${filterType}, filteredData (Filtered type count): ${filteredData.length}`,
        filteredData
      ); // LOG: More detail
    }
    setReadingListData(filteredData); // Update main data. Graph will react.
    applyFiltersAndSort(filteredData, activeTagFilters, readFilterState, currentSort); // Apply other filters and sort

  };

const filterReadStatus = (onlyUnread) => {
  let filteredData;
  if (onlyUnread === true) {
    filteredData = sourcesRawData
      .map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }))
      .filter((item) => !item.isRead);
    console.log(
      `filterReadStatus - onlyUnread: true, filteredData (Unread count): ${filteredData.length}`,
      filteredData
    ); // LOG: More detail
  } else if (onlyUnread === false) {
    filteredData = sourcesRawData
      .map((item, index) => ({
        ...item,
        embedding: item.embedding || [],
        originalIndex: index,
      }))
      .filter((item) => item.isRead);
    console.log(
      `filterReadStatus - onlyUnread: false, filteredData (Read count): ${filteredData.length}`,
      filteredData
    ); // LOG: More detail
  } else {
    filteredData = sourcesRawData.map((item, index) => ({
      ...item,
      embedding: item.embedding || [],
      originalIndex: index,
    }));
    console.log(
      `filterReadStatus - onlyUnread: undefined, filteredData (All count): ${filteredData.length}`,
      filteredData
    ); // LOG: More detail
  }
  setReadingListData(filteredData); // Update main data. Graph will react.
  applyFiltersAndSort(filteredData, activeTagFilters, readFilterState, currentSort); // Apply other filters and sort
};

  const handleTagFilter = (tag) => {
    let newActiveTagFilters = [...activeTagFilters];
    if (newActiveTagFilters.includes(tag)) {
      newActiveTagFilters = newActiveTagFilters.filter(
        (activeTag) => activeTag !== tag
      );
    } else {
      newActiveTagFilters = [...newActiveTagFilters, tag];
    }
    setActiveTagFilters(newActiveTagFilters);
  };

  useEffect(() => {
    applyFiltersAndSort(readingListData, activeTagFilters, readFilterState, currentSort); // Apply filters on tag change
  }, [activeTagFilters, readFilterState]); // Removed currentSort from dependency array

  const applyFiltersAndSort = (currentData, currentActiveTagFilters, currentReadFilterState, currentSortState) => {
    let filteredData = [...currentData];

    // Tag filters
    if (currentActiveTagFilters.length > 0) {
      filteredData = filteredData.filter((item) => {
        return currentActiveTagFilters.every((filterTag) =>
          item.tags.includes(filterTag)
        );
      });
    }

    // Read status filter
    if (currentReadFilterState === 1) {
      filteredData = filteredData.filter(item => item.isRead);
    } else if (currentReadFilterState === 2) {
      filteredData = filteredData.filter(item => !item.isRead);
    }

    setReadingListData(filteredData); // Update main filtered data (for graph)

    // Apply sorting if any
    if (currentSortState.sortBy && currentSortState.sortOrder !== 'unsorted') {
      let sortedData = [...filteredData];
      sortedData.sort((a, b) => {
        let comparison = 0;
        if (currentSortState.sortBy === "release") {
          const dateA = new Date(a.releaseDate);
          const dateB = new Date(b.releaseDate);
          comparison = dateA - dateB;
        } else if (currentSortState.sortBy === "time") {
          comparison = a.readTime - b.readTime;
        }
        return currentSortState.sortOrder === "asc" ? comparison : comparison * -1;
      });
      setDisplayedReadingListData(sortedData); // Update displayed data with sort
    } else {
      setDisplayedReadingListData(filteredData); // Display filtered data if no sort
    }
  };


  const toggleReadFilter = (button) => {
    setReadFilterState((prevState) => (prevState + 1) % 3);
  };


  const toggleSort = (button, criteria) => {
    const currentOrder = button.dataset.order || "unsorted";
    let newOrder;

    if (currentOrder === "unsorted") {
      newOrder = "asc";
    } else if (currentOrder === "asc") {
      newOrder = "desc";
    } else {
      newOrder = "unsorted";
    }

    const buttons = document.querySelectorAll(".sort-bar button");
    buttons.forEach((btn) => {
      btn.classList.remove("active");
      const arrowSpan = btn.querySelector(".sort-arrow");
      if (arrowSpan) arrowSpan.textContent = "";
    });

    if (newOrder !== "unsorted") {
      button.classList.add("active");
      button.dataset.order = newOrder;

      const arrowSpan = button.querySelector(".sort-arrow");
      if (arrowSpan) {
        arrowSpan.textContent = newOrder === "asc" ? "↑" : "↓";
      }

      sortReadingList(criteria, newOrder); // Call sortReadingList to update displayed data
    } else {
      button.dataset.order = "unsorted";
      setCurrentSort({ sortBy: null, sortOrder: 'unsorted' }); // Reset sort state
      setDisplayedReadingListData([...readingListData]); // Reset displayed data to current filtered data (without sorting)
    }
  };


  const toggleFilterType = (button, type) => {
    const isActive = button.classList.contains("active");
    const buttons = document.querySelectorAll(".sort-bar button");
    buttons.forEach((btn) => {
      if (btn !== document.getElementById("filter-read")) {
        btn.classList.remove("active");
        btn.style.removeProperty("--active-color");
        btn.style.removeProperty("--active-text-color");
      }
    });

    if (!isActive) {
      button.classList.add("active");
      switch (type) {
        case "YT":
          button.style.setProperty("--active-color", "#FF0000");
          button.style.setProperty("--active-text-color", "#FFFFFF");
          break;
        case "Arxiv":
          button.style.setProperty("--active-color", "#A51C30");
          button.style.setProperty("--active-text-color", "#FFFFFF");
          break;
        case "Site":
          button.style.setProperty("--active-color", "#F5F5DC");
          button.style.setProperty("--active-text-color", "#000000");
          break;
      }
      filterByType(type);
    } else {
      button.style.removeProperty("--active-color");
      button.style.removeProperty("--active-text-color");
      filterByType("all");
    }
  };

  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = React.memo(
    ({ width, height, readingListData, documentSimilaritiesData }) => {
      const containerRef = useRef(null);
      const [error, setError] = useState(null);
      const [isSmallScreen, setIsSmallScreen] = useState(false);
      const svgRef = useRef(null);
      const simulationRef = useRef(null);
      const nodeTitleGroupRef = useRef(null);

      const checkScreenSize = () => {
        setIsSmallScreen(window.innerWidth <= 768);
      };

      const debouncedCheckScreenSize = debounceFunc(checkScreenSize, 100);

      useEffect(() => {
        checkScreenSize();
        window.addEventListener("resize", debouncedCheckScreenSize);
        return () => {
          window.removeEventListener("resize", debouncedCheckScreenSize);
        };
      }, []);

      useEffect(() => {
        if (!documentSimilaritiesData) return;

        if (!isSmallScreen && readingListData && readingListData.length > 0) {
          initializeGraph(readingListData);
        } else if (
          !isSmallScreen &&
          (!readingListData || readingListData.length === 0)
        ) {
          cleanupGraph();
          if (svgRef.current) {
            const svg = d3.select(svgRef.current);
            svg.selectAll("*").remove();
            svg
              .append("rect")
              .attr("width", "100%")
              .attr("height", "100%")
              .attr("fill", "var(--graph-background)");
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);

            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
          }
        }
      }, [
        isSmallScreen,
        width,
        height,
        documentSimilaritiesData,
        readingListData, // Still depend on readingListData for filter/search updates
      ]);

      const cleanupGraph = useCallback(() => {
        if (simulationRef.current) {
          simulationRef.current.stop();
          simulationRef.current = null;
        }
        if (containerRef.current) {
          const container = d3.select(containerRef.current);
          container.selectAll("*").remove();
        }
      }, []);

      const initializeGraph = async (visibleDocsForGraph) => {
        if (!containerRef.current) return;
        if (!documentSimilaritiesData) return;
        cleanupGraph();

        try {
          const container = d3.select(containerRef.current);
          const svg = container
            .append("svg")
            .attr("width", width)
            .attr("height", height)
            .attr("className", "rounded-lg");

          svgRef.current = svg;

          svg
            .append("rect")
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("fill", "var(--graph-background)");

          if (!visibleDocsForGraph.length) {
            const textGroup = svg
              .append("g")
              .attr("transform", `translate(${width / 2}, ${height / 2})`);

            textGroup
              .append("text")
              .attr("text-anchor", "middle")
              .attr("fill", "var(--text-color)")
              .text("No documents to display");
            return;
          }

          const initialTranslateX = width / 3 - 25;
          const initialTranslateY = height / 4 + 10;
          const initialScale = 0.45;

          const g = svg.append("g");

          const zoom = d3
            .zoom()
            .scaleExtent([initialScale, 3])
            .translateExtent([
              [0, 0],
              [width, height],
            ])
            .on("zoom", (event) => {
              g.attr("transform", event.transform);
            });

          svg.call(
            zoom.transform,
            d3.zoomIdentity
              .translate(initialTranslateX, initialTranslateY)
              .scale(initialScale)
          );

          svg.call(zoom);

          const visibleDocIndicesMap = {};
          visibleDocsForGraph.forEach((doc, index) => {
            visibleDocIndicesMap[doc.originalIndex] = index;
          });

          const nodes = visibleDocsForGraph.map((doc, i) => ({
            id: String(i),
            title: doc.title,
            fullText: doc.description,
            tags: doc.tags,
            readTime: doc.readTime,
            releaseDate: doc.releaseDate,
            isRead: doc.isRead,
          }));

          let links = documentSimilaritiesData
            .filter((link) => {
              return (
                link.source in visibleDocIndicesMap &&
                link.target in visibleDocIndicesMap
              );
            })
            .map((link) => ({
              ...link,
              source: String(visibleDocIndicesMap[link.source]),
              target: String(visibleDocIndicesMap[link.target]),
            }));

          if (!links || links.length === 0) {
            links = [];
          }

          initializeSimulation(
            nodes,
            links,
            width,
            height,
            g,
            svg,
            visibleDocsForGraph.map((doc) => doc.readTime)
          );
        } catch (error) {
          setError("Error initializing graph. See console for details.");
        }
      };

      const initializeSimulation = (
        nodes,
        links,
        width,
        height,
        g,
        svg,
        readTimes
      ) => {
        nodes.forEach((node) => {
          node.x = width / 2 + (Math.random() - 0.5) * 40;
          node.y = height / 2 + (Math.random() - 0.5) * 40;
        });

        simulationRef.current = d3
          .forceSimulation()
          .nodes(nodes)
          .force(
            "link",
            d3
              .forceLink(links)
              .id((d) => d.id)
              .distance(50)
              .strength(0.5)
          )
          .force("charge", d3.forceManyBody().strength(-300))
          .force("center", d3.forceCenter(width / 2, height / 2))
          .force("x", d3.forceX(width / 2).strength(0.1))
          .force("y", d3.forceY(height / 2).strength(0.1))
          .force(
            "collision",
            d3
              .forceCollide()
              .strength(0.1)
              .radius(
                (d) =>
                  normalizeReadTime(
                    d.readTime,
                    Math.min(...readTimes),
                    Math.max(...readTimes)
                  ) + 10
              )
          )
          .force("boundary", forceBoundary(width, height, nodes))
          .alpha(0.3)
          .velocityDecay(0.4)
          .alphaDecay(0.05)
          .alphaMin(0.001);

        const link = g
          .append("g")
          .selectAll("line")
          .data(links)
          .join("line")
          .attr("stroke", "var(--edge-color)")
          .attr("stroke-width", (d) => d.width)
          .attr("stroke-opacity", "var(--edge-opacity)");

        const nodeGroup = g.append("g").attr("className", "nodes");
        const node = nodeGroup
          .selectAll("g")
          .data(nodes)
          .join("g")
          .attr("transform", (d) => `translate(${d.x},${d.y})`)
          .call(
            d3
              .drag()
              .on("start", dragstarted)
              .on("drag", dragged)
              .on("end", dragended)
          );

        node
          .append("circle")
          .attr("r", (d) =>
            normalizeReadTime(
              d.readTime,
              Math.min(...readTimes),
              Math.max(...readTimes)
            )
          )
          .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
          .attr("stroke", "var(--node-stroke-color)")
          .on("mouseover", function (event, d) {
            d3.select(this).attr("fill", "#8ACE00");
            link
              .attr("stroke", (l) =>
                l.source.id === d.id || l.target.id === d.id
                  ? "#8ACE00"
                  : "var(--edge-color)"
              )
              .attr("stroke-width", (l) =>
                l.source.id === d.id || l.target.id === d.id ? 3 : l.width
              );

            const titleGroup = nodeGroup
              .append("g")
              .attr("className", "node-title-group")
              .attr("transform", `translate(${d.x},${d.y})`);
            nodeTitleGroupRef.current = titleGroup;

            titleGroup
              .append("text")
              .attr("className", "node-title")
              .attr("x", 10)
              .attr("y", 5)
              .attr("fill", "var(--text-color)")
              .text(d.title);
          })
          .on("mouseout", function (event, d) {
            d3.select(this).attr("fill", (d) =>
              d.isRead ? "#4a9eff" : "var(--node-color)"
            );
            link
              .attr("stroke", "var(--edge-color)")
              .attr("stroke-width", (l) => l.width);
            if (nodeTitleGroupRef.current) {
              nodeTitleGroupRef.current.remove();
              nodeTitleGroupRef.current = null;
            }
          });

        simulationRef.current.on("tick", () => {
          link
            .attr("x1", (d) => boundPosition(d.source.x, width, -200))
            .attr("y1", (d) => boundPosition(d.source.y, height, -200))
            .attr("x2", (d) => boundPosition(d.target.x, width, -200))
            .attr("y2", (d) => boundPosition(d.target.y, height, -200));

          node.attr("transform", (d) => {
            const x = boundPosition(d.x, width, -200);
            const y = boundPosition(d.y, height, -200);
            return `translate(${x},${y})`;
          });
        });
      };

      const dragstarted = (event, d) => {
        if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
        d.fx = d.x;
        d.fy = d.y;

        simulationRef.current.force("link").strength(0.8);
        simulationRef.current.alphaTarget(0.3).restart();
      };

      const dragged = (event, d) => {
        simulationRef.current.alphaTarget(0.01);
        d.fx = boundPosition(event.x, width, -200);
        d.fy = boundPosition(event.y, height, -200);
      };

      const dragended = (event, d) => {
        if (!event.active) simulationRef.current.alphaTarget(0.01);
        d.fx = null;
        d.fy = null;
      };

      const forceBoundary = (width, height, nodes) => {
        const padding = 4;
        const xMin = padding;
        const xMax = width - padding;
        const yMin = padding;
        const yMax = height - padding;
        const strength = 0.05;

        function force(alpha) {
          nodes.forEach((node) => {
            if (node.x < xMin) {
              node.vx += (xMin - node.x) * strength * alpha;
            }
            if (node.x > xMax) {
              node.vx += (xMax - node.x) * strength * alpha;
            }
            if (node.y < yMin) {
              node.vy += (yMin - node.y) * strength * alpha;
            }
            if (node.y > yMax) {
              node.vy += (yMax - node.y) * strength * alpha;
            }
          });
        }

        return force;
      };

      const boundPosition = (position, dimension, padding) => {
        return Math.max(padding, Math.min(dimension - padding, position));
      };

      const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
        const minRadius = 5;
        const maxRadius = 20;
        if (maxReadTime === minReadTime) return (minRadius + maxRadius) / 2;
        const logMin = Math.log(minReadTime + 1);
        const logMax = Math.log(maxReadTime + 1);
        const logReadTime = Math.log(readTime + 1);
        return (
          ((logReadTime - logMin) / (logMax - logMin)) *
            (maxRadius - minRadius) +
          minRadius
        );
      };

      if (isSmallScreen) {
        return null;
      }

      if (error) {
        return (
          <div
            className="flex items-center justify-center h-32 rounded-lg"
            style={{
              backgroundColor: "var(--graph-background)",
              color: "var(--text-color)",
            }}
          >
            <p className="text-red-600">{error}</p>
          </div>
        );
      }

      return <div ref={containerRef} className="w-full h-full" />;
    }
  );
  DocumentGraph.displayName = "DocumentGraph";

  // --- JSX Markup ---
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme} // Corrected to use component-level toggleTheme
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
              <path d="M20 12h2"></path>
              <path d="M6.34 17.66l-1.41 1.41"></path>
              <path d="M19.07 4.93l-1.41 1.41"></path>
            </svg>
            <svg
              className="moon-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/posts" className="nav-link">
            Posts
          </Link>
          <Link to="/library" className="nav-link">
            Library
          </Link>
          <Link to="/" className="nav-link">
            About Me
          </Link>
        </nav>
      </header>

      <div className="reading-list-layout">
        <div className="lib-container">
          <h1 className="name" style={{ marginBottom: "10px" }}>
            Library
          </h1>

          <div className="search-bar">
            <input
              type="text"
              id="search"
              placeholder="Search by term..."
              onInput={handleSearchInput}
            />
          </div>

          <div className="sort-bar">
            <div className="sort-bar-buttons">
              <button
                id="sort-release"
                onClick={(event) => toggleSort(event.target, "release")} // Corrected
              >
                Sort by Release Date <span className="sort-arrow"></span>
              </button>
              <button
                id="sort-time"
                onClick={(event) => toggleSort(event.target, "time")} // Corrected
              >
                Sort by Time to Read <span className="sort-arrow"></span>
              </button>
              <button
                id="filter-read"
                onClick={(event) => toggleReadFilter(event.target)} // Corrected
              >
                Filter:{" "}
                {readFilterState === 0
                  ? "All"
                  : readFilterState === 1
                  ? "Read"
                  : "Unread"}
              </button>
              <button
                id="filter-YT"
                onClick={(event) => toggleFilterType(event.target, "YT")} // Corrected
              >
                YT
              </button>
              <button
                id="filter-Arxiv"
                onClick={(event) => toggleFilterType(event.target, "Arxiv")} // Corrected
              >
                Arxiv
              </button>
              <button
                id="filter-Site"
                onClick={(event) => toggleFilterType(event.target, "Site")} // Corrected
              >
                Site
              </button>
            </div>
            <div id="active-tags" className="active-tag">
              {activeTagFilters.map((tag) => (
                <Tag key={tag} tag={tag} onTagFilter={handleTagFilter} />
              ))}
            </div>
          </div>

          <ul id="reading-list">
            {console.log("displayedReadingListData before render:", displayedReadingListData)}{" "}
            {/* LOG: displayedReadingListData before render */}
            {displayedReadingListData.map((item) => ( // Use displayedReadingListData for rendering the list
              <ReadingListItem
                key={item.originalIndex}
                item={item}
                onTagFilter={handleTagFilter}
              />
            ))}
          </ul>
        </div>

        {/* Pass similaritiesRawData directly as prop */}
        <div className="graph-container">
          <div id="root" className="graph-inner">
            <div className="space-y-6">
              <DocumentGraph
                width={400}
                height={400}
                readingListData={readingListData} // Pass the main filtered readingListData for the graph
                documentSimilaritiesData={similaritiesRawData}
              />
            </div>
          </div>
        </div>
      </div>
      <button
        ref={scrollToTopButtonRef}
        onClick={scrollToTop} // Corrected to use component-level scrollToTop
        className="scroll-to-top"
        aria-label="Scroll to top"
      >
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path
            d="M12 19V5"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M5 12L12 5L19 12"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </>
  );
}

export default Library;
