// library.js
import React, { useState, useRef, useEffect, useCallback } from "react";
import ReactDOM from "react-dom/client";
import { Link } from "react-router-dom";
import * as d3 from "d3";
import { runGameOfLife } from "../functionality.js";
import "../css/library.css";
import "../functionality.js";
import { searchItems } from "../functionality.js";

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "../../public/library_sources/sources_data.json"
        ); // Path relative to public URL
        const rawData = await response.json();
        const readingListData = rawData.map((item, index) => ({
          ...item,
          embedding: item.embedding || [],
          originalIndex: index,
        }));
        setCurrentReadingListData(readingListData);
      } catch (error) {
        console.error("Error fetching reading list data:", error);
        // Handle error appropriately (e.g., set error state)
      }
    };

    fetchData();
  }, []);

// Prepare readingListData with embeddings
const readingListData = readingListDataRaw.map((item, index) => ({
  ...item,
  embedding: item.embedding || [], // Ensure embedding exists, default to empty array if missing
  originalIndex: index, // Add original index to each item
}));

// =====================
// Library
// =====================
function Library() {
  // --- State Declarations ---
  const [theme, setTheme] = useState("dark");
  const scrollToTopButtonRef = useRef(null);
  const [currentReadingListData, setCurrentReadingListData] =
    useState(readingListData); // State for reading list data

  // --- Helper Functions ---
  const toggleTheme = () => {
    setTheme((current) => (current === "dark" ? "light" : "dark"));
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // --- Effects ---
  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    runGameOfLife("gameOfLife");
  }, []);

  const useModel = window.use;
  window.documentVisibilityChanged = new Event("documentVisibilityChanged");

  // Hashing and caching functions (assumes that a hashString function is available)
  const hashDocSet = (docs) => {
    const sortedTitles = docs
      .map((d) => d.title)
      .sort()
      .join("|");
    return hashString(sortedTitles).toString();
  };

  const checkCacheValidity = (docs) => {
    const docSetHash = hashDocSet(docs);
    const cachedEmbeddings =
      JSON.parse(localStorage.getItem("documentEmbeddings")) || {};
    return cachedEmbeddings[docSetHash] !== undefined;
  };

  const getCachedSimilarities = (docSetHash) => {
    console.log("getCachedSimilarities called, docSetHash:", docSetHash);
    const store = JSON.parse(localStorage.getItem("similaritiesCache")) || {};
    return store[docSetHash] || null;
  };

  const setCachedSimilarities = (docSetHash, links) => {
    localStorage.setItem(
      `similaritiesCache-${docSetHash}`,
      JSON.stringify(links)
    );
  };

  const generateEmbeddingsWithCache = async (
    documents,
    model,
    batchSize = 10
  ) => {
    if (!model || !documents || documents.length === 0) return null;
    console.time("Embedding Generation");
    // Generate a unique hash for the document set
    const docSetHash = hashDocSet(documents);

    // Check if embeddings are already cached
    const cachedEmbeddings =
      JSON.parse(localStorage.getItem("documentEmbeddings")) || {};
    const cachedResults = cachedEmbeddings[docSetHash] || null;

    if (cachedResults) {
      console.log("Loaded embeddings from cache for docSetHash:", docSetHash);
      console.timeEnd("Embedding Generation");
      return cachedResults;
    }

    // If not cached, generate new embeddings
    const newDocuments = documents.map((doc) => ({
      ...doc,
      hash: btoa(unescape(encodeURIComponent(doc.content))),
    }));

    try {
      const contents = newDocuments.map((doc) => doc.content);
      const batchedEmbeddings = [];
      const embeddingPromises = [];
      for (let i = 0; i < contents.length; i += batchSize) {
        const batch = contents.slice(i, i + batchSize);
        embeddingPromises.push(model.embed(batch).then((e) => e.array()));
      }
      const results = await Promise.all(embeddingPromises);
      batchedEmbeddings.push(...results.flat());

      // Cache the new embeddings
      cachedEmbeddings[docSetHash] = batchedEmbeddings;
      localStorage.setItem(
        "documentEmbeddings",
        JSON.stringify(cachedEmbeddings)
      );

      console.log(
        "Generated and cached embeddings for docSetHash:",
        docSetHash
      );
      console.timeEnd("Embedding Generation");
      return batchedEmbeddings;
    } catch (error) {
      console.error("Error generating embeddings:", error);
      console.timeEnd("Embedding Generation");
      return null;
    }
  };

  const cosineSimilarity = (vecA, vecB) => {
    const dotProduct = vecA.reduce((acc, val, i) => acc + val * vecB[i], 0);
    const normA = Math.sqrt(vecA.reduce((acc, val) => acc + val * val, 0));
    const normB = Math.sqrt(vecB.reduce((acc, val) => acc + val * val, 0));
    return dotProduct / (normA * normB);
  };

  const debounce = (func, wait) => {
    let timeout;
    return function (...args) {
      clearTimeout(timeout);
      timeout = setTimeout(() => func.apply(this, args), wait);
    };
  };

  // =====================
  // DocumentGraph Component
  // =====================
  const DocumentGraph = ({ width, height, readingListData }) => {
    // Accept readingListData as prop
    const containerRef = useRef(null);
    const [loading, setLoading] = useState(false); // Model loading not needed anymore
    const [error, setError] = useState(null);
    const [visibleDocs, setVisibleDocs] = useState([]);
    const [isSmallScreen, setIsSmallScreen] = useState(false);
    const svgRef = useRef(null);
    const simulationRef = useRef(null);
    const nodeTitleGroupRef = useRef(null); // Ref to hold the node title group

    const checkScreenSize = () => {
      const smallScreen = window.innerWidth <= 768;
      setIsSmallScreen(smallScreen);
    };

    const debouncedCheckScreenSize = debounce(checkScreenSize, 100);

    useEffect(() => {
      checkScreenSize();
      window.addEventListener("resize", debouncedCheckScreenSize);
      return () => {
        window.removeEventListener("resize", debouncedCheckScreenSize);
      };
    }, []);

    useEffect(() => {
      if (!isSmallScreen && containerRef.current) {
        initializeGraph();
      }
    }, [isSmallScreen, visibleDocs, width, height, loading]); // Removed model dependency

    const getVisibleDocuments = useCallback(() => {
      const items = Array.from(document.querySelectorAll("#reading-list li"));
      const visibleItems = items.filter(
        (item) => item.style.display !== "none"
      );

      if (visibleItems.length === 0) {
        if (items.length === 0) {
          return readingListData; // Use prop readingListData
        }
        return [];
      }

      return visibleItems
        .map((item) => {
          const titleElement = item.querySelector(".item-title strong");
          if (!titleElement) return null;
          const titleText = titleElement.textContent;
          const title = titleText.match(/"([^"]+)"/)?.[1];
          if (!title) return null;
          return readingListData.find((doc) => doc.title === title); // Use prop readingListData
        })
        .filter(Boolean);
    }, [readingListData]); // Depend on readingListData

    useEffect(() => {
      const handleVisibilityChange = () => {
        const currentVisibleDocs = getVisibleDocuments();
        setVisibleDocs(currentVisibleDocs);
      };

      setVisibleDocs(readingListData); // Initial visible docs are all docs
      const throttledHandleVisibilityChange = debounce(
        handleVisibilityChange,
        200
      );
      window.addEventListener(
        "documentVisibilityChanged",
        throttledHandleVisibilityChange
      );

      return () => {
        window.removeEventListener(
          "documentVisibilityChanged",
          handleVisibilityChange
        );
      };
    }, [getVisibleDocuments, readingListData]); // Depend on readingListData

    const cleanupGraph = useCallback(() => {
      if (simulationRef.current) {
        simulationRef.current.stop();
        simulationRef.current = null;
      }
      if (containerRef.current) {
        const container = d3.select(containerRef.current);
        container.selectAll("*").remove();
      }
    }, []);

    const initializeGraph = async () => {
      console.log("initializeGraph function is being called");
      if (!containerRef.current) {
        console.log("Exiting early: containerRef.current is null");
        return;
      }
      if (loading) {
        console.log("Exiting early: loading is true");
        return;
      }
      cleanupGraph();

      const container = d3.select(containerRef.current);
      const svg = container
        .append("svg")
        .attr("width", width)
        .attr("height", height)
        .attr("className", "rounded-lg");

      svgRef.current = svg;

      svg
        .append("rect")
        .attr("width", "100%")
        .attr("height", "100%")
        .attr("fill", "var(--graph-background)");

      if (!visibleDocs.length) {
        const textGroup = svg
          .append("g")
          .attr("transform", `translate(${width / 2}, ${height / 2})`);

        textGroup
          .append("text")
          .attr("text-anchor", "middle")
          .attr("fill", "var(--text-color)")
          .text("No documents to display");

        return;
      }

      const initialTranslateX = width / 3 - 25;
      const initialTranslateY = height / 4 + 10;
      const initialScale = 0.45;

      const g = svg.append("g");

      const zoom = d3
        .zoom()
        .scaleExtent([initialScale, 3])
        .translateExtent([
          [0, 0],
          [width, height],
        ])
        .on("zoom", (event) => {
          g.attr("transform", event.transform);
        });

      svg.call(
        zoom.transform,
        d3.zoomIdentity
          .translate(initialTranslateX, initialTranslateY)
          .scale(initialScale)
      );

      svg.call(zoom);

      // Create a mapping from visible doc original indices to new node indices
      const visibleDocIndicesMap = {};
      visibleDocs.forEach((doc, index) => {
        visibleDocIndicesMap[doc.originalIndex] = index;
      });

      const nodes = visibleDocs.map((doc, i) => ({
        id: String(i), // Use new index as node ID
        title: doc.title,
        fullText: doc.description,
        tags: doc.tags,
        readTime: doc.readTime,
        releaseDate: doc.releaseDate,
        isRead: doc.isRead,
      }));

      const readTimes = visibleDocs.map((doc) => doc.readTime);
      const minReadTime = Math.min(...readTimes);
      const maxReadTime = Math.max(...readTimes);

      // Directly use imported documentSimilaritiesData and remap indices
      let links = documentSimilaritiesData
        .filter((link) => {
          return (
            link.source in visibleDocIndicesMap &&
            link.target in visibleDocIndicesMap
          );
        })
        .map((link) => ({
          ...link,
          source: String(visibleDocIndicesMap[link.source]), // Remap source index
          target: String(visibleDocIndicesMap[link.target]), // Remap target index
        }));

      if (!links || links.length === 0) {
        console.log(
          "No pre-calculated similarities found for visible documents in document_similarities.json."
        );
        links = []; // Ensure links is at least an empty array for simulation initialization
      } else {
        console.log(
          "Loaded pre-calculated similarities from document_similarities.json"
        );
      }

      initializeSimulation(
        nodes,
        links,
        width,
        height,
        g,
        svg,
        minReadTime,
        maxReadTime
      );
    };

    const initializeSimulation = (
      nodes,
      links,
      width,
      height,
      g,
      svg,
      minReadTime,
      maxReadTime
    ) => {
      // Randomly initialize node positions near the center
      nodes.forEach((node) => {
        node.x = width / 2 + (Math.random() - 0.5) * 100;
        node.y = height / 2 + (Math.random() - 0.5) * 100;
      });

      simulationRef.current = d3
        .forceSimulation()
        .nodes(nodes)
        .force(
          "link",
          d3
            .forceLink(links)
            .id((d) => d.id)
            .distance(50)
            .strength(0.5)
        )
        .force("charge", d3.forceManyBody().strength(-1200))
        .force("center", d3.forceCenter(width / 2, height / 2))
        .force("x", d3.forceX(width / 2).strength(0.1))
        .force("y", d3.forceY(height / 2).strength(0.1))
        .force("collision", d3.forceCollide().radius(25))
        .force("boundary", () => {
          const padding = 4;
          const xMin = padding;
          const xMax = width - padding;
          const yMin = padding;
          const yMax = height - padding;
          nodes.forEach((node) => {
            if (node.x < xMin) {
              node.vx += (xMin - node.x) * 0.01;
            }
            if (node.x > xMax) {
              node.vx += (xMax - node.x) * 0.01;
            }
            if (node.y < yMin) {
              node.vy += (yMin - node.y) * 0.01;
            }
            if (node.y > yMax) {
              node.vy += (yMax - node.y) * 0.01;
            }
          });
        })
        .alpha(0.5)
        .velocityDecay(0.3)
        .alphaDecay(0.05)
        .alphaMin(0.001);

      const link = g
        .append("g")
        .selectAll("line")
        .data(links)
        .join("line")
        .attr("stroke", "var(--edge-color)")
        .attr("stroke-width", (d) => d.width)
        .attr("stroke-opacity", "var(--edge-opacity)");

      const nodeGroup = g.append("g").attr("className", "nodes");
      const node = nodeGroup
        .selectAll("g")
        .data(nodes)
        .join("g")
        .attr("transform", (d) => `translate(${d.x},${d.y})`)
        .call(
          d3
            .drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended)
        );

      node
        .append("circle")
        .attr("r", (d) =>
          normalizeReadTime(d.readTime, minReadTime, maxReadTime)
        )
        .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
        .attr("stroke", "var(--node-stroke-color)")
        .on("mouseover", function (event, d) {
          d3.select(this).attr("fill", "#8ACE00");
          link
            .attr("stroke", (l) =>
              l.source.id === d.id || l.target.id === d.id
                ? "#8ACE00"
                : "var(--edge-color)"
            )
            .attr("stroke-width", (l) =>
              l.source.id === d.id || l.target.id === d.id ? 3 : l.width
            );

          // Create node title group and store reference
          const titleGroup = nodeGroup
            .append("g")
            .attr("className", "node-title-group")
            .attr("transform", `translate(${d.x},${d.y})`);
          nodeTitleGroupRef.current = titleGroup; // Store the reference

          titleGroup
            .append("text")
            .attr("className", "node-title")
            .attr("x", 10)
            .attr("y", 5)
            .attr("fill", "var(--text-color)")
            .text(d.title);
        })
        .on("mouseout", function (event, d) {
          d3.select(this).attr("fill", (d) =>
            d.isRead ? "#4a9eff" : "var(--node-color)"
          );
          link
            .attr("stroke", "var(--edge-color)")
            .attr("stroke-width", (l) => l.width);
          // Remove the specific node title group using the stored reference
          if (nodeTitleGroupRef.current) {
            nodeTitleGroupRef.current.remove();
            nodeTitleGroupRef.current = null; // Clear the reference
          }
        });

      simulationRef.current.on("tick", () => {
        link
          .attr("x1", (d) => boundPosition(d.source.x, width, -200))
          .attr("y1", (d) => boundPosition(d.source.y, height, -200))
          .attr("x2", (d) => boundPosition(d.target.x, width, -200))
          .attr("y2", (d) => boundPosition(d.target.y, height, -200));

        node.attr("transform", (d) => {
          const x = boundPosition(d.x, width, -200);
          const y = boundPosition(d.y, height, -200);
          return `translate(${x},${y})`;
        });
      });
    };

    const dragstarted = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      d.fx = d.x;
      d.fy = d.y;
    };

    const dragged = (event, d) => {
      simulationRef.current.alphaTarget(0.01);
      d.fx = boundPosition(event.x, width, -200);
      d.fy = boundPosition(event.y, height, -200);
    };

    const dragended = (event, d) => {
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      d.fx = null;
      d.fy = null;
    };

    const boundPosition = (position, dimension, padding) => {
      return Math.max(padding, Math.min(dimension - padding, position));
    };

    const normalizeReadTime = (readTime, minReadTime, maxReadTime) => {
      const minRadius = 5;
      const maxRadius = 20;
      const logMin = Math.log(minReadTime + 1);
      const logMax = Math.log(maxReadTime + 1);
      const logReadTime = Math.log(readTime + 1);
      return (
        ((logReadTime - logMin) / (logMax - logMin)) * (maxRadius - minRadius) +
        minRadius
      );
    };

    if (isSmallScreen) {
      return null;
    }

    if (loading) {
      return (
        <div
          className="flex items-center justify-center rounded-lg"
          style={{
            width,
            height,
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="loading">Loading Graph Data...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div
          className="flex items-center justify-center h-32 rounded-lg"
          style={{
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="text-red-600">{error}</p>
        </div>
      );
    }

    return <div ref={containerRef} className="w-full h-full" />;
  };

  // =====================
  // App Component & Rendering
  // =====================
  const App = () => {
    return (
      <div className="space-y-6">
        <DocumentGraph
          width={400}
          height={400}
          readingListData={currentReadingListData}
        />{" "}
        {/* Pass readingListData as prop */}
      </div>
    );
  };

  // --- JSX Markup ---
  return (
    <>
      {/* Metadata */}
      <meta charSet="UTF-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>bb.radz</title>

      <canvas
        id="gameOfLife"
        className="game-of-life"
        width="200"
        height="400"
      ></canvas>

      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <p className="logo">BBradz</p>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <svg
              className="sun-icon"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <circle cx="12" cy="12" r="4"></circle>
              <path d="M12 2v2"></path>
              <path d="M12 20v2"></path>
              <path d="M4.93 4.93l1.41 1.41"></path>
              <path d="M17.66 17.66l1.41 1.41"></path>
              <path d="M2 12h2"></path>
