import React, { useState, useRef, useEffect, useCallback } from "react";
import "../css/landing-style.css";
import "../css/library.css";
import {
  readingListData as initialReadingListData, // Rename import to avoid shadowing
  getTagColor,
  getTextColor,
  debounce,
  formatDate,
} from "../functionality.js"; // Import utility functions
import { Link } from "react-router-dom";
import * as d3 from "d3";
import * as tf from "@tensorflow/tfjs";
import * as use from "@tensorflow-models/universal-sentence-encoder";

console.log("readingListData immediately after import:", initialReadingListData); // DEBUGGING: Check import right away

const hashString = (str) => {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = (hash << 5) - hash + char;
    hash = hash & hash;
  }
  return Math.abs(hash);
};

const hashDocSet = (docs) => {
  const sortedTitles = docs
    .map((d) => d.title)
    .sort()
    .join("|");
  return hashString(sortedTitles).toString();
};

const checkCacheValidity = (docs) => {
  const docSetHash = hashDocSet(docs);
  const cachedEmbeddings =
    JSON.parse(localStorage.getItem("documentEmbeddings")) || {};
  return cachedEmbeddings[docSetHash] !== undefined;
};

const getCachedSimilarities = (docSetHash) => {
  const store = JSON.parse(localStorage.getItem("similaritiesCache")) || {};
  return store[docSetHash] || null;
};

const setCachedSimilarities = (docSetHash, links) => {
  localStorage.setItem(
    `similaritiesCache-${docSetHash}`,
    JSON.stringify(links)
  );
};

const generateEmbeddingsWithCache = async (
  documents,
  model,
  batchSize = 10
) => {
  if (!model || !documents || documents.length === 0) return null;
  console.time("Embedding Generation");
  // Generate a unique hash for the document set
  console.time("Hash Document Set");
  const docSetHash = hashDocSet(documents);
  console.timeEnd("Hash Document Set");

  // Check if embeddings are already cached
  console.time("Check Cache");
  const cachedEmbeddings =
    JSON.parse(localStorage.getItem("documentEmbeddings")) || {};
  const cachedResults = cachedEmbeddings[docSetHash] || null;
  console.timeEnd("Check Cache");

  if (cachedResults) {
    console.log("Loaded embeddings from cache for docSetHash:", docSetHash);
    console.timeEnd("Embedding Generation");
    return cachedResults;
  } else {
    console.log("No embeddings cache hit for docSetHash:", docSetHash);
  }

  // If not cached, generate new embeddings
  const newDocuments = documents.map((doc) => ({
    ...doc,
    content: `${doc.title} ${doc.description} ${
      Array.isArray(doc.tags) ? doc.tags.join(" ") : ""
    }`,
    // Safely join tags, handle if tags is not an array
    // content: `${doc.title} ${doc.description} ${doc.tags.join(" ")}`, // OLD CODE
    hash: btoa(unescape(encodeURIComponent(doc.description))), // using description for hash as content is now title+desc+tags
  }));

  try {
    const contents = newDocuments.map((doc) => doc.content);
    const batchedEmbeddings = [];

    console.time("TensorFlow Embeddings");
    const embeddingPromises = [];
    for (let i = 0; i < contents.length; i += batchSize) {
      const batch = contents.slice(i, i + batchSize);
      embeddingPromises.push(model.embed(batch).then((e) => e.array()));
    }
    const results = await Promise.all(embeddingPromises);
    batchedEmbeddings.push(...results.flat());
    console.timeEnd("TensorFlow Embeddings");

    // Cache the new embeddings
    console.time("Cache Embeddings");
    cachedEmbeddings[docSetHash] = batchedEmbeddings;
    localStorage.setItem(
      "documentEmbeddings",
      JSON.stringify(cachedEmbeddings)
    );
    console.timeEnd("Cache Embeddings");

    console.log("Generated and cached embeddings for docSetHash:", docSetHash);
    console.timeEnd("Embedding Generation");
    return batchedEmbeddings;
  } catch (error) {
    console.error("Error generating embeddings:", error);
    console.timeEnd("Embedding Generation");
    return null;
  }
};

const cosineSimilarity = (vecA, vecB) => {
  const dotProduct = vecA.reduce((acc, val, i) => acc + val * vecB[i], 0);
  const normA = Math.sqrt(vecA.reduce((acc, val) => acc + val * val, 0));
  const normB = Math.sqrt(vecB.reduce((acc, val) => acc + val * val, 0));
  return dotProduct / (normA * normB);
};

function Library() {
  console.log("Library component render started"); // DEBUGGING: Component render start
  const [theme, setTheme] = useState("dark");
  const [readingListData, setReadingListData] = useState(initialReadingListData); // Use state for reading list data
  const [visibleDocs, setVisibleDocs] = useState(initialReadingListData); // Initialize visibleDocs with all data
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState(null);
  const [sortDirection, setSortDirection] = useState("desc");
  const [readFilter, setReadFilter] = useState("All"); // 'All', 'Read', 'Unread'
  const [typeFilters, setTypeFilters] = useState([]); // Array of types to filter by
  const [activeTags, setActiveTags] = useState([]); // For active tag display

  console.log("Initial visibleDocs state:", visibleDocs); // DEBUGGING: Initial state value

  const toggleTheme = () => {
    setTheme((currentTheme) => (currentTheme === "dark" ? "light" : "dark"));
    document.body.setAttribute(
      "data-theme",
      theme === "dark" ? "light" : "dark"
    );
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const searchItems = () => {
    const term = searchTerm.toLowerCase();
    const filteredDocs = readingListData.filter((doc) => {
      const searchText = `${doc.title} ${doc.description} ${doc.tags ? doc.tags.join(" ") : ""}`.toLowerCase();
      return searchText.includes(term);
    });
    setVisibleDocs(filteredDocs);
  };

  const toggleSort = (sortByType) => {
    let newDirection = "asc";
    if (sortBy === sortByType) {
      newDirection = sortDirection === "asc" ? "desc" : "asc";
    }
    setSortBy(sortByType);
    setSortDirection(newDirection);
  };

  const toggleReadFilter = () => {
    switch (readFilter) {
      case "All":
        setReadFilter("Read");
        break;
      case "Read":
        setReadFilter("Unread");
        break;
      case "Unread":
        setReadFilter("All");
        break;
      default:
        setReadFilter("All");
    }
  };

  const toggleFilterType = (filterType) => {
    const isTypeActive = typeFilters.includes(filterType);
    if (isTypeActive) {
      setTypeFilters(typeFilters.filter((type) => type !== filterType));
    } else {
      setTypeFilters([...typeFilters, filterType]);
    }
  };

  useEffect(() => {
    let filteredDocs = initialReadingListData;

    // Search filter
    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      filteredDocs = filteredDocs.filter((doc) => {
        const searchText = `${doc.title} ${doc.description} ${doc.tags ? doc.tags.join(" ") : ""}`.toLowerCase();
        return searchText.includes(term);
      });
    }

    // Read filter
    if (readFilter === "Read") {
      filteredDocs = filteredDocs.filter((doc) => doc.isRead);
    } else if (readFilter === "Unread") {
      filteredDocs = filteredDocs.filter((doc) => !doc.isRead);
    }

    // Type filters
    if (typeFilters.length > 0) {
      filteredDocs = filteredDocs.filter((doc) => {
        if (!doc.tags || !Array.isArray(doc.tags)) return false; // Safe check for tags
        return doc.tags.some(tag => typeFilters.includes(tag));
      });
    }

    // Sorting
    if (sortBy) {
      filteredDocs = [...filteredDocs].sort((a, b) => {
        let valueA, valueB;
        if (sortBy === "release") {
          valueA = new Date(a.releaseDate).getTime() || 0;
          valueB = new Date(b.releaseDate).getTime() || 0;
        } else if (sortBy === "time") {
          valueA = a.readTime;
          valueB = b.readTime;
        }
        if (sortDirection === "asc") {
          return valueA - valueB;
        } else {
          return valueB - valueA;
        }
      });
    }

    setVisibleDocs(filteredDocs);
  }, [searchTerm, sortBy, sortDirection, readFilter, typeFilters]);

  const [model, setModel] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const svgRef = useRef(null);
  const simulationRef = useRef(null);
  const containerRef = useRef(null);
  const readingListRef = useRef(null);

  const checkScreenSize = useCallback(() => {
    const smallScreen = window.innerWidth <= 768;
    setIsSmallScreen(smallScreen);
  }, []);

  useEffect(() => {
    const debouncedCheckScreenSize = debounce(checkScreenSize, 100);
    checkScreenSize();
    window.addEventListener("resize", debouncedCheckScreenSize);
    return () => {
      window.removeEventListener("resize", debouncedCheckScreenSize);
    };
  }, [checkScreenSize, debounce]);

  useEffect(() => {
    document.body.setAttribute("data-theme", theme);
  }, [theme]);

  useEffect(() => {
    console.log("useEffect to set visibleDocs started"); // DEBUGGING: Effect start
    console.log("Imported readingListData inside useEffect:", initialReadingListData); // DEBUGGING: Data inside effect
    setVisibleDocs(initialReadingListData);
    setReadingListData(initialReadingListData); // Initialize readingListData as well
    console.log(
      "visibleDocs after setting from readingListData in useEffect:",
      visibleDocs
    ); // DEBUGGING: State after set in effect
    console.log("useEffect to set visibleDocs finished"); // DEBUGGING: Effect end
  }, []);

  console.log("visibleDocs before render:", visibleDocs); // DEBUGGING: State before JSX render

  const DocumentGraph = ({ width, height }) => {
    const containerRef = useRef(null);
    const [graphModel, setGraphModel] = useState(null); // Renamed model to graphModel to avoid confusion
    const [graphLoading, setGraphLoading] = useState(true); // Renamed loading to graphLoading
    const [graphError, setGraphError] = useState(null); // Renamed error to graphError
    const [graphVisibleDocs, setGraphVisibleDocs] = useState([]); // Renamed visibleDocs to graphVisibleDocs
    const [processedDocs, setProcessedDocs] = useState([]); // To store processed docs
    const [graphIsSmallScreen, setGraphIsSmallScreen] = useState(false); // Renamed isSmallScreen to graphIsSmallScreen
    const svgRef = useRef(null);
    const simulationRef = useRef(null);

    // Function to check screen size and update state
    const checkScreenSizeGraph = useCallback(() => {
      const smallScreen = window.innerWidth <= 768;
      setGraphIsSmallScreen(smallScreen);
    }, []);

    // Debounce function to limit how often the resize handler is called - using imported debounce

    // Effect to add and remove resize event listener
    useEffect(() => {
      const debouncedCheckScreenSizeGraph = debounce(checkScreenSizeGraph, 100);
      checkScreenSizeGraph(); // Initial check
      window.addEventListener("resize", debouncedCheckScreenSizeGraph);
      return () => {
        window.removeEventListener("resize", debouncedCheckScreenSizeGraph);
      };
    }, [checkScreenSizeGraph, debounce]); // debounce added to dependency array

    // Effect to reinitialize the graph when screen size changes
    useEffect(() => {
      if (!graphIsSmallScreen && containerRef.current) {
        initializeGraph();
      }
    }, [
      graphIsSmallScreen,
      graphVisibleDocs,
      width,
      height,
      graphModel,
      graphLoading,
    ]); // graphModel and graphLoading

    // Function to get visible documents - using useCallback
    const getVisibleDocumentsGraph = useCallback(() => {
      const items = Array.from(document.querySelectorAll("#reading-list li"));
      const visibleItems = items.filter(
        (item) => item.style.display !== "none"
      );

      if (visibleItems.length === 0) {
        if (items.length === 0) {
          return initialReadingListData; // use imported readingListData
        }
        return [];
      }
      // Correct selector for React list items
      return visibleItems
        .map((item) => {
          const titleElement = item.querySelector(".reading-list-item h3"); // Correct selector for React rendered list
          if (!titleElement) return null;
          const titleText = titleElement.textContent;
          const title = titleText; // Title is directly the text content now
          if (!title) return null;
          return initialReadingListData.find((doc) => doc.title === title); // use imported readingListData
        })
        .filter(Boolean)
        .filter(Boolean); // Double filter to ensure no nulls and then truthy values
      // .filter(Boolean); // Original filter
    }, []); // readingListData and any other external dependencies used inside useCallback should be in dependency array

    // Effect to update visible documents when the document visibility changes
    useEffect(() => {
      const handleVisibilityChange = () => {
        const currentVisibleDocs = getVisibleDocumentsGraph();
        setGraphVisibleDocs(currentVisibleDocs);
      };

      // setGraphVisibleDocs(getVisibleDocumentsGraph()); // Initial visible docs - use current visible docs
      setGraphVisibleDocs(initialReadingListData); // Initial visible docs - use imported readingListData
      const throttledHandleVisibilityChange = debounce(
        handleVisibilityChange,
        200
      );
      window.addEventListener(
        "documentVisibilityChanged",
        throttledHandleVisibilityChange
      );

      return () => {
        window.removeEventListener(
          "documentVisibilityChanged",
          throttledHandleVisibilityChange // Correct listener to remove
        );
      };
    }, [getVisibleDocumentsGraph, debounce]); // debounce added to dependency array

    // Effect to load the Universal Sentence Encoder model
    useEffect(() => {
      const loadModel = async () => {
        try {
          console.time("Model Loading");
          if (window.cachedUSEModel) {
            setGraphModel(window.cachedUSEModel); // setGraphModel
            setGraphLoading(false); // setGraphLoading
            console.timeEnd("Model Loading");
            return;
          }
          setGraphLoading(true); // setGraphLoading
          const loadedModel = await use.load();
          setGraphModel(loadedModel); // setGraphModel
          window.cachedUSEModel = loadedModel;
          setGraphLoading(false); // setGraphLoading
          console.timeEnd("Model Loading");
        } catch (err) {
          console.error("Error loading model:", err);
          setGraphError(
            // setGraphError
            "Failed to load the model. Please check console for details."
          );
          setGraphLoading(false); // setGraphLoading
          console.timeEnd("Model Loading");
        }
      };
      loadModel();
    }, []);

    // Function to clean up the graph - useCallback added
    const cleanupGraph = useCallback(() => {
      if (simulationRef.current) {
        simulationRef.current.stop();
        simulationRef.current = null;
      }

      if (containerRef.current) {
        const container = d3.select(containerRef.current);
        container.selectAll("*").remove();
      }
    }, []);

    // Function to initialize the graph - useCallback added
    const initializeGraph = useCallback(async () => {
      if (!containerRef.current || !graphModel || graphLoading) return; // graphModel, graphLoading

      console.time("Graph Initialization");

      cleanupGraph();

      const container = d3.select(containerRef.current);
      const svg = container
        .append("svg")
        .attr("width", width)
        .attr("height", height)
        .attr("class", "rounded-lg");

      svgRef.current = svg;

      svg
        .append("rect")
        .attr("width", "100%")
        .attr("height", "100%")
        .attr("fill", "var(--graph-background)");

      if (!graphVisibleDocs.length) {
        // graphVisibleDocs
        const textGroup = svg
          .append("g")
          .attr("transform", `translate(${width / 2}, ${height / 2})`);

        textGroup
          .append("text")
          .attr("text-anchor", "middle")
          .attr("fill", "var(--text-color)")
          .text("No documents to display");

        console.timeEnd("Graph Initialization");
        return;
      }

      const initialTranslateX = width / 3 - 25;
      const initialTranslateY = height / 4 + 10;
      const initialScale = 0.45;

      const g = svg.append("g");
      const zoom = d3
        .zoom()
        .scaleExtent([initialScale, 3])
        .translateExtent([
          [0, 0],
          [width, height],
        ])
        .on("zoom", (event) => {
          g.attr("transform", event.transform);
        });

      svg.call(
        zoom.transform,
        d3.zoomIdentity
          .translate(initialTranslateX, initialTranslateY)
          .scale(initialScale)
      );

      svg.call(zoom);

      console.log("graphVisibleDocs before processing:", graphVisibleDocs); // DEBUG: Inspect graphVisibleDocs

      console.time("Process Documents for Graph");
      const processedDocsForGraph = graphVisibleDocs.map((doc) => {
        // graphVisibleDocs
        console.log("Processing doc:", doc); // DEBUG: Inspect each doc
        return {
          content: `${doc.title} ${doc.description} ${
            Array.isArray(doc.tags) ? doc.tags.join(" ") : ""
          }`, // Safe join here too for consistency
          // content: `${doc.title} ${doc.description} ${doc.tags.join(" ")}`, // OLD CODE
        };
      });
      setProcessedDocs(processedDocsForGraph); // Store processed docs in state
      console.timeEnd("Process Documents for Graph");


      const embeddings = await generateEmbeddingsWithCache(
        processedDocsForGraph,
        graphModel, // graphModel
        10
      );
      if (!embeddings) {
        console.error("No embeddings generated");
        console.timeEnd("Graph Initialization");
        return;
      }

      const nodes = graphVisibleDocs.map((doc, i) => ({
        // graphVisibleDocs
        id: String(i),
        title: doc.title,
        fullText: doc.description,
        tags: doc.tags,
        readTime: doc.readTime,
        releaseDate: doc.releaseDate,
        isRead: doc.isRead,
      }));

      const readTimes = graphVisibleDocs.map((doc) => doc.readTime); // graphVisibleDocs
      const minReadTime = Math.min(...readTimes);
      const maxReadTime = Math.max(...readTimes);

      const docSetHash = hashDocSet(graphVisibleDocs); // graphVisibleDocs
      let links = getCachedSimilarities(docSetHash);

      if (!links) {
        console.log("No similarities cache hit for docSetHash:", docSetHash);
        links = [];
        console.time("Calculate Similarities");
        const processLinks = (i) => {
          if (i >= graphVisibleDocs.length) {
            // graphVisibleDocs
            console.time("Cache Similarities");
            setCachedSimilarities(docSetHash, links);
            console.timeEnd("Cache Similarities");
            console.timeEnd("Calculate Similarities");
            initializeSimulation(
              nodes,
              links,
              width,
              height,
              g,
              svg,
              minReadTime,
              maxReadTime
            ); // Pass minReadTime and maxReadTime
            console.timeEnd("Graph Initialization");
            return;
          }

          for (let j = i + 1; j < graphVisibleDocs.length; j++) {
            // graphVisibleDocs
            const similarity = cosineSimilarity(embeddings[i], embeddings[j]);
            if (similarity > 0.75) {
              links.push({
                source: String(i),
                target: String(j),
                similarity,
                width: 1 + (similarity - 0.75) * (4 / (1 - 0.75)),
              });
            }
          }

          setTimeout(() => processLinks(i + 1), 100); // Process next document after 100ms
        };

        processLinks(0); // Start processing
      } else {
        console.log("Loaded similarities from cache for docSetHash:", docSetHash);
        initializeSimulation(
          nodes,
          links,
          width,
          height,
          g,
          svg,
          minReadTime,
          maxReadTime
        ); // Pass minReadTime and maxReadTime
        console.timeEnd("Graph Initialization");
      }
    }, [
      graphVisibleDocs,
      graphModel,
      graphLoading,
      cleanupGraph,
      generateEmbeddingsWithCache,
      getCachedSimilarities,
      setCachedSimilarities,
      cosineSimilarity,
      hashDocSet,
    ]); // graphModel, graphLoading, cleanupGraph, generateEmbeddingsWithCache, getCachedSimilarities, setCachedSimilarities, cosineSimilarity, hashDocSet added to dependency array

    const initializeSimulation = useCallback(
      (nodes, links, width, height, g, svg, minReadTime, maxReadTime) => {
        console.time("Initialize Simulation");
        // Randomly initialize node positions near the center
        nodes.forEach((node) => {
          node.x = width / 2 + (Math.random() - 0.5) * 100;
          node.y = height / 2 + (Math.random() - 0.5) * 100;
        });

        // Set up the simulation with a custom boundary force
        simulationRef.current = d3
          .forceSimulation()
          .nodes(nodes)
          .force(
            "link",
            d3
              .forceLink(links)
              .id((d) => d.id)
              .distance(50)
              .strength(0.5)
          )
          .force("charge", d3.forceManyBody().strength(-1200))
          .force("center", d3.forceCenter(width / 2, height / 2))
          .force("x", d3.forceX(width / 2).strength(0.1))
          .force("y", d3.forceY(height / 2).strength(0.1))
          .force("collision", d3.forceCollide().radius(25))
          .force("boundary", () => {
            const padding = 4;
            const xMin = padding;
            const xMax = width - padding;
            const yMin = padding;
            const yMax = height - padding;
            nodes.forEach((node) => {
              if (node.x < xMin) {
                node.vx += (xMin - node.x) * 0.01;
              }
              if (node.x > xMax) {
                node.vx += (xMax - node.x) * 0.01;
              }
              if (node.y < yMin) {
                node.vy += (yMin - node.y) * 0.01;
              }
              if (node.y > yMax) {
                node.vy += (yMax - node.y) * 0.01;
              }
            });
          })
          .alpha(0.5)
          .velocityDecay(0.3) // Keep alpha from dropping to zero too quickly:
          .alphaDecay(0.05) // Slower decay
          .alphaMin(0.001); // Don’t let alpha get all the way to 0

        // Create link elements
        console.time("Create Links");
        const link = g
          .append("g")
          .selectAll("line")
          .data(links)
          .join("line")
          .attr("stroke", "var(--edge-color)")
          .attr("stroke-width", (d) => d.width)
          .attr("stroke-opacity", "var(--edge-opacity)");
        console.timeEnd("Create Links");

        // Create node group
        console.time("Create Nodes");
        const nodeGroup = g.append("g").attr("class", "nodes");
        const node = nodeGroup
          .selectAll("g")
          .data(nodes)
          .join("g")
          .attr("transform", (d) => `translate(${d.x},${d.y})`)
          .call(
            d3
              .drag()
              .on("start", dragstarted)
              .on("drag", dragged)
              .on("end", dragended)
          );
        console.timeEnd("Create Nodes");

        // Draw circles for each node
        console.time("Draw Node Circles");
        node
          .append("circle")
          .attr("r", (d) =>
            normalizeReadTime(d.readTime, minReadTime, maxReadTime)
          )
          .attr("fill", (d) => (d.isRead ? "#4a9eff" : "var(--node-color)"))
          .attr("stroke", "var(--node-stroke-color)")
          .on("mouseover", function (event, d) {
            d3.select(this).attr("fill", "#8ACE00");
            link
              .attr("stroke", (l) =>
                l.source.id === d.id || l.target.id === d.id
                  ? "#8ACE00"
                  : "var(--edge-color)"
              )
              .attr("stroke-width", (l) =>
                l.source.id === d.id || l.target.id === d.id ? 3 : l.width
              );

            const titleGroup = nodeGroup
              .append("g")
              .attr("class", "node-title-group")
              .attr("transform", `translate(${d.x},${d.y})`);

            titleGroup
              .append("text")
              .attr("class", "node-title")
              .attr("x", 10)
              .attr("y", 5)
              .attr("fill", "var(--text-color)")
              .text(d.title);
          })
          .on("mouseout", function (event, d) {
            d3.select(this).attr("fill", (d) =>
              d.isRead ? "#4a9eff" : "var(--node-color)"
            );

            link
              .attr("stroke", "var(--edge-color)")
              .attr("stroke-width", (l) => l.width);

            nodeGroup.selectAll(".node-title-group").remove();
          });
        console.timeEnd("Draw Node Circles");


        // Tick updates: keep everything in sync
        console.time("Simulation Tick");
        simulationRef.current.on("tick", () => {
          link
            .attr("x1", (d) => boundPosition(d.source.x, width, -200))
            .attr("y1", (d) => boundPosition(d.source.y, height, -200))
            .attr("x2", (d) => boundPosition(d.target.x, width, -200))
            .attr("y2", (d) => boundPosition(d.target.y, height, -200));

          node.attr("transform", (d) => {
            const x = boundPosition(d.x, width, -200);
            const y = boundPosition(d.y, height, -200);
            return `translate(${x},${y})`;
          });
        });
        console.timeEnd("Simulation Tick");
        console.timeEnd("Initialize Simulation");
      },
      [cosineSimilarity]
    );

    /** Drag event handlers */
    // "start" fires when user begins dragging
    const dragstarted = (event, d) => {
      // Reheat alpha so the simulation updates actively
      if (!event.active) simulationRef.current.alphaTarget(0.01).restart();
      d.fx = d.x;
      d.fy = d.y;
    };

    // "drag" fires continuously while user drags
    const dragged = (event, d) => {
      // Keep alpha "hot" while dragging
      simulationRef.current.alphaTarget(0.01);
      d.fx = boundPosition(event.x, width, -200);
      d.fy = boundPosition(event.y, height, -200);
    };

    // "end" fires when user stops dragging
    const dragended = (event, d) => {
      // Cool it off, but not all the way to zero
      if (!event.active) simulationRef.current.alphaTarget(0.01);
      d.fx = null;
      d.fy = null;
    };

    // Helper to constrain node positions so they don't go out of bounds
    const boundPosition = (position, dimension, padding) => {
      return Math.max(padding, Math.min(dimension - padding, position));
    };

    // Helper function to normalize read time for node radius - useCallback added as it's used in initializeGraph
    const normalizeReadTime = useCallback(
      (readTime, minReadTime, maxReadTime) => {
        const minRadius = 5;
        const maxRadius = 20;
        const logMin = Math.log(minReadTime + 1);
        const logMax = Math.log(maxReadTime + 1);
        const logReadTime = Math.log(readTime + 1);
        return (
          ((logReadTime - logMin) / (logMax - logMin)) *
            (maxRadius - minRadius) +
          minRadius
        );
      },
      []
    );

    useEffect(() => {
      initializeGraph();
    }, [initializeGraph]);

    if (graphIsSmallScreen) {
      return null;
    }

    if (graphLoading) {
      return (
        <div
          className="flex items-center justify-center rounded-lg"
          style={{
            width,
            height,
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="loading">Loading Embeddings</p>
        </div>
      );
    }

    if (graphError) {
      return (
        <div
          className="flex items-center justify-center h-32 rounded-lg"
          style={{
            backgroundColor: "var(--graph-background)",
            color: "var(--text-color)",
          }}
        >
          <p className="text-red-600">{graphError}</p>
        </div>
      );
    }

    return <div ref={containerRef} className="w-full h-full" />;
  };

