            <svg className="moon-icon" viewBox="0 0 24 24" fill="none">
              <path d="M21 12.79A9 9 0 1 1 11.21 3"></path>
            </svg>
          </button>
        </div>
        <nav className="nav-links">
          <Link to="/section1" className="nav-link">
            Section1
          </Link>
          <Link to="/section2" className="nav-link">
            Section2
          </Link>
          <Link to="/" className="nav-link">
            Home
          </Link>
        </nav>
      </header>

      {/* Main Content Container */}
      <div className="container">
        <main>
          {/* --- Section 1 --- */}
          <section className={`series ${sections.section1 ? "collapsed" : ""}`}>
